// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IArtParams.sol";
import "./Structs.sol";

contract OffChainParams is IParams, Ownable {

    using Strings for uint8;
    using Strings for uint256;

    function getParmsSequence(BaseAttributes calldata atts, bool isSample, IArtData.ArtProps calldata artProps)
        public
        pure
        virtual
        override
        returns(string memory)
    {
        string memory lines = "[[";
        for(uint i=0; i<atts.planeAttributes.length; i++) {
            lines = planeParams(lines, atts.planeAttributes[i], (i < atts.planeAttributes.length-1) );
        }

        return string.concat(
            lines,
            "],",
            artParams(atts, isSample, artProps),
            artParams_Paint(atts),
            "]"
        );

    }

    function artParams(BaseAttributes calldata atts, bool isSample, IArtData.ArtProps calldata artProps) internal pure returns (string memory){
        string memory str =  string.concat(
            atts.skyCol.toString(),
            ",", atts.palette.toString(),
            ",", atts.proximity.toString(),
            ",", artProps.numOfX.toString(),
            ",", artProps.numOfY.toString(),
            ",", atts.extraParams[uint(EP.NumAngles)].toString(),
            ",", artProps.numTypes.toString(),
            ",", (isSample ? "true" : "false")
        );
        return str;
    }

    function artParams_Paint(BaseAttributes calldata atts) internal pure returns (string memory){
        if (atts.extraParams.length > 0) {
            return  string.concat(
                ",", atts.extraParams[uint(EP.PaintType)].toString(),
                ",", atts.extraParams[uint(EP.PaletteIdx)].toString()
            );
        }
        else {
            return '';
        }
    }

    function planeParams(string memory lines, PlaneAttributes memory planeAtt, bool last) internal pure returns (string memory){
        string memory str =  string.concat(
            lines,
            "[",
            planeParams_base(planeAtt),
            getPlanePaintParams(planeAtt),
            last ?
            "]," : "]"
        );
        return str;
    }

    function planeParams_base(PlaneAttributes memory planeAtt) internal pure returns (string memory){
        return string.concat(
            planeAtt.locX.toString(),
            ",", planeAtt.locY.toString(),
            ",", planeAtt.angle.toString(),
            ",", planeAtt.trailCol.toString(),
            ",", planeAtt.level.toString(),
            ",", planeAtt.speed.toString(),
            ",", planeAtt.planeType.toString()
        );
    }

    function getPlanePaintParams(PlaneAttributes memory planeAtt) internal pure returns (string memory){
        if (planeAtt.extraParams.length > 0) {
            return string.concat(
                ",", planeAtt.extraParams[uint8(PlaneEP.ColorIdx)].toString(),
                ",", planeAtt.extraParams[uint8(PlaneEP.ColorIdx2)].toString(),
                ",", planeAtt.extraParams[uint8(PlaneEP.ColorIdx3)].toString()
            );
        }
        else {
            return '';
        }
    }
}"
