      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Frigid_DNS is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    mapping(string => bool) public allowedSuffixes;
    bool public initialized; 
    uint256 public constant DOMAIN_PRICE_USDT = 10 * 10**6;
    uint256 public constant TAX_PERCENTAGE = 1; // 1% tax
    IERC20 public usdtToken;
    

    struct Domain {
        address owner;
        string metadata; 
        bool forSale;
        uint256 salePrice;
    }

    mapping(string => Domain) public domains;

    event DomainBought(string indexed domain, address indexed buyer);
    event DomainListedForSale(string indexed domain, uint256 price);
    event DomainDeListedForSale(string indexed domain);
    event DomainSold(string indexed domain, address indexed buyer, uint256 price, uint256 tax);
    event MetadataUpdated(string indexed domain, string newMetadata);
    event DomainTransferred(string indexed domain, address indexed newOwner);
    event SuffixAdded(string indexed suffix);


    function initialize(address usdtAddress, string[] memory suffixes) public initializer {
        require(!initialized, "Already initialized");
        __Ownable_init(msg.sender);
        usdtToken = IERC20(usdtAddress);
        for (uint i = 0; i < suffixes.length; i++) {
            allowedSuffixes[suffixes[i]] = true;
        }
        initialized = true;
    }

    function buyDomain(string memory name, string memory suffix) public {
        require(isValidName(name), "Invalid domain name");
        require(allowedSuffixes[suffix], "Invalid domain suffix");

        string memory fullDomain = string(abi.encodePacked(name, ".", suffix));
        Domain storage domain = domains[fullDomain];

        require(domain.owner == address(0), "Domain is unavailable");

        uint256 allowance = usdtToken.allowance(msg.sender, address(this));
        require(allowance >= DOMAIN_PRICE_USDT, "Allowance not set");

        uint256 balance = usdtToken.balanceOf(msg.sender);
        require(balance >= DOMAIN_PRICE_USDT, "Insufficient USDT balance");

        domain.owner = msg.sender;
        domain.metadata = "";
        domain.salePrice = 0;
        domain.forSale = false;

        require(usdtToken.transferFrom(msg.sender, address(this), DOMAIN_PRICE_USDT), "USDT transfer failed");

        emit DomainBought(fullDomain, msg.sender);
    }

    function updateDomainMetadata(string memory fullDomain, string memory newMetadata) public {
        Domain storage domain = domains[fullDomain];

        require(domain.owner == msg.sender, "Only the owner can update metadata");

        domain.metadata = newMetadata;

        emit MetadataUpdated(fullDomain, newMetadata);
    }

    function transferDomain(string memory fullDomain, address newOwner) public {
        Domain storage domain = domains[fullDomain];

        require(domain.owner == msg.sender, "Only the owner can transfer the domain");
        require(newOwner != address(0), "New owner address cannot be zero");

        domain.owner = newOwner;
        domain.metadata = "";
        domain.salePrice = 0;
        domain.forSale = false;

        emit DomainTransferred(fullDomain, newOwner);
    }

    function isValidName(string memory name) internal pure returns (bool) {
        bytes memory nameBytes = bytes(name);
        if (nameBytes.length == 0 || nameBytes.length > 63) return false; // Restrict length to 63 characters

        if (nameBytes[0] == 0x2D || nameBytes[nameBytes.length - 1] == 0x2D) {
            return false;
        }
        for (uint i = 0; i < nameBytes.length; i++) {
            bytes1 char = nameBytes[i];
            if (
                !(char >= 0x61 && char <= 0x7A) && // a-z
                !(char >= 0x30 && char <= 0x39) && // 0-9
                !(char == 0x2D) // -
            ) {
                return false;
            }
        }
        return true;
    }

    function addSuffix(string memory suffix) public onlyOwner {
        require(bytes(suffix).length != 0, "Invalid domain suffix");

        allowedSuffixes[suffix] = true;

        emit SuffixAdded(suffix);
    }

    function listDomainForSale(string memory fullDomain, uint256 price) public {
        Domain storage domain = domains[fullDomain];

        require(domain.owner == msg.sender, "Only the owner can list the domain for sale");
        require(price > 0, "Sale price must be greater than zero");

        domain.forSale = true;
        domain.salePrice = price;

        emit DomainListedForSale(fullDomain, price);
    }

    function delistDomainForSale(string memory fullDomain) public {
        Domain storage domain = domains[fullDomain];

        require(domain.owner == msg.sender, "Only the owner can de-list the domain for sale");
        require(domain.forSale == true, "Domain already de-listed");

        domain.forSale = false;
        domain.salePrice = 0;

        emit DomainDeListedForSale(fullDomain);
    }

    function buyListedDomain(string memory fullDomain) public {
        Domain storage domain = domains[fullDomain];
        uint256 salePrice = domain.salePrice;
        uint256 saleTax = domain.salePrice * TAX_PERCENTAGE / 100;
        uint256 salePriceWithTax = salePrice + saleTax;

        uint256 allowance = usdtToken.allowance(msg.sender, address(this));
        require(allowance >= salePriceWithTax, "Allowance not set");

        uint256 balance = usdtToken.balanceOf(msg.sender);
        require(balance >= salePriceWithTax, "Insufficient USDT balance");

        require(domain.forSale, "Domain is not for sale");
        require(domain.owner != address(0), "Domain is not for sale");

        address seller = domain.owner;

        domain.owner = msg.sender;
        domain.metadata = "";
        domain.forSale = false;
        domain.salePrice = 0;

        require(usdtToken.transferFrom(msg.sender, seller, salePrice), "USDT transfer failed");
        require(usdtToken.transferFrom(msg.sender, address(this), saleTax), "USDT transfer failed");

        emit DomainSold(fullDomain, msg.sender, salePrice, saleTax);
    }

    function isAvailableOrForSale(string memory name, string memory suffix) public view returns (bool, uint256, address) {
        require(isValidName(name), "Invalid domain name");
        require(allowedSuffixes[suffix], "Invalid domain suffix");

        string memory fullDomain = string(abi.encodePacked(name, ".", suffix));
        Domain storage domain = domains[fullDomain];
        
        if (domain.owner == address(0)) {
            return (true, DOMAIN_PRICE_USDT, address(0));
        }

        if (domain.forSale) {
            uint256 salePriceWithTax = domain.salePrice + (domain.salePrice * TAX_PERCENTAGE / 100);
            return (true, salePriceWithTax, domain.owner);
        }

        return (false, 0, address(0));
    }

    function withdrawUSDT(uint256 amount) public onlyOwner {
        uint256 contractBalance = usdtToken.balanceOf(address(this));
        require(amount <= contractBalance, "Insufficient balance");

        require(usdtToken.transfer(msg.sender, amount), "USDT transfer failed");
    }


    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}
"
