    "contracts/EtherSeasonOneReward.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {BitMaps} from "@openzeppelin/contracts/utils/structs/BitMaps.sol";
import {ERC1155} from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import {ERC2981} from "@openzeppelin/contracts/token/common/ERC2981.sol";
import {IERC721A} from "erc721a/contracts/IERC721A.sol";
import {ERC1155Burnable} from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import {MessageHashUtils} from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import {OperatorFilterer} from "closedsea/src/OperatorFilterer.sol";

error InvalidSignature();
error InvalidClaimant();
error TicketAlreadyClaimed();

contract EtherSeasonOneReward is ERC1155, ERC1155Burnable, ERC2981, OperatorFilterer, Ownable {
    using MessageHashUtils for bytes32;
    using BitMaps for BitMaps.BitMap;

    bool public operatorFilteringEnabled;

    string public name = "Ether Season One Reward";
    string public symbol = "ETHRS1";

    address rewardSigner = 0xE2A7D7e2445770a44b2D830bd0651CfAeFC220d8;
    address reward721Address = 0x36A239AE23048F76Ed86C419426CcE3933A390e8;

    event Claim(address claimant, uint ticketId);
    struct ClaimTicket {uint256 ticketType; uint256 ticketId; uint256 tokenId; uint256 amount; address addr;}

    BitMaps.BitMap private ticketClaim;

    constructor() ERC1155("https://api.ether.site/metadata/reward/{id}.json") Ownable(msg.sender) {
        _registerForOperatorFiltering();
        operatorFilteringEnabled = true;
    }

    /**
     * Allows a user to claim "tickets" from the Ether website and either mint a ERC1155 or transfer a ERC721 they have won.
     *
     * @param tickets is ticket data packed as ClaimTicket struct.
     * @param signature is the signature given from the site's API.
     */
    function claim(ClaimTicket[] calldata tickets, bytes calldata signature) external {

        bytes32 smh = keccak256(abi.encode(msg.sender,tickets)).toEthSignedMessageHash(); // Address + tickets must match signature.
        if (ECDSA.recover(smh, signature) != rewardSigner) {revert InvalidSignature();}

        for(uint i = 0; i < tickets.length;) {
            uint thisTicketId = tickets[i].ticketId;

            if(ticketClaim.get(thisTicketId)) {revert TicketAlreadyClaimed();}

            ticketClaim.set(thisTicketId);
            emit Claim(msg.sender, thisTicketId);

            if (tickets[i].ticketType == 0 && tickets[i].amount > 0) { // ERC1155 Mint Claim
                _mint(msg.sender, tickets[i].tokenId, tickets[i].amount, "");
            }
            else if (tickets[i].ticketType == 1){ // ERC721 Transfer Claim
                IERC721A(tickets[i].addr).safeTransferFrom(reward721Address, msg.sender, tickets[i].tokenId);
            }

            unchecked{++i;}
        }
    }

    /**
     * Public getter function for the bitset to see if a ticket has been claimed or not.
     *
     * @param ticketId is ticket data packed as ClaimTicket struct.
     */
    function isClaimed(uint256 ticketId) public view returns (bool) {
        return ticketClaim.get(ticketId);
    }

    /**
     * Only owner function to set the signer that's used by the API.
     *
     * @param signer is the new signer
     */
    function setRewardSigner(address signer) external onlyOwner {
        rewardSigner = signer;
    }

    /**
     * Only owner function to set the rewarder address that holds ERC721s for claims.
     *
     * @param rewarder is the new rewarder
     */
    function setRewarderAddress(address rewarder) external onlyOwner {
        reward721Address = rewarder;
    }

    /**
     * Set royalty for ERC2981 support
     *
     * @param receiver is the person to receive royalties
     * @param feeNumerator is the fee in bps
     */
    function setDefaultRoyalty(address receiver, uint96 feeNumerator) public onlyOwner {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    /**
     * supportsInterface with ERC1155 and ERC2981
     * @param interfaceId is the interface to check
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155, ERC2981) returns (bool){
        return ERC1155.supportsInterface(interfaceId) || ERC2981.supportsInterface(interfaceId);
    }

    /**
     * Only owner function to set if filtering is on or off
     *
     * @param value is the filtering status
     */
    function setOperatorFilteringEnabled(bool value) public onlyOwner {
        operatorFilteringEnabled = value;
    }

    //OperatorFiltering Overrides
    function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator){super.setApprovalForAll(operator, approved);}
    function safeTransferFrom(address from,address to,uint256 tokenId,uint256 amount,bytes memory data) public override onlyAllowedOperator(from) {super.safeTransferFrom(from, to, tokenId, amount, data);}
    function safeBatchTransferFrom(address from,address to,uint256[] memory ids,uint256[] memory amounts,bytes memory data) public override onlyAllowedOperator(from) {super.safeBatchTransferFrom(from, to, ids, amounts, data);}
    function _isPriorityOperator(address operator) internal pure override returns (bool) {return operator == address(0x1E0049783F008A0085193E00003D00cd54003c71);} // OpenSea Seaport Conduit
    function _operatorFilteringEnabled() internal view override returns (bool) {return operatorFilteringEnabled;}

}
"
    },
