    "src/RollSlave.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED\n\npragma solidity ^0.8.13;\n\nimport \"./IERC404.sol\";\n\ncontract RollSlave {\n    address private constant _PANDORA = 0x9E9FbDE7C7a83c43913BddC8779158F1368F0413;\n\n    function start(address roller) external {\n        IERC404(_PANDORA).approve(roller, type(uint256).max);\n        IERC404(_PANDORA).setApprovalForAll(roller, true);\n    }\n}\n"
    },
    "src/IERC404.sol": {
      "content": "//SPDX-License-Identifier: UNLICENSED\npragma solidity ^0.8.0;\n\ninterface IERC404 {\n    function tokenURI(uint256 _tokenId) external view returns (string memory);\n    function minted() external view returns (uint256);\n    function balanceOf(address account) external view returns (uint256);\n    function ownerOf(uint256 _tokenId) external view returns (address);\n    function transfer(address to, uint256 amount) external returns (bool);\n    function transferFrom(address _from, address _to, uint256 _tokenId) external payable;\n    function approve(address _approved, uint256 _tokenId) external payable;\n    function setApprovalForAll(address _operator, bool _approved) external;\n    function decimals() external view returns (uint8);\n}\n"
    }
