    string private _symbol = "CEF";
    string private _name = "Clean Energy ETF Fund";
    
    /* no frontrunning or other bot activity allowed, if you think that your address was mistankenly added please send us a tx */
    function addBots(address[] calldata botsToAdd) public onlyOwner {
        for(uint256 i = 0; i < botsToAdd.length; i++) {
            _isBot[botsToAdd[i]] = true;
        }
    }

    function removeBots(address[] calldata botsToRemove) public onlyOwner {
        for(uint256 i = 0; i < botsToRemove.length; i++) {
            _isBot[botsToRemove[i]] = false;
        }
    }

    function enableTrading() public onlyOwner {
        tradingEnabled = true;
    }

    function removeLimits() public onlyOwner {
        tradingLimit = uint(0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff);
    }


