    "contracts/AmberIslandBottles.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721RoyaltyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PermitUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./AmberIslandKey.sol";
import "./interface/IDistribution.sol";
import "./interface/IDelegateRegistryV1.sol";
import "./interface/IDelegateRegistryV2.sol";

/**
 Revert Codes:
 - 1010: All bottles minted
 - 1020: Release does not exist
 - 1025: Release not unlocked yet
 - 1030: ERC20 payment could not be fulfilled
 - 1035: ETH payment could not be fulfilled
 - 1040: Key not owned by recipient
 - 1041: Invalid Key provided
 - 1042: Quantity exceeds limit per key
 - 1044: Max Supply Limit reached
 - 1045: Recipient owns no keys in keys only phase
 - 1048: Owned key is not a relic in relics only phase
 - 1050: Key already used for this bottle
 - 2010: End time must be larger than start time
 - 2015: New phase start overlaps with previous endtime
 - 2020: Phase does not exist on Release
 - 3010: Unable to withdraw ERC20
 */
contract AmberIslandBottlesV2 is ERC721Upgradeable, ERC721RoyaltyUpgradeable, AccessControlUpgradeable {
    AmberIslandKey keysContract;
    bytes32 public constant RELEASE_ROLE = keccak256("RELEASE_ROLE");
    bytes32 public constant BOTTLE_STATE_ROLE = keccak256("BOTTLE_STATE_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    enum BottleState {
        Closed,
        Opened,
        Redeemed
    }
    mapping(uint256 => BottleState) tokenIdToBottleState;

    struct Phase {
        uint256 startTime;
        uint256 endTime;
        bool keyHoldersOnly;
        bool relicHoldersOnly;
        uint256 discount;
        IDistribution distribution;
    }

    struct Release {
        bool exists;
        string name;
        uint256 price;
        uint256 maxSupply;
        string uri;
        uint256 nextBottle;
        uint256 bottlesPerKey;
        uint96 royalties;
        BottleState defaultState;
        Phase[] phases;
        uint16 activePhase;
        ERC20 paymentToken;
    }

    uint256 releaseId = 3000000;
    mapping(uint256 => Release) idToRelease;
    mapping(uint256 => mapping(uint256 => uint256)) keyIdToReleaseMinted;
    mapping(uint256 => uint256) tokenIdToReleaseId;
    mapping(uint256 => bool) keyIdToIsRelic;

    address feeRecipient = 0x8b375C1488cf58524Bacc962a2BE732189906739;
    address DELEGATE_REGISTRY_V1 = 0x00000000000076A84feF008CDAbe6409d2FE638B;
    address DELEGATE_REGISTRY_V2 = 0x00000000000000447e69651d841bD8D104Bed493;

    event ReleaseCreated(Release release);

    modifier releaseExists(uint256 id) {
        require(idToRelease[id].exists, "AIBV2: 1020");
        _;
    }

    function initialize(address _keyContractAddress) public initializer {
        __ERC721_init("AmberIslandBottlesV2", "AIB");
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(RELEASE_ROLE, msg.sender);
        _grantRole(BOTTLE_STATE_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        keysContract = AmberIslandKey(_keyContractAddress);
    }

    function safeMint(
        uint256 _release,
        address _to,
        uint256 _keyTokenId,
        uint256 _quantity
    ) public payable releaseExists(_release) {
        Release storage bottle = idToRelease[_release];
        Phase storage releasePhase = bottle.phases[bottle.activePhase];

        require(block.timestamp >= releasePhase.startTime && block.timestamp < releasePhase.endTime, "AIBV2: 1025");

        require(IDistribution(releasePhase.distribution).validateEligible(msg.sender), "AIBV2: Wallet not eligible");

        uint256 bottlePrice = bottle.price;

        if (releasePhase.keyHoldersOnly) {
            require(_keyTokenId != 999999999, "AIBV2: 1041");

            require(_quantity <= bottle.bottlesPerKey, "AIBV2: 1042");

            require(keyIdToReleaseMinted[_keyTokenId][_release] < bottle.bottlesPerKey, "AIBV2: 1050");

            if(releasePhase.relicHoldersOnly) {
                require(keyIdToIsRelic[_keyTokenId], "AIBV2: 1048");
            }    
        }

        if (_keyTokenId != 999999999) {
            require(recipientIsKeyOwner(_to, _keyTokenId), "AIBV2: 1040");

            address tokenOwner = keysContract.ownerOf(_keyTokenId);
            if (IDelegationRegistryV1(DELEGATE_REGISTRY_V1).checkDelegateForContract(_to, tokenOwner, address(keysContract)) || IDelegateRegistryV2(DELEGATE_REGISTRY_V2).checkDelegateForContract(_to, tokenOwner, address(keysContract), "")) {
                _to = tokenOwner;
            }

            if (keyIdToReleaseMinted[_keyTokenId][_release] < bottle.bottlesPerKey) {
                bottlePrice = bottlePrice - releasePhase.discount;
            }
        }

        if((bottle.nextBottle - 1) + _quantity > bottle.maxSupply) {
            _quantity = bottle.maxSupply - bottle.nextBottle;
        }

        ERC20 paymentToken = bottle.paymentToken;
        require(paymentToken.transferFrom(msg.sender, feeRecipient, (bottlePrice * _quantity)), "AIBV2: 1030");

        for (uint8 i = 0; i < _quantity; i++) {
            executeMint(_release, _to, _keyTokenId);
        }
    }

    function recipientIsKeyOwner(address _recipient, uint256 _keyTokenId) public view returns (bool) {
        address tokenOwner = keysContract.ownerOf(_keyTokenId);
        return tokenOwner == _recipient || IDelegationRegistryV1(DELEGATE_REGISTRY_V1).checkDelegateForContract(_recipient, tokenOwner, address(keysContract)) || IDelegateRegistryV2(DELEGATE_REGISTRY_V2).checkDelegateForContract(_recipient, tokenOwner, address(keysContract), "");
    }

    function orderMint(
        uint256 _release,
        address _to,
        uint256 _keyTokenId
    ) public onlyRole(MINTER_ROLE) {
        executeMint(_release, _to, _keyTokenId);
    }

    function executeMint(
        uint256 _release,
        address _to,
        uint256 _keyTokenId
    ) internal {
        Release storage bottle = idToRelease[_release];

        uint256 bottleCount = bottle.nextBottle;
        require(bottleCount <= bottle.maxSupply, "AIBV2: 1044");

        uint256 tokenId = 10000000000 + _release + bottleCount;
        idToRelease[_release].nextBottle = bottleCount + 1;
        _safeMint(_to, tokenId);
        _setTokenRoyalty(tokenId, feeRecipient, bottle.royalties);

        if(_keyTokenId != 999999999) {
            keyIdToReleaseMinted[_keyTokenId][_release]++;
        }

        tokenIdToBottleState[tokenId] = bottle.defaultState;
        tokenIdToReleaseId[tokenId] = _release;
    }

    function createRelease(
        Release calldata _release
    ) public onlyRole(RELEASE_ROLE) {
        Phase memory initialPhase = _release.phases[0];
        require(initialPhase.endTime > initialPhase.startTime, "AIBV2: 2010");

        idToRelease[releaseId] = _release;

        emit ReleaseCreated(idToRelease[releaseId]);

        releaseId = releaseId + 1000000;
    }

    function setActiveReleasePhase(uint256 _releaseId, uint16 _phaseId) public onlyRole(RELEASE_ROLE) {
        Release storage release = idToRelease[_releaseId];
        require(release.phases.length > _phaseId, "AIBV2: 2020");
        release.activePhase = _phaseId;
    }

    function editReleasePhase(uint256 _releaseId, uint16 _phaseId, Phase memory _newPhase) public onlyRole(RELEASE_ROLE) {
        Phase[] storage phases = idToRelease[_releaseId].phases;

        phases[_phaseId] = _newPhase;
    }

    function getReleasePhaseDetails(uint256 _releaseId, uint16 _phaseId) public view returns (Phase memory) {
        Phase[] storage phases = idToRelease[_releaseId].phases;
        require(phases.length > _phaseId, "AIBV2: 2020");

        return phases[_phaseId];
    }

    function getReleaseDetails(
        uint256 release
    ) public view releaseExists(release) returns (Release memory) {
        Release memory details = idToRelease[release];
        return details;
    }

    function setBottlePrice(
        uint256 release,
        uint256 price
    ) public onlyRole(RELEASE_ROLE) {
        idToRelease[release].price = price;
    }

    function getBottlePrice(
        uint256 release,
        bool includeKey
    ) public view releaseExists(release) returns (uint256) {
        uint256 price = idToRelease[release].price;

        if (includeKey) {
            uint256 keyPrice = keysContract.getMintPrice();
            price = price + keyPrice;
        }

        return price;
    }

    function setBottleUri(
        uint256 release,
        string memory uri
    ) public onlyRole(RELEASE_ROLE) {
        idToRelease[release].uri = uri;
    }

    function getKeyUsedForRelease(
        uint256 release,
        uint256 keyTokenId
    ) public view returns (bool) {
         Release storage bottle = idToRelease[release];
        return keyIdToReleaseMinted[keyTokenId][release] < bottle.bottlesPerKey;
    }

    function getCurrentTokenId(
        uint256 release
    ) public view releaseExists(release) returns (uint256) {
        return idToRelease[release].nextBottle;
    }

    function getCurrentReleaseId() public view returns (uint256) {
        return releaseId;
    }

    function setFeeRecipient(
        address _recipient
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        feeRecipient = _recipient;
    }

    function getKeyIsRelic(uint256 _keyId) public view  returns (bool) {
        return keyIdToIsRelic[_keyId];
    }

    function bulkSetIsRelic(uint256[] memory _keyIds, bool _isRelic) public onlyRole(DEFAULT_ADMIN_ROLE) {
        for(uint8 i = 0; i < _keyIds.length; i++) {
            keyIdToIsRelic[_keyIds[i]] = _isRelic;
        }
    }

    function bulkSetBottleState(
        uint256[] memory tokenIds,
        BottleState state
    ) public onlyRole(BOTTLE_STATE_ROLE) {
        for (uint32 i = 0; i < tokenIds.length; i++) {
            tokenIdToBottleState[tokenIds[i]] = state;
        }
    }

    function tokenURI(
        uint256 tokenId
    ) public view override(ERC721Upgradeable) returns (string memory) {
        Release storage bottle = idToRelease[tokenIdToReleaseId[tokenId]];

        string memory state;
        BottleState bottleState = tokenIdToBottleState[tokenId];
        if (bottleState == BottleState.Closed) state = "/prereveal.json";
        else if (bottleState == BottleState.Opened) state = "/revealed.json";
        else if (bottleState == BottleState.Redeemed) state = "/redeemed.json";

        return string.concat(bottle.uri, state);
    }

    function withdraw() public onlyRole(DEFAULT_ADMIN_ROLE) {
        payable(msg.sender).transfer(address(this).balance);
    }

    function withdrawERC20(address _tokenAddress) public onlyRole(DEFAULT_ADMIN_ROLE) {
        IERC20 tokenContract = IERC20(_tokenAddress);
        uint256 balance = tokenContract.balanceOf(address(this));
        require(tokenContract.transfer(feeRecipient, balance), "AIBV2: 3010");
    }

    // The following functions are overrides required by Solidity.

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(ERC721Upgradeable, ERC721RoyaltyUpgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // function _burn(uint256 tokenId) internal override(ERC721, ERC721Upgradeable) {}
}
"
    },
    "contracts/interface/IDelegateRegistryV2.sol": {
      "content": "// SPDX-License-Identifier: CC0-1.0
pragma solidity >=0.8.13;

/**
 * @title IDelegateRegistry
 * @custom:version 2.0
 * @custom:author foobar (0xfoobar)
 * @notice A standalone immutable registry storing delegated permissions from one address to another
 */
interface IDelegateRegistryV2 {
    /// @notice Delegation type, NONE is used when a delegation does not exist or is revoked
    enum DelegationType {
        NONE,
        ALL,
        CONTRACT,
        ERC721,
        ERC20,
        ERC1155
    }

    /// @notice Struct for returning delegations
    struct Delegation {
        DelegationType type_;
        address to;
        address from;
        bytes32 rights;
        address contract_;
        uint256 tokenId;
        uint256 amount;
    }

    /// @notice Emitted when an address delegates or revokes rights for their entire wallet
    event DelegateAll(address indexed from, address indexed to, bytes32 rights, bool enable);

    /// @notice Emitted when an address delegates or revokes rights for a contract address
    event DelegateContract(address indexed from, address indexed to, address indexed contract_, bytes32 rights, bool enable);

    /// @notice Emitted when an address delegates or revokes rights for an ERC721 tokenId
    event DelegateERC721(address indexed from, address indexed to, address indexed contract_, uint256 tokenId, bytes32 rights, bool enable);

    /// @notice Emitted when an address delegates or revokes rights for an amount of ERC20 tokens
    event DelegateERC20(address indexed from, address indexed to, address indexed contract_, bytes32 rights, uint256 amount);

    /// @notice Emitted when an address delegates or revokes rights for an amount of an ERC1155 tokenId
    event DelegateERC1155(address indexed from, address indexed to, address indexed contract_, uint256 tokenId, bytes32 rights, uint256 amount);

    /// @notice Thrown if multicall calldata is malformed
    error MulticallFailed();

    /**
     * -----------  WRITE -----------
     */

    /**
     * @notice Call multiple functions in the current contract and return the data from all of them if they all succeed
     * @param data The encoded function data for each of the calls to make to this contract
     * @return results The results from each of the calls passed in via data
     */
    function multicall(bytes[] calldata data) external payable returns (bytes[] memory results);

    /**
     * @notice Allow the delegate to act on behalf of `msg.sender` for all contracts
     * @param to The address to act as delegate
     * @param rights Specific subdelegation rights granted to the delegate, pass an empty bytestring to encompass all rights
     * @param enable Whether to enable or disable this delegation, true delegates and false revokes
     * @return delegationHash The unique identifier of the delegation
     */
    function delegateAll(address to, bytes32 rights, bool enable) external payable returns (bytes32 delegationHash);

    /**
     * @notice Allow the delegate to act on behalf of `msg.sender` for a specific contract
     * @param to The address to act as delegate
     * @param contract_ The contract whose rights are being delegated
     * @param rights Specific subdelegation rights granted to the delegate, pass an empty bytestring to encompass all rights
     * @param enable Whether to enable or disable this delegation, true delegates and false revokes
     * @return delegationHash The unique identifier of the delegation
     */
    function delegateContract(address to, address contract_, bytes32 rights, bool enable) external payable returns (bytes32 delegationHash);

    /**
     * @notice Allow the delegate to act on behalf of `msg.sender` for a specific ERC721 token
     * @param to The address to act as delegate
     * @param contract_ The contract whose rights are being delegated
     * @param tokenId The token id to delegate
     * @param rights Specific subdelegation rights granted to the delegate, pass an empty bytestring to encompass all rights
     * @param enable Whether to enable or disable this delegation, true delegates and false revokes
     * @return delegationHash The unique identifier of the delegation
     */
    function delegateERC721(address to, address contract_, uint256 tokenId, bytes32 rights, bool enable) external payable returns (bytes32 delegationHash);

    /**
     * @notice Allow the delegate to act on behalf of `msg.sender` for a specific amount of ERC20 tokens
     * @dev The actual amount is not encoded in the hash, just the existence of a amount (since it is an upper bound)
     * @param to The address to act as delegate
     * @param contract_ The address for the fungible token contract
     * @param rights Specific subdelegation rights granted to the delegate, pass an empty bytestring to encompass all rights
     * @param amount The amount to delegate, > 0 delegates and 0 revokes
     * @return delegationHash The unique identifier of the delegation
     */
    function delegateERC20(address to, address contract_, bytes32 rights, uint256 amount) external payable returns (bytes32 delegationHash);

    /**
     * @notice Allow the delegate to act on behalf of `msg.sender` for a specific amount of ERC1155 tokens
     * @dev The actual amount is not encoded in the hash, just the existence of a amount (since it is an upper bound)
     * @param to The address to act as delegate
     * @param contract_ The address of the contract that holds the token
     * @param tokenId The token id to delegate
     * @param rights Specific subdelegation rights granted to the delegate, pass an empty bytestring to encompass all rights
     * @param amount The amount of that token id to delegate, > 0 delegates and 0 revokes
     * @return delegationHash The unique identifier of the delegation
     */
    function delegateERC1155(address to, address contract_, uint256 tokenId, bytes32 rights, uint256 amount) external payable returns (bytes32 delegationHash);

    /**
     * ----------- CHECKS -----------
     */

    /**
     * @notice Check if `to` is a delegate of `from` for the entire wallet
     * @param to The potential delegate address
     * @param from The potential address who delegated rights
     * @param rights Specific rights to check for, pass the zero value to ignore subdelegations and check full delegations only
     * @return valid Whether delegate is granted to act on the from's behalf
     */
    function checkDelegateForAll(address to, address from, bytes32 rights) external view returns (bool);

    /**
     * @notice Check if `to` is a delegate of `from` for the specified `contract_` or the entire wallet
     * @param to The delegated address to check
     * @param contract_ The specific contract address being checked
     * @param from The cold wallet who issued the delegation
     * @param rights Specific rights to check for, pass the zero value to ignore subdelegations and check full delegations only
     * @return valid Whether delegate is granted to act on from's behalf for entire wallet or that specific contract
     */
    function checkDelegateForContract(address to, address from, address contract_, bytes32 rights) external view returns (bool);

    /**
     * @notice Check if `to` is a delegate of `from` for the specific `contract` and `tokenId`, the entire `contract_`, or the entire wallet
     * @param to The delegated address to check
     * @param contract_ The specific contract address being checked
     * @param tokenId The token id for the token to delegating
     * @param from The wallet that issued the delegation
     * @param rights Specific rights to check for, pass the zero value to ignore subdelegations and check full delegations only
     * @return valid Whether delegate is granted to act on from's behalf for entire wallet, that contract, or that specific tokenId
     */
    function checkDelegateForERC721(address to, address from, address contract_, uint256 tokenId, bytes32 rights) external view returns (bool);

    /**
     * @notice Returns the amount of ERC20 tokens the delegate is granted rights to act on the behalf of
     * @param to The delegated address to check
     * @param contract_ The address of the token contract
     * @param from The cold wallet who issued the delegation
     * @param rights Specific rights to check for, pass the zero value to ignore subdelegations and check full delegations only
     * @return balance The delegated balance, which will be 0 if the delegation does not exist
     */
    function checkDelegateForERC20(address to, address from, address contract_, bytes32 rights) external view returns (uint256);

    /**
     * @notice Returns the amount of a ERC1155 tokens the delegate is granted rights to act on the behalf of
     * @param to The delegated address to check
     * @param contract_ The address of the token contract
     * @param tokenId The token id to check the delegated amount of
     * @param from The cold wallet who issued the delegation
     * @param rights Specific rights to check for, pass the zero value to ignore subdelegations and check full delegations only
     * @return balance The delegated balance, which will be 0 if the delegation does not exist
     */
    function checkDelegateForERC1155(address to, address from, address contract_, uint256 tokenId, bytes32 rights) external view returns (uint256);

    /**
     * ----------- ENUMERATIONS -----------
     */

    /**
     * @notice Returns all enabled delegations a given delegate has received
     * @param to The address to retrieve delegations for
     * @return delegations Array of Delegation structs
     */
    function getIncomingDelegations(address to) external view returns (Delegation[] memory delegations);

    /**
     * @notice Returns all enabled delegations an address has given out
     * @param from The address to retrieve delegations for
     * @return delegations Array of Delegation structs
     */
    function getOutgoingDelegations(address from) external view returns (Delegation[] memory delegations);

    /**
     * @notice Returns all hashes associated with enabled delegations an address has received
     * @param to The address to retrieve incoming delegation hashes for
     * @return delegationHashes Array of delegation hashes
     */
    function getIncomingDelegationHashes(address to) external view returns (bytes32[] memory delegationHashes);

    /**
     * @notice Returns all hashes associated with enabled delegations an address has given out
     * @param from The address to retrieve outgoing delegation hashes for
     * @return delegationHashes Array of delegation hashes
     */
    function getOutgoingDelegationHashes(address from) external view returns (bytes32[] memory delegationHashes);

    /**
     * @notice Returns the delegations for a given array of delegation hashes
     * @param delegationHashes is an array of hashes that correspond to delegations
     * @return delegations Array of Delegation structs, return empty structs for nonexistent or revoked delegations
     */
    function getDelegationsFromHashes(bytes32[] calldata delegationHashes) external view returns (Delegation[] memory delegations);

    /**
     * ----------- STORAGE ACCESS -----------
     */

    /**
     * @notice Allows external contracts to read arbitrary storage slots
     */
    function readSlot(bytes32 location) external view returns (bytes32);

    /**
     * @notice Allows external contracts to read an arbitrary array of storage slots
     */
    function readSlots(bytes32[] calldata locations) external view returns (bytes32[] memory);
}
"
