        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
    }

    /// @notice Swaps as little as possible of one token for `amountOut` of another along the specified path (reversed)
    /// @param params The parameters necessary for the multi-hop swap, encoded as `ExactOutputParams` in calldata
    /// @return amountIn The amount of the input token
    function exactOutput(ExactOutputParams calldata params) external payable returns (uint256 amountIn);
}
"
    },
    "contracts/core/LiquidityBootstrapPool.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity =0.8.21;

import "solady/src/utils/SafeTransferLib.sol";
import "solady/src/utils/MerkleProofLib.sol";
import "solady/src/utils/LibString.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";

import "./Treasury.sol";

import "../lib/WeightedMathLib.sol";
import "../lib/LiquidityBootstrapLib.sol";

import "../interfaces/IDecimals.sol";
import "../interfaces/ILiquidityBootstrapPool.sol";

import "../extensions/Pausable.sol";
import "../extensions/TokensRescuer.sol";

import "../extensions/TokensRescuer.sol";

interface IWETH {
    function deposit() external payable;
    function withdrawTo(address recipient, uint256 value) external;
}

struct SwapDepositInfo {
    bytes path;
    uint256 amountIn;
    uint256 minAmountOut;
}

struct SwapWithdrawInfo {
    bytes path;
    uint256 minAmountOut;
    bool toUnwrap;
}

contract LiquidityBootstrapPool is
    ILiquidityBootstrapPool,
    ReentrancyGuardUpgradeable,
    Pausable,
    OwnableUpgradeable,
    TokensRescuer
{
    using LiquidityBootstrapLib for *;

    using FixedPointMathLib for *;

    using SafeTransferLib for *;

    using WeightedMathLib for *;

    using MerkleProofLib for *;

    using LibString for *;

    /// @dev Emitted when assets are swapped for shares.
    /// @param caller The address of the caller initiating the swap.
    /// @param assets The amount of assets being swapped.
    /// @param shares The amount of shares received in the swap.
    /// @param swapFee The amount of fee charged in the swap.
    event Buy(
        address indexed caller,
        uint256 assets,
        uint256 shares,
        uint256 swapFee
    );

    /// @dev Emitted when shares are swapped for assets.
    /// @param caller The address of the caller initiating the swap.
    /// @param shares The amount of shares being swapped.
    /// @param assets The amount of assets received in the swap.
    /// @param swapFee The amount of fee charged in the swap.
    event Sell(
        address indexed caller,
        uint256 shares,
        uint256 assets,
        uint256 swapFee
    );

    /// @dev Emitted when shares are redeemed.
    /// @param caller The address of the caller initiating the redemption.
    /// @param shares The amount of shares being redeemed.
    event Redeem(
        address indexed caller,
        uint256 indexed streamID,
        uint256 shares
    );

    /// @dev Emitted when the liquidity pool is closed.
    /// @param assets The amount of assets transferred out during the pool closure.
    event Close(
        uint256 assets,
        uint256 platformFees,
        uint256 swapFeesAsset,
        uint256 swapFeesShare
    );

    event Cancel();

    /// @notice Mapping to track the purchased shares for each address.
    mapping(address => uint256) public purchasedShares;

    /// @notice Mapping to track the assets referred by each address.
    mapping(address => uint256) public referredAssets;

    /// @notice Mapping to track the redeemed shares for each address.
    mapping(address => uint256) public redeemedShares;

    /// @notice The total number of purchased shares in the pool.
    uint256 public totalPurchased;

    /// @notice The total amount of assets referred in the pool.
    uint256 public totalReferred;

    /// @notice The total swap fee amount in asset charged to users.
    uint256 public totalSwapFeesAsset;

    /// @notice The total swap fee amount in lbp token charged to users.
    uint256 public totalSwapFeesShare;

    /// @notice Flag to indicate if the liquidity pool is closed.
    bool public closed;

    address public asset;
    uint88 public virtualAssets;
    bool public sellingAllowed;

    address public share;
    uint88 public virtualShares;

    uint88 public maxSharePrice;
    uint88 public maxTotalSharesOut;
    uint40 public saleEnd;
    uint40 public saleStart;

    uint88 public maxTotalAssetsIn;
    uint64 public weightStart;
    uint64 public weightEnd;

    address public manager;
    uint64 public swapFee;
    address public platform;
    uint64 public referrerFee;
    uint64 public platformFee;

    bytes32 public whitelistMerkleRoot;

    mapping(address => address) public referrers;

    uint256 public assetsStart;
    uint256 public cap;
    address public weth;
    address public router;
    uint8 public constant ADDRESS_SIZE = 20;

    mapping(address arbiter => bool isPending) public uncommittedArbiters;
    address[] public uncommittedArbitersArr;

    mapping(address => uint256) public referredShares;
    uint256 public totalReferredShares;

    /// @notice Modifier to restrict access to whitelisted addresses.
    /// @dev This modifier checks if the caller's address is whitelisted using a Merkle proof.
    modifier onlyWhitelisted(bytes32[] memory proof, address recipient) {
        if (whitelisted()) {
            if (
                !proof.verify(
                    whitelistMerkleRoot,
                    keccak256(abi.encodePacked(recipient))
                )
            ) {
                revert("WhitelistProof");
            }
        }
        _;
    }

    /// @notice Modifier to check if the sale is active.
    /// @dev This modifier checks if the current timestamp is within the sale period.
    modifier whenSaleActive() {
        if (
            block.timestamp < saleStart || block.timestamp >= saleEnd || closed
        ) {
            revert("TradingDisallowed");
        }
        _;
    }

    /// @notice Modifier to check if selling is allowed.
    /// @dev This modifier checks if selling of shares is allowed.
    modifier whenSellingAllowed() {
        if (!sellingAllowed) {
            revert("SellingDisallowed");
        }
        _;
    }

    modifier onlyManager() {
        if (msg.sender != manager) {
            revert("CallerDisallowed");
        }
        _;
    }

    /// @notice Initializes contract
    function initialize(
        PoolDeploySettings memory poolDeploySettings
    ) external initializer {
        __ReentrancyGuard_init();
        __Ownable_init_unchained(msg.sender);

        manager = poolDeploySettings.manager;
        swapFee = poolDeploySettings.swapFee;
        referrerFee = poolDeploySettings.referrerFee;
        platformFee = poolDeploySettings.platformFee;

        asset = poolDeploySettings.asset;
        virtualAssets = poolDeploySettings.virtualAssets;
        sellingAllowed = poolDeploySettings.sellingAllowed;

        share = poolDeploySettings.share;
        virtualShares = poolDeploySettings.virtualShares;

        maxSharePrice = poolDeploySettings.maxSharePrice;
        maxTotalSharesOut = poolDeploySettings.maxTotalSharesOut;
        saleEnd = poolDeploySettings.saleEnd;
        saleStart = poolDeploySettings.saleStart;

        maxTotalAssetsIn = poolDeploySettings.maxTotalAssetsIn;
        weightStart = poolDeploySettings.weightStart;
        weightEnd = poolDeploySettings.weightEnd;

        platform = poolDeploySettings.platform;
        whitelistMerkleRoot = poolDeploySettings.whitelistMerkleRoot;
        cap = poolDeploySettings.cap;
        weth = poolDeploySettings.weth;
        router = poolDeploySettings.router;

        uncommittedArbitersArr = poolDeploySettings.arbiters;
        for (uint i = 0; i < poolDeploySettings.arbiters.length; i++) {
            address arbiter = poolDeploySettings.arbiters[i];
            bool isWhitelisted = Treasury(platform).arbiters(arbiter);

            if (isWhitelisted) {
                uncommittedArbiters[arbiter] = true;
            }
        }
    }

    function setAssetsStart() external onlyOwner {
        if (assetsStart != 0) {
            revert("assetsStart != 0");
        }

        assetsStart = asset.balanceOf(address(this));
    }

    function cancel(address sharesReceiver) external onlyManager {
        if (block.timestamp > saleStart) {
            revert("block.timestamp > saleStart");
        }

        closed = true;

        share.safeTransfer(sharesReceiver, share.balanceOf(address(this)));
        asset.safeTransfer(sharesReceiver, asset.balanceOf(address(this)));

        emit Cancel();
    }

    /// @notice Check if the whitelist is enabled.
    /// @dev This flag indicates whether the whitelist is enabled.
    /// @return True if the whitelist is enabled, false otherwise.
    function whitelisted() public view returns (bool) {
        return whitelistMerkleRoot != 0;
    }

    function setWhitelist(bytes32 _whitelistMerkleRoot) external onlyManager {
        whitelistMerkleRoot = _whitelistMerkleRoot;
    }

    function buyTokensInWithSwap(
        SwapDepositInfo memory swapInfo,
        uint256 minSharesOut,
        address recipient,
        address referrer,
        bytes32[] memory proof
    ) public payable nonReentrant returns (uint256) {
        (address tokenIn, address _asset) = _getTokens(swapInfo.path);
        _checkTokens(_asset, asset);

        if (msg.value > 0) {
            if (tokenIn == weth) {
                swapInfo.amountIn = msg.value;
                IWETH(weth).deposit{ value: msg.value }();
            } else {
                revert("Msg value");
            }
        } else {
            tokenIn.safeTransferFrom(
                msg.sender,
                address(this),
                swapInfo.amountIn
            );
        }

        tokenIn.safeApprove(router, swapInfo.amountIn);
        uint256 assetsIn = _uniSwap(
            swapInfo.path,
            swapInfo.amountIn,
            swapInfo.minAmountOut
        );
        (uint256 sharesOut, uint256 returnedAmount) = _buyTokensIn(
            assetsIn,
            minSharesOut,
            recipient,
            referrer,
            proof,
            true
        );

        if (returnedAmount > 0) {
            asset.safeTransfer(msg.sender, returnedAmount);
        }

        return sharesOut;
    }

    function sellTokensInWithSwap(
        SwapWithdrawInfo memory swapInfo,
        uint256 sharesIn,
        uint256 minAssetsOut,
        address recipient,
        bytes32[] memory proof
    ) public payable nonReentrant returns (uint256 tokensOut) {
        (address _asset, address tokenOut) = _getTokens(swapInfo.path);

        _checkTokens(_asset, asset);

        uint256 assetsOut = _sellTokensIn(
            sharesIn,
            minAssetsOut,
            recipient,
            proof
        );
        asset.safeApprove(router, assetsOut);
        tokensOut = _uniSwap(swapInfo.path, assetsOut, swapInfo.minAmountOut);

        if (swapInfo.toUnwrap && tokenOut == weth) {
            IWETH(weth).withdrawTo(recipient, tokensOut);
        } else {
            tokenOut.safeTransfer(recipient, tokensOut);
        }
    }

    function buyTokensIn(
        uint256 assetsIn,
        uint256 minSharesOut,
        address recipient,
        address referrer,
        bytes32[] memory proof
    ) public nonReentrant returns (uint256) {
        (uint256 sharesOut, uint256 returnedAmount) = _buyTokensIn(
            assetsIn,
            minSharesOut,
            recipient,
            referrer,
            proof,
            false
        );

        asset.safeTransferFrom(
            msg.sender,
            address(this),
            assetsIn - returnedAmount
        );

        return sharesOut;
    }

    function sellTokensIn(
        uint256 sharesIn,
        uint256 minAssetsOut,
        address recipient,
        bytes32[] memory proof
    ) public nonReentrant returns (uint256 assetsOut) {
        assetsOut = _sellTokensIn(sharesIn, minAssetsOut, recipient, proof);

        asset.safeTransfer(recipient, assetsOut);
    }

    /// @notice Close the pool and distribute assets and shares accordingly.
    /// @dev This function closes the pool after the sale has ended and distributes
    /// assets to the platform fee and the manager, and shares to the manager for
    /// any unsold shares. Once closed, the pool cannot be used for further transactions.
    function close() external {
        uint256 assetBalance = asset.balanceOf(address(this));
        if (cap > 0 && assetBalance >= cap) {
            address _factoryOwner = OwnableUpgradeable(
                OwnableUpgradeable(owner()).owner()
            ).owner();
            if (msg.sender != manager && msg.sender != _factoryOwner) {
                revert("Must be manager or factory owner");
            }
        } else if (block.timestamp < saleEnd) revert("Sales not completed");

        if (closed) revert("ClosingDisallowed");

        uint256 raisedAssets = assetBalance.rawSub(totalSwapFeesAsset).rawSub(
            assetsStart
        );
        uint256 platformFees = raisedAssets.mulWad(platformFee);
        uint256 totalAssetsMinusFees = raisedAssets.rawSub(platformFees).rawSub(
            totalReferred
        );

        {
            if (swapFee + referrerFee + platformFee > 0) {
                uint256 totalAssetsFee = platformFees + totalSwapFeesAsset;
                if (totalAssetsFee > 0) {
                    asset.safeTransfer(platform, totalAssetsFee);
                }
                if (sellingAllowed && totalSwapFeesShare > 0) {
                    share.safeTransfer(platform, totalSwapFeesShare);
                }
                Treasury(platform).distributeFee(
                    asset,
                    share,
                    platformFees,
                    totalSwapFeesAsset,
                    totalSwapFeesShare
                );
            }
        }

        asset.safeTransfer(
            manager,
            asset.balanceOf(address(this)).rawSub(totalReferred)
        );

        uint256 totalShares = share.balanceOf(address(this));
        uint256 unsoldShares = totalShares.rawSub(totalPurchased).rawSub(
            totalReferredShares
        );

        if (unsoldShares != 0) {
            share.safeTransfer(manager, unsoldShares);
        }

        closed = true;

        emit Close(
            totalAssetsMinusFees,
            platformFees,
            totalSwapFeesAsset,
            totalSwapFeesShare
        );
    }

    /// @notice Redeem shares and, if referred, assets.
    /// @dev This function allows users to redeem their shares and, if they
    /// have been referred, receive assets.
    /// @param recipient The address to receive redeemed shares and assets.
    /// @param referred A boolean indicating whether the user has been referred.
    /// @return shares The number of shares redeemed.
    function redeem(
        address recipient,
        bool referred
    )
        external
        returns (
            uint256 shares,
            uint256 _refferedAssets,
            uint256 _refferedShares
        )
    {
        if (!closed) revert("RedeemingDisallowed");

        uint256 streamID;

        shares = purchasedShares[msg.sender];
        delete purchasedShares[msg.sender];
        share.safeTransfer(msg.sender, shares);

        if (referred && referrerFee != 0) {
            _refferedAssets = referredAssets[msg.sender];
            _refferedShares = referredShares[msg.sender];

            delete referredAssets[msg.sender];
            delete referredShares[msg.sender];

            asset.safeTransfer(recipient, _refferedAssets);
            share.safeTransfer(recipient, _refferedShares);
        }

        if (shares != 0) {
            emit Redeem(msg.sender, streamID, shares);
        }
    }

    /// @notice Toggle the pause state of the pool.
    /// @dev This function allows the manager to pause and unpause the pool.
    /// When the pool is paused, no new swaps can be executed.
    function togglePause() external onlyManager {
        _togglePause();
    }

    /// @notice Get the pool arguments including reserves, weights, and other parameters.
    /// @dev This function returns the current pool configuration including asset
    /// and share reserves, weights, and other parameters.
    /// @return pool A struct containing the pool configuration.
    function args() public view returns (Pool memory) {
        return
            Pool(
                asset,
                share,
                asset.balanceOf(address(this)).rawSub(totalSwapFeesAsset),
                share.balanceOf(address(this)).rawSub(totalSwapFeesShare),
                virtualAssets,
                virtualShares,
                weightStart,
                weightEnd,
                saleStart,
                saleEnd,
                totalPurchased,
                maxSharePrice
            );
    }

    function poolInfo()
        external
        view
        returns (
            Pool memory,
            uint64,
            uint64,
            uint64,
            uint256,
            bool,
            uint8,
            uint8
        )
    {
        return (
            args(),
            swapFee,
            referrerFee,
            platformFee,
            totalSwapFeesShare,
            paused,
            IDecimals(asset).decimals(),
            IDecimals(share).decimals()
        );
    }

    /// @notice Get the reserves and weights of the pool.
    /// @dev This function returns the current asset and share reserves, as well
    /// as the asset and share weights.
    /// @return assetReserve The current asset reserve.
    /// @return shareReserve The current share reserve.
    /// @return assetWeight The asset weight.
    /// @return shareWeight The share weight.
    function reservesAndWeights()
        external
        view
        returns (
            uint256 assetReserve,
            uint256 shareReserve,
            uint256 assetWeight,
            uint256 shareWeight
        )
    {
        return args().computeReservesAndWeights();
    }

    /// @notice Preview the amount of assets required to receive a specific number of shares.
    /// @dev This function calculates the amount of assets needed to obtain a certain
    /// number of shares based on the current pool configuration.
    /// @param sharesOut The number of shares desired.
    /// @return assetsIn The amount of assets required.
    function previewAssetsIn(
        uint256 sharesOut
    ) external view returns (uint256 assetsIn) {
        return args().previewAssetsIn(sharesOut).mulWad(1e18 + swapFee);
    }

    /// @notice Preview the number of shares that will be received for a specific amount of assets.
    /// @dev This function calculates the number of shares that will be received for a
    /// given amount of assets based on the current pool configuration.
    /// @param assetsIn The amount of assets used.
    /// @return sharesOut The number of shares received.
    function previewSharesOut(
        uint256 assetsIn
    ) public view returns (uint256 sharesOut) {
        return args().previewSharesOut(assetsIn.mulWad(1e18 - swapFee));
    }

    /// @notice Preview the number of shares that need to be used to obtain a specific amount of assets.
    /// @dev This function calculates the number of shares required to obtain a certain
    /// amount of assets based on the current pool configuration.
    /// @param assetsOut The amount of assets desired.
    /// @return sharesIn The number of shares required.
    function previewSharesIn(
        uint256 assetsOut
    ) external view returns (uint256 sharesIn) {
        return args().previewSharesIn(assetsOut).mulWad(1e18 + swapFee);
    }

    function scaledReserves(uint256 assetsIn) external view returns (uint256) {
        (
            uint256 assetReserve,
            uint256 shareReserve,
            uint256 assetWeight,
            uint256 shareWeight
        ) = args().computeReservesAndWeights();

        (uint256 assetReserveScaled, uint256 shareReserveScaled) = args()
            .scaledReserves(assetReserve, shareReserve);

        uint256 assetsInScaled = LiquidityBootstrapLib.scaleTokenBefore(
            args().asset,
            assetsIn
        );

        uint256 sharesOut = WeightedMathLib.getAmountOut(
            assetsInScaled,
            assetReserveScaled,
            shareReserveScaled,
            assetWeight,
            shareWeight
        );

        return sharesOut;
    }

    /// @notice Preview the amount of assets that will be received for a specific number of shares.
    /// @dev This function calculates the amount of assets that will be received for a
    /// given number of shares based on the current pool configuration.
    /// @param sharesIn The number of shares used.
    /// @return assetsOut The amount of assets received.
    function previewAssetsOut(
        uint256 sharesIn
    ) external view returns (uint256 assetsOut) {
        return args().previewAssetsOut(sharesIn.mulWad(1e18 - swapFee));
    }

    /// @notice Rescues ERC20 tokens sent to this contract.
    /// @param token The token address to rescue.
    /// @param amount Amount of tokens to rescue.
    function rescueERC20Token(address token, uint256 amount) external {
        if (token == asset || token == share) {
            revert("Except pool tokens");
        }

        _rescueERC20Token(token, amount, owner());
    }

    /// @notice Rescues native tokens sent to this contract.
    /// @param amount Amount of native tokens to rescue.
    function rescueNativeToken(uint256 amount) external {
        _rescueNativeToken(amount, owner());
    }

    function _buyTokensIn(
        uint256 assetsIn,
        uint256 minSharesOut,
        address recipient,
        address referrer,
        bytes32[] memory proof,
        bool isExecutor
    )
        internal
        whenNotPaused
        whenSaleActive
        onlyWhitelisted(proof, recipient)
        returns (uint256 sharesOut, uint256 returnedAmount)
    {
        Pool memory pool = args();

        uint256 assetsBalance = asset.balanceOf(address(this));
        if (isExecutor) {
            pool.assets -= assetsIn;
            assetsBalance -= assetsIn;
        }

        if (assetsBalance >= cap && cap > 0) {
            return (0, assetsIn);
        }

        {
            uint256 totalAmount = assetsBalance + assetsIn;
            if (cap > 0 && totalAmount > cap) {
                returnedAmount = totalAmount - cap;
                uint256 remainAssets = assetsIn - returnedAmount;
                if (minSharesOut != 0 && remainAssets != 0) {
                    minSharesOut = minSharesOut.mulDiv(remainAssets, assetsIn);
                }
                assetsIn = remainAssets;
            }
        }

        if (assetsIn > 0) {
            uint256 swapFees = assetsIn.mulWad(swapFee);
            totalSwapFeesAsset += swapFees;
            sharesOut = pool.previewSharesOut(assetsIn.rawSub(swapFees));
            if (sharesOut < minSharesOut) revert("SlippageExceeded");
            _swapAssetsForShares(
                recipient,
                referrer,
                assetsIn,
                sharesOut,
                pool.assets,
                pool.shares,
                swapFees
            );
        }
    }

    function _sellTokensIn(
        uint256 sharesIn,
        uint256 minAssetsOut,
        address recipient,
        bytes32[] memory proof
    )
        internal
        whenNotPaused
        whenSellingAllowed
        onlyWhitelisted(proof, recipient)
        whenSaleActive
        returns (uint256 assetsOut)
    {
        Pool memory pool = args();

        uint256 swapFees = sharesIn.mulWad(swapFee);
        uint256 sharesReferred = sharesIn.mulWad(referrerFee);
        address actualReferrer = referrers[recipient];

        if (actualReferrer != address(0) && referrerFee != 0) {
            referredShares[actualReferrer] = referredShares[actualReferrer]
                .rawAdd(sharesReferred);

            totalReferredShares += sharesReferred;
            totalSwapFeesShare += swapFees;
        } else {
            totalSwapFeesShare += swapFees + sharesReferred;
        }

        assetsOut = pool.previewAssetsOut(sharesIn.rawSub(swapFees));

        if (assetsOut < minAssetsOut) revert("SlippageExceeded");

        _swapSharesForAssets(
            recipient,
            assetsOut,
            sharesIn,
            pool.assets,
            pool.shares,
            swapFees
        );
    }

    function _swapSharesForAssets(
        address recipient,
        uint256 assetsOut,
        uint256 sharesIn,
        uint256 assets,
        uint256 shares,
        uint256 swapFees
    ) internal {
        if (assets >= maxTotalAssetsIn) {
            revert("AssetsInExceeded");
        }

        uint256 totalPurchasedBefore = totalPurchased;

        if (
            totalPurchasedBefore >= maxTotalSharesOut ||
            totalPurchasedBefore >= shares
        ) {
            revert("SharesOutExceeded");
        }

        if (purchasedShares[msg.sender] < sharesIn) {
            revert("BalanceExceeded");
        }

        purchasedShares[msg.sender] -= sharesIn;
        totalPurchased = totalPurchasedBefore.rawSub(sharesIn);

        emit Sell(recipient, sharesIn, assetsOut, swapFees);
    }

    function _swapAssetsForShares(
        address recipient,
        address referrer,
        uint256 assetsIn,
        uint256 sharesOut,
        uint256 assets,
        uint256 shares,
        uint256 swapFees
    ) internal {
        if (assets + assetsIn - swapFees >= maxTotalAssetsIn) {
            revert("AssetsInExceeded");
        }

        uint256 totalPurchasedAfter = totalPurchased + sharesOut;

        if (
            totalPurchasedAfter >= maxTotalSharesOut ||
            totalPurchasedAfter >= shares
        ) {
            revert("SharesOutExceeded");
        }

        totalPurchased = totalPurchasedAfter;
        purchasedShares[recipient] = purchasedShares[recipient].rawAdd(
            sharesOut
        );

        uint256 assetsReferred = assetsIn.mulWad(referrerFee);
        if (referrer != address(0) && referrerFee != 0) {
            address actualReferrer = referrers[recipient];

            if (actualReferrer == address(0)) {
                referrers[recipient] = referrer;
                actualReferrer = referrer;
            }

            totalReferred += assetsReferred;
            referredAssets[actualReferrer] = referredAssets[actualReferrer]
                .rawAdd(assetsReferred);
        } else {
            totalSwapFeesAsset += assetsReferred;
        }

        emit Buy(recipient, assetsIn, sharesOut, swapFees);
    }

    function _uniSwap(
        bytes memory path,
        uint256 amountIn,
        uint256 minAmountOut
    ) internal returns (uint256) {
        ISwapRouter.ExactInputParams memory params = ISwapRouter
            .ExactInputParams({
                path: path,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: minAmountOut
            });

        return ISwapRouter(router).exactInput(params);
    }

    function _getTokens(
        bytes memory path
    ) private pure returns (address tokenIn, address tokenOut) {
        tokenIn = _sliceAddress(path, 0);
        tokenOut = _sliceAddress(path, path.length - ADDRESS_SIZE);
    }

    function _checkTokens(address token0, address token1) private pure {
        require(token0 == token1, "Tokens dismatch");
    }

    /**
     * @notice Extracts a specific slice of the given byte array
     *         as an address.
     * @param input The byte array from which to extract the address.
     * @param start The starting index in the byte array to begin the extraction.
     * @return token The extracted address from the input bytes.
     */
    function _sliceAddress(
        bytes memory input,
        uint256 start
    ) private pure returns (address token) {
        bytes memory addressBytes = new bytes(ADDRESS_SIZE);

        assembly {
            let src := add(add(input, 32), start)
            let dst := add(addressBytes, 32)
            mstore(dst, mload(src))

            token := mload(add(addressBytes, ADDRESS_SIZE))
        }
    }

    uint256[134] private __gap;
}
"
