    "contracts/core/Treasury.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity =0.8.21;

import "solady/src/utils/SafeTransferLib.sol";

import { AccessControlUpgradeable } from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "./LiquidityBootstrapPool.sol";
import "../extensions/TokensRescuer.sol";
import "../lib/WeightedMathLib.sol";

contract Treasury is
    AccessControlUpgradeable,
    OwnableUpgradeable,
    TokensRescuer
{
    using FixedPointMathLib for *;

    using SafeTransferLib for address;

    struct FeeDist {
        address recipient;
        uint256 percent;
    }

    struct ArbiterFee {
        uint256 numerator;
        uint256 denominator;
    }

    event FeeDistUpdated(FeeDist feeRecipient, string feeDistType);

    mapping(address pool => FeeDist[]) public platformFee;
    mapping(address pool => FeeDist[]) public swapFee;
    mapping(address pool => address[] arbiters) public committedArbiters;
    mapping(address arbiter => bool isWhitelisted) public arbiters;

    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");

    ArbiterFee public arbiterFee;

    function __Treasury_init(
        address _owner,
        ArbiterFee memory _arbiterFee
    ) external initializer {
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _setRoleAdmin(MODERATOR_ROLE, DEFAULT_ADMIN_ROLE);
        __Ownable_init_unchained(msg.sender);

        arbiterFee = _arbiterFee;
    }

    function setArbiterFee(
        ArbiterFee memory _arbiterFee
    ) external onlyRole(MODERATOR_ROLE) {
        arbiterFee = _arbiterFee;
    }

    function setArbiter(
        address arbiter,
        bool isWhitelisted
    ) external onlyRole(MODERATOR_ROLE) {
        arbiters[arbiter] = isWhitelisted;
    }

    function acceptPool(address pool) external {
        bool isUncommittedArbiter = LiquidityBootstrapPool(pool)
            .uncommittedArbiters(msg.sender);
        uint256 saleStart = LiquidityBootstrapPool(pool).saleStart();

        if (isUncommittedArbiter && block.timestamp < saleStart) {
            committedArbiters[pool].push(msg.sender);
        }
    }

    function updateFeeDistPlatformFee(
        address pool,
        FeeDist[] calldata _feeDist
    ) external onlyRole(MODERATOR_ROLE) {
        delete platformFee[pool];
        _updateFeeDist(platformFee[pool], _feeDist, "PlatformFee");
    }

    function updateFeeDistSwapFee(
        address pool,
        FeeDist[] calldata _feeDist
    ) external onlyRole(MODERATOR_ROLE) {
        delete swapFee[pool];
        _updateFeeDist(swapFee[pool], _feeDist, "SwapFee");
    }

    function distributeFee(
        address asset,
        address share,
        uint256 platformFeeAssets,
        uint256 swapFeesAssets,
        uint256 swapFeesShares
    ) external {
        _distributeFee(
            asset,
            share,
            platformFeeAssets,
            swapFeesAssets,
            swapFeesShares
        );
    }

    function rescueERC20Token(
        address token,
        uint256 amount,
        address receiver
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _rescueERC20Token(token, amount, receiver);
    }

    function rescueNativeToken(
        uint256 amount,
        address receiver
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _rescueNativeToken(amount, receiver);
    }

    function _updateFeeDist(
        FeeDist[] storage feeDist,
        FeeDist[] calldata _feeDist,
        string memory feeDistType
    ) internal {
        uint256 totalPercentage;
        for (uint8 i = 0; i < _feeDist.length; i++) {
            if (_feeDist[i].recipient == address(0)) revert("ZeroAddress");
            feeDist.push(_feeDist[i]);
            totalPercentage += _feeDist[i].percent;
            emit FeeDistUpdated(_feeDist[i], feeDistType);
        }
        if (totalPercentage != 1 ether) revert("InvalidPercentageSum");
    }

    function _distributeFee(
        address asset,
        address share,
        uint256 platformFeeAssets,
        uint256 swapFeesAssets,
        uint256 swapFeesShares
    ) internal virtual {
        address pool = msg.sender;

        FeeDist[] memory feeDistPlatform = _checkDistSettings(
            platformFee[pool]
        );
        FeeDist[] memory feeDistSwap = _checkDistSettings(swapFee[pool]);
        address[] memory poolPoolArbiters = committedArbiters[pool];

        _distFees(platformFeeAssets, asset, feeDistPlatform);

        if (
            poolPoolArbiters.length == 0 &&
            arbiterFee.numerator * arbiterFee.denominator != 0
        ) {
            _distFees(swapFeesAssets, asset, feeDistSwap);
            _distFees(swapFeesShares, share, feeDistSwap);
        } else {
            uint256 arbitersFeesAssets = (swapFeesAssets *
                arbiterFee.numerator) / arbiterFee.denominator;
            uint256 arbitersFeesShares = (swapFeesShares *
                arbiterFee.numerator) / arbiterFee.denominator;

            // 1/3 volume = arbiters fee
            _distFees(arbitersFeesAssets, asset, poolPoolArbiters);
            _distFees(arbitersFeesShares, share, poolPoolArbiters);

            // 2/3 volume = swap platform fee
            _distFees(swapFeesAssets - arbitersFeesAssets, asset, feeDistSwap);
            _distFees(swapFeesShares - arbitersFeesShares, share, feeDistSwap);
        }
    }

    function _distFees(
        uint256 amount,
        address token,
        FeeDist[] memory feeDist
    ) internal {
        for (uint8 i = 0; i < feeDist.length; i++) {
            uint256 feeP = feeDist[i].percent;
            uint256 feeShare = amount.mulWad(feeP);
            token.safeTransfer(feeDist[i].recipient, feeShare);
        }
    }

    function _distFees(
        uint256 amount,
        address token,
        address[] memory recipients
    ) internal {
        uint256 percentage = 1 ether / recipients.length;
        for (uint8 i = 0; i < recipients.length; i++) {
            uint256 feeP = percentage;
            uint256 feeShare = amount.mulWad(feeP);
            token.safeTransfer(recipients[i], feeShare);
        }
    }

    function _checkDistSettings(
        FeeDist[] memory feeDist
    ) internal view returns (FeeDist[] memory) {
        if (feeDist.length == 0) {
            FeeDist[] memory newFeeDist = new FeeDist[](1);
            newFeeDist[0] = FeeDist(owner(), 1 ether);
            return newFeeDist;
        } else {
            return feeDist;
        }
    }

    uint256[38] private __gap;
}
"
    },
