pragma solidity 0.8.21;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "@openzeppelin/contracts/utils/Address.sol";

import "../interfaces/ITokensRescuer.sol";

import "./CheckerZeroAddr.sol";

abstract contract TokensRescuer is Initializable, CheckerZeroAddr {
    using SafeERC20 for IERC20;

    event ERC20TokenRescue(address token, address recipient, uint256 amount);
    event NativeTokenRescue(address recipient, uint256 amount);

    function _rescueNativeToken(
        uint256 amount,
        address receiver
    ) internal onlyNonZeroAddress(receiver) {
        Address.sendValue(payable(receiver), amount);

        emit NativeTokenRescue(receiver, amount);
    }

    function _rescueERC20Token(
        address token,
        uint256 amount,
        address receiver
    ) internal virtual onlyNonZeroAddress(receiver) onlyNonZeroAddress(token) {
        IERC20(token).safeTransfer(receiver, amount);

        emit ERC20TokenRescue(token, receiver, amount);
    }
}
