pragma solidity ^0.8.17;

//Imports:
//Ownable.sol: This is the OpenZeppelin contract providing ownership control functionalities.
import "@openzeppelin/contracts/access/Ownable.sol";

//Contract Variables:
//readings: An array of DailyReadingsDetails structs to store daily readings information.
contract BTRUDailyReadings is Ownable {
    struct DailyReadingsDetails {
        string name;
        string description;
        string ipfsImageHash;
        string readings;
        string date;
    }

    DailyReadingsDetails[] public readings;

    //Events:
    //DailyReadingsUpdated: Triggered when daily readings are updated.
    event DailyReadingsUpdated(
        string name,
        string description,
        string ipfsImageHash,
        string readings,
        string date
    );

    //Struct:
    //DailyReadingsDetails: Struct to store details of daily readings including name, description, image hash, readings content, and date.
    //Constructor:
    //The contract doesn't have a constructor as it inherits from Ownable, which handles owner initialization.
    function dailyReadingsUpdate(
        string memory _name,
        string memory _description,
        string memory _ipfsImageHash,
        string memory _readings,
        string memory _date
    ) public onlyOwner {
        readings.push(DailyReadingsDetails({
            name: _name,
            description: _description,
            ipfsImageHash: _ipfsImageHash,
            readings: _readings,
            date: _date
        }));

        emit DailyReadingsUpdated(_name, _description, _ipfsImageHash, _readings, _date);
    }

    //Functions:
    //dailyReadingsUpdate: Allows the owner to update daily readings by adding new entries to the readings array.
    //getDailyReadingsCount: Returns the number of daily readings entries.
    //getDailyReadingsDetails: Returns the details of a specific daily readings entry based on its index in the readings array.
    //getDailyReadingsDetailsRange: Returns a range of daily readings entries based on start and end indices.
    //Modifiers:
    //onlyOwner: Restricts access to functions only to the contract owner.
    function getDailyReadingsCount() public view returns (uint256) {
        return readings.length;
    }

    function getDailyReadingsDetails(uint256 index) public view returns (DailyReadingsDetails memory) {
        require(index < readings.length, "Readings index out of bounds");
        return readings[index];
    }

    // Function to get a range of readings details from mapping index array.
    function getDailyReadingsDetailsRange(uint256 startIndex, uint256 endIndex) public view returns (DailyReadingsDetails[] memory) {
        require(endIndex >= startIndex, "End index must be greater or equal to start index");
        require(endIndex < readings.length, "End index out of bounds");

        uint256 rangeSize = endIndex - startIndex + 1;
        DailyReadingsDetails[] memory detailsRange = new DailyReadingsDetails[](rangeSize);

        for (uint256 i = 0; i < rangeSize; i++) {
            detailsRange[i] = readings[startIndex + i];
        }

        return detailsRange;
    }
