// File contracts/Vault.sol

pragma solidity 0.8.9;

contract Vault is Ownable, IERC721Receiver {
    using Address for address;
    using SafeERC20 for IERC20;

    event ImplementationGranted(address account, address implementation, address caller);
    event ImplementationRevoked(address account, address implementation, address caller);
    event ImplementationDisabled(address implementation, address caller);

    event Swept(address token, address recipient, uint256 amount, address caller);
    event SweptERC721(IERC721 token, address recipient, uint256 tokenId, address caller);
    event Received(address account, uint256 value);

    address[] public traders;

    address public immutable factory;

    mapping(address => mapping(address => bool)) public implementations;

    modifier onlyAuth(address implementation) {
        _checkAuth(implementation);
        _;
    }

    constructor(address _factory) {
        factory = _factory;
    }

    function initOwner(address _owner) external {
        require(msg.sender == factory, "Ownable: caller is not the factory");
        _transferOwnership(_owner);
    }

    function execute(address implementation, bytes memory args)
        external
        onlyAuth(implementation)
        returns (bytes memory)
    {
        return _delegateTo(implementation, args);
    }

    function grantImplementation(address account, address implementation) external onlyOwner {
        if (!hasImplementation(account, implementation)) {
            implementations[account][implementation] = true;
            if (!hasTrader(account)) {
                traders.push(account);
            }
            emit ImplementationGranted(account, implementation, _msgSender());
        }
    }

    function revokeImplementation(address account, address implementation) external onlyOwner {
        _revokeImplementation(account, implementation);
        emit ImplementationRevoked(account, implementation, _msgSender());
    }

    function disableImplementation(address implementation) external onlyOwner {
        uint256 length = traders.length;
        for (uint256 i = 0; i < length; i++) {
            _revokeImplementation(traders[i], implementation);
        }
        emit ImplementationDisabled(implementation, _msgSender());
    }

    function _revokeImplementation(address account, address implementation) internal {
        if (hasImplementation(account, implementation)) {
            implementations[account][implementation] = false;
        }
    }

    function sweep(
        address _token,
        address _recipient,
        uint256 _amount
    ) public onlyOwner {
        uint256 balance = 0;
        if (_token == address(0)) {
            balance = (address(this)).balance;
        } else {
            balance = IERC20(_token).balanceOf(address(this));
        }
        require(balance > 0, "Sweep: sweep zero amount");
        require(_amount <= balance, "Sweep: transfer amount exceeds balance");

        if (_token == address(0)) {
            payable(_recipient).transfer(_amount);
        } else {
            IERC20(_token).safeTransfer(_recipient, _amount);
        }
        emit Swept(_token, _recipient, _amount, _msgSender());
    }

    function sweepTo(address _token, address _recipient) public onlyOwner {
        uint256 balance = 0;
        if (_token == address(0)) {
            balance = (address(this)).balance;
        } else {
            balance = IERC20(_token).balanceOf(address(this));
        }
        sweep(_token, _recipient, balance);
    }

    function sweepToOwner(address _token) external onlyOwner {
        sweepTo(_token, owner());
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    function sweepERC721(
        IERC721 _token,
        address _recipient,
        uint256 _tokenId
    ) public onlyOwner {
        _token.approve(_recipient, _tokenId);
        _token.transferFrom(address(this), _recipient, _tokenId);
        emit SweptERC721(_token, _recipient, _tokenId, _msgSender());
    }

    function sweepERC721ToOwner(IERC721 _token, uint256 _tokenId) public onlyOwner {
        sweepERC721(_token, owner(), _tokenId);
    }

    function _delegateTo(address callee, bytes memory data) internal returns (bytes memory) {
        (bool success, bytes memory returnData) = callee.delegatecall(data);
        assembly {
            if eq(success, 0) {
                revert(add(returnData, 0x20), returndatasize())
            }
        }
        return returnData;
    }

    function _checkAuth(address implementation) internal view virtual {
        require(
            isAuthorized(_msgSender(), implementation),
            "Auth: caller is not the owner or is not authorized for implementation"
        );
    }

    function isAuthorized(address account, address implementation) public view virtual returns (bool) {
        if (account == owner()) {
            return true;
        }

        return hasImplementation(account, implementation);
    }

    function hasImplementation(address account, address implementation) public view virtual returns (bool) {
        return implementations[account][implementation];
    }

    function hasTrader(address account) public view virtual returns (bool) {
        uint256 length = traders.length;
        for (uint256 i = 0; i < length; i++) {
            if (traders[i] == account) {
                return true;
            }
        }
        return false;
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }
}
