    "contracts/claim/ClaimCore.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.22;

import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { MerkleProof } from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { IOFT } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oft/interfaces/IOFT.sol";

import { IClaim, IDonate, Currency, Donation } from "./IClaim.sol";

/**
 * @title ClaimCore
 */
abstract contract ClaimCore is IClaim, Ownable {
    bytes32 public merkleRoot;

    IDonate public immutable donateContract;

    uint256 public immutable numeratorUsdc;
    uint256 public immutable numeratorUsdt;
    uint256 public immutable numeratorNative;

    uint256 public constant DENOMINATOR = 10 ** 18;

    address public donateAndClaim;

    modifier onlyDonateAndClaim() {
        if (msg.sender != donateAndClaim) revert OnlyDonateAndClaim();
        _;
    }

    constructor(
        bytes32 _merkleRoot,
        address _donateContract,
        address _stargateUsdc,
        address _stargateUsdt,
        address _stargateNative,
        uint256 _nativePrice,
        address _owner
    ) Ownable(_owner) {
        merkleRoot = _merkleRoot;
        donateContract = IDonate(_donateContract);

        if (_stargateUsdc != address(0)) {
            numeratorUsdc = 10 ** (IERC20Metadata(IOFT(_stargateUsdc).token()).decimals() - 1); // -1 for 10%
        }

        if (_stargateUsdt != address(0)) {
            numeratorUsdt = 10 ** (IERC20Metadata(IOFT(_stargateUsdt).token()).decimals() - 1); // -1 for 10%
        }

        // native is always denominated in 18 decimals
        if (_stargateNative != address(0)) {
            // Validate this is an actual native pool, eg. NOT WETH
            if (IOFT(_stargateNative).token() != address(0)) {
                revert InvalidNativeStargate();
            }

            if (_nativePrice == 0) revert InvalidNativePrice();
            numeratorNative = (10 ** (18 - 1)) / _nativePrice; // -1 for 10%
        }
    }

    function setDonateAndClaim(address _donateAndClaim) external onlyOwner {
        if (donateAndClaim != address(0)) revert DonateAndClaimAlreadySet();
        donateAndClaim = _donateAndClaim;
        emit DonateAndClaimSet(_donateAndClaim);
    }

    /**
     * @notice Quote the donation amount
     * @param _zroAmount amount of zro to calculate required donation against
     * @return donation amount in native and stable coin.
     */
    function requiredDonation(uint256 _zroAmount) public view returns (Donation memory) {
        uint256 usdc = (_zroAmount * numeratorUsdc) / DENOMINATOR;
        uint256 usdt = (_zroAmount * numeratorUsdt) / DENOMINATOR;
        uint256 native = (_zroAmount * numeratorNative) / DENOMINATOR;
        return Donation(usdc, usdt, native);
    }

    function assertDonation(Currency _currency, address _user, uint256 _zroAmount) public view {
        // retrieve the expected and actual donation amounts
        Donation memory expectedDonation = requiredDonation(_zroAmount);
        Donation memory actualDonation = donateContract.getDonation(_user);

        // numerator must be set, otherwise indicates theres an unsupported currency attempting to assert
        // this is a threshold, users can submit more than "required"
        if (_currency == Currency.USDC && numeratorUsdc > 0) {
            if (expectedDonation.usdc > actualDonation.usdc) {
                revert InsufficientDonation(_currency, expectedDonation.usdc, actualDonation.usdc);
            }
        } else if (_currency == Currency.USDT && numeratorUsdt > 0) {
            if (expectedDonation.usdt > actualDonation.usdt) {
                revert InsufficientDonation(_currency, expectedDonation.usdt, actualDonation.usdt);
            }
        } else if (_currency == Currency.Native && numeratorNative > 0) {
            if (expectedDonation.native > actualDonation.native) {
                revert InsufficientDonation(_currency, expectedDonation.native, actualDonation.native);
            }
        } else {
            revert IDonate.UnsupportedCurrency(_currency);
        }
    }

    /**
     * @notice Set the merkle root for the claim
     * @param _merkleRoot New merkle root
     */
    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        // Can be set to zero address to effectively pause
        merkleRoot = _merkleRoot;
        emit MerkleRootSet(_merkleRoot);
    }

    /**
     * @notice Leaf encode the user and amount
     * @param _account User address
     * @param _zroAmount Amount of ZRO
     * @return Leaf hash
     */
    function _leafEncode(address _account, uint256 _zroAmount) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(keccak256(abi.encodePacked(_account, _zroAmount))));
    }

    /**
     * @notice Verify the proof using the merkle root
     * @param _user User address
     * @param _zroAmount Amount of ZRO
     * @param _proof Merkle proof
     * @return True if proof is valid
     */
    function _verifyProof(
        address _user,
        uint256 _zroAmount,
        bytes32[] calldata _proof
    ) internal view virtual returns (bool) {
        bytes32 leaf = _leafEncode(_user, _zroAmount);
        return MerkleProof.verify(_proof, merkleRoot, leaf);
    }
}
"
    },
    "contracts/claim/ClaimRemote.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.22;

import { OAppOptionsType3 } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/libs/OAppOptionsType3.sol";
import { OAppSender } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OAppSender.sol";
import { OAppCore } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OAppCore.sol";
import { RateLimiter } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/utils/RateLimiter.sol";
// solhint-disable-next-line max-line-length
import { MessagingFee, MessagingReceipt, MessagingParams } from "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroEndpointV2.sol";

import { ClaimCore, Currency } from "./ClaimCore.sol";

/**
 * @title ZROClaimRemote
 */
contract ClaimRemote is ClaimCore, OAppSender, OAppOptionsType3, RateLimiter {
    uint8 internal constant MSG_TYPE_CLAIM = 1;
    uint32 public immutable remoteEid;

    constructor(
        uint32 _remoteEid,
        address _endpoint,
        bytes32 _merkleRoot,
        address _donateContract,
        address _stargateUsdc,
        address _stargateUsdt,
        address _stargateNative,
        uint256 _nativePrice,
        address _owner
    )
        ClaimCore(_merkleRoot, _donateContract, _stargateUsdc, _stargateUsdt, _stargateNative, _nativePrice, _owner)
        OAppCore(_endpoint, _owner)
    {
        remoteEid = _remoteEid;
    }

    function setRateLimits(RateLimitConfig[] memory _rateLimitConfigs) external onlyOwner {
        _setRateLimits(_rateLimitConfigs);
    }

    /**
     * @notice Quote the fee for MSG_TYPE_CLAIM to claim tokens from a remote ZROClaimHub contract
     * @param _to The address to send the tokens to on this chain
     * @param _zroAmount The amount of tokens to claim
     * @param _extraOptions Additional options for the message
     * @return fee The MessagingFee for MSG_TYPE_CLAIM
     */
    function quoteClaim(
        address _to,
        uint256 _zroAmount,
        bytes calldata _extraOptions
    ) external view virtual returns (MessagingFee memory fee) {
        bytes memory payload = abi.encode(msg.sender, _zroAmount, _to);
        bytes memory options = combineOptions(remoteEid, MSG_TYPE_CLAIM, _extraOptions);
        return _quote(remoteEid, payload, options, false);
    }

    /**
     * @notice Claim tokens from a remote ZROClaimHub contract. Pay for donation on source chain and send a LZ message
     * to the ZROClaimHub contract on the destination chain. Once the message is received, the ZRO tokens will be sent
     * back to this chain to the provided 'to' address
     * @param _currency The currency of the donation to be paid
     * @param _zroAmount The amount of tokens to claim
     * @param _proof The Merkle proof for the redemption
     * @param _to The address to send the tokens to on this chain
     * @param _extraOptions Additional options for the message
     * @return receipt The MessagingReceipt for the message
     */
    function claim(
        Currency _currency,
        uint256 _zroAmount,
        bytes32[] calldata _proof,
        address _to,
        bytes calldata _extraOptions // need enough to send 'msg.value' back to this chain via, quoteClaimCallback()
    ) external payable returns (MessagingReceipt memory receipt) {
        return _claim(msg.sender, _currency, _zroAmount, _proof, _to, _extraOptions);
    }

    function claim(
        address _user,
        Currency _currency,
        uint256 _zroAmount,
        bytes32[] calldata _proof,
        address _to,
        bytes calldata _extraOptions // need enough to send 'msg.value' back to this chain via, quoteClaimCallback()
    ) external payable onlyDonateAndClaim returns (MessagingReceipt memory receipt) {
        return _claim(_user, _currency, _zroAmount, _proof, _to, _extraOptions);
    }

    function _claim(
        address _user,
        Currency _currency,
        uint256 _zroAmount,
        bytes32[] calldata _proof,
        address _to,
        bytes calldata _extraOptions // need enough to send 'msg.value' back to this chain via, quoteClaimCallback()
    ) internal returns (MessagingReceipt memory receipt) {
        if (_to == address(0)) revert InvalidToAddress();

        // step 1: assert claimer has donated sufficient amount
        assertDonation(_currency, _user, _zroAmount);

        // step 2: validate the proof is correct
        if (!_verifyProof(_user, _zroAmount, _proof)) revert InvalidProof();

        // step 3: rate limiting the transactions
        _checkAndUpdateRateLimit(remoteEid, 1 /** amount */);

        // step 4: generate the message and options for the crosschain call
        bytes memory payload = abi.encode(_user, _zroAmount, _to);
        bytes memory options = combineOptions(remoteEid, MSG_TYPE_CLAIM, _extraOptions);

        // step 5: send the message cross chain
        // solhint-disable-next-line check-send-result
        receipt = endpoint.send{ value: msg.value }(
            MessagingParams(remoteEid, _getPeerOrRevert(remoteEid), payload, options, false),
            payable(_user)
        );

        emit ZRORequested(_user, _zroAmount, _to);
    }
}
"
