contract Doge_Caramelon_ICO is Ownable, Pausable {
    IERC20 public doge_caremelon_token;

    address public treasuryAddress;

    uint256 public ratio;
    uint256 public IFOEndTime;

    event RatioUpdated(uint256 ratio);
    event TreasuryUpdated(address indexed treasuryAddress);
    event withdrawByTreasuryAddress(uint256 amountWithdrawn);
    event BuyToken(address indexed user, uint256 amount);
    event IFOTimeIncreased(uint _endTime);

    receive() external payable {
        buyToken();
    }

    modifier onlyTreasury() {
        require(msg.sender == treasuryAddress, "Only treasury!");
        _;
    }

    constructor(
        address tokenAddr,
        address treasuryAddr,
        uint256 _ratio,
        uint256 endTime
    ) {
        doge_caremelon_token = IERC20(tokenAddr);
        treasuryAddress = treasuryAddr;
        ratio = _ratio;
        IFOEndTime = endTime;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function buyToken() private whenNotPaused {
        require(msg.value != 0, "Invalid amount!");
        require(block.timestamp < IFOEndTime, "IFO expired!");
        uint256 tokenAmountToTransfer = calculateTokenAmount(msg.value);
        require(tokenAmountToTransfer != 0, "0 amount!");
        doge_caremelon_token.transfer(msg.sender, tokenAmountToTransfer);
        emit BuyToken(msg.sender, tokenAmountToTransfer);
    }

    function calculateTokenAmount(uint256 ethAmount)
        public
        view
        returns (uint256 tokenAmount)
    {
        tokenAmount = ((ethAmount * ratio) / 1e18);
    }

    function updateRatio(uint256 _ratio) public onlyOwner whenNotPaused {
        require(_ratio != 0, "0 R");
        ratio = _ratio;
        emit RatioUpdated(_ratio);
    }

    function increaseIFOEndTime(uint256 _endTime)
        public
        onlyOwner
        whenNotPaused
    {
        require(
            _endTime > block.timestamp,
            "Invalid end time!"
        );
        IFOEndTime = _endTime;
        emit IFOTimeIncreased(_endTime);
    }

    function updateTreasuryAddress(address _treasuryAddr)
        public
        onlyOwner
        whenNotPaused
    {
        require(_treasuryAddr != address(0), "0 addr!");
        treasuryAddress = _treasuryAddr;
        emit TreasuryUpdated(_treasuryAddr);
    }

    function withdrawByTreasury() public onlyTreasury whenNotPaused {
        require(block.timestamp > IFOEndTime, "IFO not ended yet!");
        uint256 remainingAmount = doge_caremelon_token.balanceOf(address(this));
        doge_caremelon_token.transfer(msg.sender, remainingAmount);
        emit withdrawByTreasuryAddress(remainingAmount);
    }

    function withdraw(
        address tokenAddress,
        address _toUser,
        uint256 amount
    ) public onlyOwner returns (bool status) {
        require(_toUser != address(0), "0");
        if (tokenAddress == address(0)) {
            require(address(this).balance >= amount, "!");
            require(payable(_toUser).send(amount), "f");
            return true;
        } else {
            require(IERC20(tokenAddress).balanceOf(address(this)) >= amount);
            IERC20(tokenAddress).transfer(_toUser, amount);
            return true;
        }
    }
}
