// File: game.sol

pragma solidity ^0.8.0;



contract SoundSynthesizer is Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _soundIds;

    struct Sound {
        uint256 id;
        string soundUri;
    }

    mapping(uint256 => Sound) public sounds;

    event SoundCreated(uint256 indexed id, string soundUri);
    event SoundsSynthesized(uint256 indexed newSoundId, uint256[] sourceSoundIds);


    constructor(address initialOwner) Ownable(initialOwner) {}

    function createSound(string memory soundUri) public onlyOwner {
        _soundIds.increment();
        uint256 newSoundId = _soundIds.current();
        sounds[newSoundId] = Sound(newSoundId, soundUri);

        emit SoundCreated(newSoundId, soundUri);
    }

    function synthesizeSounds(uint256[] memory sourceSoundIds, string memory newSoundUri) public {
        _soundIds.increment();
        uint256 newSoundId = _soundIds.current();
        sounds[newSoundId] = Sound(newSoundId, newSoundUri);
        
        emit SoundsSynthesized(newSoundId, sourceSoundIds);
    }
}
