 * @title NEF Token Contract (official NEF token for Nefster.com)
 * @dev This contract implements an ERC20 token with additional configrable features:
 *      - Anti-bot tax on transactions for non-whitelisted addresses
 *      - Anti-bot cooldown period to prevent rapid transactions 
 *      - Anti-whale mechanism limiting the maximum transfer amount per transaction
 *      - Owner privileges for adjusting contract parameters and managing whitelist
 */
contract NEF is ERC20, Ownable, Pausable {

    // Maximum transfer amount rate in basis points. 50 = 0.5% of total supply.
    uint256 public maxTransferAmountRate = 50;

    // Cooldown period for transactions to prevent bots from manipulating the price.
    uint256 public cooldownPeriod = 30 seconds;

    // Fee rate applied to transactions between non-whitelisted addresses.
    // Only used to penalize snipe bots on inital Uniswap liquidity pool creation
    uint256 public botFee = 0; 

    // Mapping to store addresses excluded from the anti-whale mechanism.
    mapping(address => bool) private _isExcludedFromAntiWhale;

    // Mapping to store the timestamp of the last transaction for each address.
    mapping(address => uint256) private lastTransaction;

    // Mapping to manage whitelisted addresses that are exempt from taxation.
    mapping(address => bool) private _whitelist;

    /**
     * @dev Constructor for NEF token, initializing the token supply and setting up initial configuration.
     */
    constructor() ERC20("NEFSTER", "NEF") {
        _mint(msg.sender, 21000000000 * (10 ** uint256(decimals()))); // Mint 21 Billion tokens
        _isExcludedFromAntiWhale[msg.sender] = true; // Exclude the owner from anti-whale limits
        _isExcludedFromAntiWhale[address(this)] = true; // Exclude the contract itself from anti-whale limits
        _whitelist[msg.sender] = true; // Add the owner to the whitelist
        _whitelist[address(this)] = true; // Add the contract itself to the whitelist
    }

    /**
     * @dev Overrides the _transfer function of ERC20 to implement configrable anti-bot and anti-whale measures.
     * @param from Address sending the tokens
     * @param to Address receiving the tokens
     * @param amount Amount of tokens to transfer
