    function _transfer(address from, address to, uint256 amount) internal virtual override {
        require(!paused(), "ERC20Pausable: token transfer while paused");
        // Ensure the cooldown period has passed for the sender
        require(block.timestamp - lastTransaction[from] >= cooldownPeriod, "Cooldown period has not passed");

        // Apply anti-whale measures if neither address is excluded
        if (!_isExcludedFromAntiWhale[from] && !_isExcludedFromAntiWhale[to]) {
            uint256 totalSupply = totalSupply();
            uint256 maxTransferAmount = (totalSupply * maxTransferAmountRate) / 10000;
            require(amount <= maxTransferAmount, "Transfer amount exceeds the maxTransferAmount");
        }

        // Apply fee if neither address is whitelisted and a fee rate is set
        if (!_whitelist[from] && !_whitelist[to] && botFee > 0) {
            uint256 feeAmount = (amount * botFee) / 100;
            uint256 transferAmount = amount - feeAmount;
            super._transfer(from, address(this), feeAmount); // Transfer fee to the contract
            amount = transferAmount;
        }

        lastTransaction[from] = block.timestamp; // Update the last transaction timestamp
        super._transfer(from, to, amount); // Proceed with the transfer after applying tax and checks
    }

    /**
