    function setExcludeFromAntiWhale(address _account, bool _excluded) public onlyOwner {
        _isExcludedFromAntiWhale[_account] = _excluded;
    }

    /**
     * @dev Allows the owner to adjust the maximum transfer amount rate.
     * @param _maxTransferAmountRate New maximum transfer amount rate in basis points
     */
    function setMaxTransferAmountRate(uint256 _maxTransferAmountRate) public onlyOwner {
        require(_maxTransferAmountRate <= 10000, "Max transfer amount rate must not exceed 10000 basis points");
        maxTransferAmountRate = _maxTransferAmountRate;
    }

    /**
     * @dev Allows the owner to adjust the cooldown period for transactions.
     * @param _cooldownPeriod New cooldown period in seconds
     */
    function setCooldownPeriod(uint256 _cooldownPeriod) public onlyOwner {
        cooldownPeriod = _cooldownPeriod;
    }

    /**
     * @dev Adds an address to the whitelist, exempting it from taxation.
     * @param _address Address to whitelist
     */
    function addToWhitelist(address _address) public onlyOwner {
        _whitelist[_address] = true;
    }

    /**
     * @dev Removes an address from the whitelist.
     * @param _address Address to remove from the whitelist
     */
    function removeFromWhitelist(address _address) public onlyOwner {
        _whitelist[_address] = false;
    }

    /**
     * @dev Allows the owner to adjust the fee rate applied to transactions.
     * @param _newBotFee New fee rate as a percentage
     */
    function adjustBotFee(uint256 _newBotFee) public onlyOwner {
        require(_newBotFee <= 100, "Tax rate must not exceed 100%");
        botFee = _newBotFee;
    }

    /**
     * @dev Allows the owner to withdraw ERC20 tokens sent to the contract.
     * @param token ERC20 token address
     * @param to Address to receive the withdrawn tokens
     * @param amount Amount of tokens to withdraw
     */
    function withdrawToken(address token, address to, uint256 amount) public onlyOwner {
        IERC20(token).transfer(to, amount);
    }

    /**
     * @dev Checks whether an account is excluded from the anti-whale mechanism.
     * @param account Address of the account to check
