    "contracts/GoodJobMetadataV2.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/utils/Base64.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import './interface/IGoodJob.sol';
import './interface/IArt.sol';
import './interface/IParts.sol';

contract GoodJobMetadataV2 is AccessControl {
    using Strings for uint256;

    bytes32 public constant METADATA_ROLE = keccak256('METADATA_ROLE');

    IArt.Art[] private arts;
    IParts.Parts[] private faces;
    IParts.Parts[] private hands;
    IParts.Parts[] private expressions;

    string private name;
    string private description;
    string private imageUriBase;

    uint256 private switchTokenId;
    uint256[] private switchExpressionId;

    bool public isFreezed = false;

    constructor(string memory _name, string memory _description, string memory _imageUriBase) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(METADATA_ROLE, _msgSender());
        name = _name;
        description = _description;
        imageUriBase = _imageUriBase;
    }

    function setArts(IArt.Art[] memory _arts) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        arts = _arts;
    }

    function setFaces(IParts.Parts[] memory _faces) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        faces = _faces;
    }

    function setHands(IParts.Parts[] memory _hands) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        hands = _hands;
    }

    function setExpressions(IParts.Parts[] memory _expressions) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        expressions = _expressions;
    }

    function setName(string memory _name) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        name = _name;
    }

    function setDescription(string memory _description) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        description = _description;
    }

    function setImageUriBase(string memory _imageUriBase) public onlyRole(METADATA_ROLE) {
        if (isFreezed) {
            revert('already freezed');
        }
        imageUriBase = _imageUriBase;
    }

    function freeze() public onlyRole(METADATA_ROLE) {
        isFreezed = true;
    }

    function setSwitchTokenId(uint256 _switchTokenId) public onlyRole(METADATA_ROLE) {
        switchTokenId = _switchTokenId;
    }

    function setSwitchExpressionId(uint256[] memory _switchExpressionId) public onlyRole(METADATA_ROLE) {
        switchExpressionId = _switchExpressionId;
    }

    function getExpressionId(uint256 tokenId, uint256 expressionId) public view returns (uint256) {
        if (tokenId < switchTokenId) {
            return expressionId;
        }
        bool isTargetExpression = false;
        for (uint256 i = 0; i < switchExpressionId.length; i++) {
            if (expressionId == switchExpressionId[i]) {
                isTargetExpression = true;
                break;
            }
        }

        // switchExpressionIdに含まれるexpressionIdの場合
        if (isTargetExpression) {
            uint256 _hash = uint256(keccak256(abi.encodePacked(tokenId, expressionId)));
            // 全体の確率の20%を維持するためのチェック
            if (_hash % 5 == 0) {
                // 20%の確率で元のexpressionIdを保持
                return expressionId;
            } else {
                // 80%の確率で他のexpressionIdに置き換え
                return getReplacementExpressionId(_hash);
            }
        }

        // switchExpressionIdに含まれないexpressionIdの場合はそのまま返す
        return expressionId;
    }

    function getReplacementExpressionId(uint256 _hash) private view returns (uint256) {
        uint256[] memory remainingExpressions = getRemainingExpressions();
        uint256 index = _hash % remainingExpressions.length;
        return remainingExpressions[index];
    }

    function getRemainingExpressions() private view returns (uint256[] memory) {
        uint256 totalExpressions = expressions.length; // 全expressionIdの数
        uint256[] memory remainingExpressions = new uint256[](totalExpressions - switchExpressionId.length);
        uint256 counter = 0;

        for (uint256 i = 1; i <= totalExpressions; i++) {
            bool isExcluded = false;
            for (uint256 j = 0; j < switchExpressionId.length; j++) {
                // switchExpressionId配列とiの値を比較する際に、1始まりに合わせる
                if (i == switchExpressionId[j]) {
                    isExcluded = true;
                    break;
                }
            }
            if (!isExcluded) {
                remainingExpressions[counter] = i;
                counter++;
            }
        }

        return remainingExpressions;
    }

    function getImage(IGoodJob.GoodJob memory params) public view returns (string memory) {
        return string(abi.encodePacked('data:image/svg+xml;utf8,', getSVG(params)));
    }

    function getSVG(IGoodJob.GoodJob memory params) public view returns (string memory) {
        uint256 artId = params.art + 1;
        uint256 faceId = params.face + 1;
        uint256 handId = params.hand + 1;
        uint256 expressionId = params.expression + 1;

        return
            string(
                abi.encodePacked(
                    "<svg xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' viewBox='0 0 1080 1080'>",
                    "<image xlink:href='",
                    imageUriBase,
                    '/art/',
                    artId.toString(),
                    '.svg',
                    "' x='0' y='0' height='1080px' width='1080px'/>",
                    "<image xlink:href='",
                    imageUriBase,
                    '/body/1.svg',
                    "' x='0' y='0' height='1080px' width='1080px'/>",
                    "<image xlink:href='",
                    imageUriBase,
                    '/face/',
                    faceId.toString(),
                    '.svg',
                    "' x='0' y='0' height='1080px' width='1080px'/>",
                    "<image xlink:href='",
                    imageUriBase,
                    '/hand/',
                    handId.toString(),
                    '.svg',
                    "' x='0' y='0' height='1080px' width='1080px'/>",
                    "<image xlink:href='",
                    imageUriBase,
                    '/expression/',
                    expressionId.toString(),
                    '.svg',
                    "' x='0' y='0' height='1080px' width='1080px'/>",
                    '</svg>'
                )
            );
    }

    function createMetadata(uint256 tokenId, IGoodJob.GoodJob calldata params) public view returns (string memory) {
        IArt.Art memory _art = arts[params.art];
        IParts.Parts memory _face = faces[params.face];
        IParts.Parts memory _hand = hands[params.hand];
        uint256 expressionId = getExpressionId(tokenId, params.expression + 1);
        IParts.Parts memory _expression = expressions[expressionId - 1];

        bytes memory attributes = abi.encodePacked(
            unicode'{"trait_type": "作品名","value": "',
            _art.name,
            unicode'"},{"trait_type": "アーティスト","value": "',
            _art.artist,
            unicode'"},{"trait_type": "所属","value": "',
            _art.belong,
            unicode'"},{"trait_type": "顔","value": "',
            _face.name,
            unicode'"},{"trait_type": "手","value": "',
            _hand.name,
            unicode'"},{"trait_type": "表情","value": "',
            _expression.name,
            '"}'
        );
        return
            string(
                abi.encodePacked(
                    'data:application/json;,',
                    '{"name": "',
                    name,
                    ' #',
                    tokenId.toString(),
                    '","description": "',
                    description,
                    '","image": "',
                    imageUriBase,
                    '/images/',
                    tokenId.toString(),
                    '.png',
                    '","origin_image": "',
                    imageUriBase,
                    '/images/',
                    tokenId.toString(),
                    '.svg',
                    '","attributes": [',
                    attributes,
                    ']}'
                )
            );
    }
}
"
