// SPDX-License-Identifier: AGPL-3.0-or-later
// Temple (interfaces/v2/access/ITempleElevatedAccess.sol)

/**
 * @notice Inherit to add Executor and Rescuer roles for DAO elevated access.
 */ 
interface ITempleElevatedAccess {
    event ExplicitAccessSet(address indexed account, bytes4 indexed fnSelector, bool indexed value);
    event RescueModeSet(bool indexed value);

    event NewRescuerProposed(address indexed oldRescuer, address indexed oldProposedRescuer, address indexed newProposedRescuer);
    event NewRescuerAccepted(address indexed oldRescuer, address indexed newRescuer);

    event NewExecutorProposed(address indexed oldExecutor, address indexed oldProposedExecutor, address indexed newProposedExecutor);
    event NewExecutorAccepted(address indexed oldExecutor, address indexed newExecutor);

    struct ExplicitAccess {
        bytes4 fnSelector;
        bool allowed;
    }

    /**
     * @notice A set of addresses which are approved to execute emergency operations.
     */ 
    function rescuer() external returns (address);

    /**
     * @notice A set of addresses which are approved to execute normal operations on behalf of the DAO.
     */ 
    function executor() external returns (address);

    /**
     * @notice Explicit approval for an address to execute a function.
     * allowedCaller => function selector => true/false
     */
    function explicitFunctionAccess(address contractAddr, bytes4 functionSelector) external returns (bool);

    /**
     * @notice Under normal circumstances, rescuers don't have access to admin/operational functions.
     * However when rescue mode is enabled (by rescuers or executors), they claim the access rights.
     */
    function inRescueMode() external returns (bool);

    /**
     * @notice Set the contract into or out of rescue mode.
     * Only the rescuers or executors are allowed to set.
     */
    function setRescueMode(bool value) external;

    /**
     * @notice Proposes a new Rescuer.
     * Can only be called by the current rescuer.
     */
    function proposeNewRescuer(address account) external;

    /**
     * @notice Caller accepts the role as new Rescuer.
     * Can only be called by the proposed rescuer
     */
    function acceptRescuer() external;

    /**
     * @notice Proposes a new Executor.
     * Can only be called by the current executor or resucer (if in resuce mode)
     */
    function proposeNewExecutor(address account) external;

    /**
     * @notice Caller accepts the role as new Executor.
     * Can only be called by the proposed executor
     */
    function acceptExecutor() external;

    /**
     * @notice Grant `allowedCaller` the rights to call the function selectors in the access list.
     * @dev fnSelector == bytes4(keccak256("fn(argType1,argType2,...)"))
     */
    function setExplicitAccess(address allowedCaller, ExplicitAccess[] calldata access) external;
}
"
    },
    "contracts/interfaces/v2/safeGuards/IThresholdSafeGuard.sol": {
      "content": "pragma solidity 0.8.19;
// SPDX-License-Identifier: AGPL-3.0-or-later
// Temple (interfaces/v2/SafeGuards/IThresholdSafeGuard.sol)

import { Guard } from "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";

interface IThresholdSafeGuard is Guard {
    event DisableGuardChecksSet(bool value);
    event DefaultSignaturesThresholdSet(uint256 threshold);
    event FunctionThresholdSet(address indexed contractAddr, bytes4 indexed functionSignature, uint256 threshold);
    event EthTransferThresholdSet(address indexed contractAddr, uint256 threshold);
    event SafeTxExecutorAdded(address indexed executor);
    event SafeTxExecutorRemoved(address indexed executor);

    error InvalidExecutor();
    error DynamicSignatureThresholdNotMet(uint256 required, uint256 found);

    /**
      * @notice Break-glass to disable this guard such that the default Safe behaviour is resumed.
      * Only the Owner is allowed to disable. Never set the owner to the same safe which this is guarding
      * as it could be bricked.
     */
    function disableGuardChecks() external view returns (bool);

    /**
     * @notice The required number of signatures required by default for all transactions.
     */
    function defaultSignaturesThreshold() external view returns (uint256);

    /**
      * @notice Per contract and function override of required signature thresholds on the Safe.
      * If not explicitly set for a contract/function, then the `defaultSignaturesThreshold` is used.
      * If the threshold is less than the builtin Safe threshold, no signature checks are performed 
      * in this guard -- the Safe has already verified the signers.
     */
    function functionThresholds(address contractAddr, bytes4 functionSignature) external view returns (uint256);

    /**
     * @notice The required signature thresholds for ETH transfers.
     */
    function ethTransferThresholds(address contractAddr) external view returns (uint256);
}"
    },
    "contracts/v2/access/TempleElevatedAccess.sol": {
      "content": "pragma solidity 0.8.19;
// SPDX-License-Identifier: AGPL-3.0-or-later
// Temple (v2/access/TempleElevatedAccess.sol)

import { ITempleElevatedAccess } from "contracts/interfaces/v2/access/ITempleElevatedAccess.sol";
import { CommonEventsAndErrors } from "contracts/common/CommonEventsAndErrors.sol";

/**
 * @notice Inherit to add Executor and Rescuer roles for DAO elevated access.
 */ 
abstract contract TempleElevatedAccess is ITempleElevatedAccess {
    /**
     * @notice The address which is approved to execute emergency operations.
     */ 
    address public override rescuer;

    /**
     * @notice The address which is approved to execute normal operations on behalf of the DAO.
     */ 
    address public override executor;

    /**
     * @notice Explicit approval for an address to execute a function.
     * allowedCaller => function selector => true/false
     */
    mapping(address => mapping(bytes4 => bool)) public override explicitFunctionAccess;

    /**
     * @notice Under normal circumstances, rescuers don't have access to admin/operational functions.
     * However when rescue mode is enabled (by rescuers or executors), they claim the access rights.
     */
    bool public override inRescueMode;

    /// @dev Track proposed rescuer/executor
    address private _proposedNewRescuer;
    address private _proposedNewExecutor;

    constructor(address initialRescuer, address initialExecutor) {
        if (initialRescuer == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        if (initialExecutor == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        if (initialExecutor == initialRescuer) revert CommonEventsAndErrors.InvalidAddress();

        rescuer = initialRescuer;
        executor = initialExecutor;
    }

    /**
     * @notice Set the contract into or out of rescue mode.
     * Only the rescuers are allowed to set.
     */
    function setRescueMode(bool value) external override {
        if (msg.sender != rescuer) revert CommonEventsAndErrors.InvalidAccess();
        emit RescueModeSet(value);
        inRescueMode = value;
    }

    /**
     * @notice Proposes a new Rescuer.
     * Can only be called by the current rescuer.
     */
    function proposeNewRescuer(address account) external override {
        if (msg.sender != rescuer) revert CommonEventsAndErrors.InvalidAccess();
        if (account == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        emit NewRescuerProposed(msg.sender, _proposedNewRescuer, account);
        _proposedNewRescuer = account;
    }

    /**
     * @notice Caller accepts the role as new Rescuer.
     * Can only be called by the proposed rescuer
     */
    function acceptRescuer() external override {
        if (msg.sender != _proposedNewRescuer) revert CommonEventsAndErrors.InvalidAccess();
        if (msg.sender == executor) revert CommonEventsAndErrors.InvalidAddress();

        emit NewRescuerAccepted(rescuer, msg.sender);
        rescuer = msg.sender;
        delete _proposedNewRescuer;
    }

    /**
     * @notice Proposes a new Executor.
     * Can only be called by the current executor or rescuer (if in resuce mode)
     */
    function proposeNewExecutor(address account) external override onlyElevatedAccess {
        if (account == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        emit NewExecutorProposed(executor, _proposedNewExecutor, account);
        _proposedNewExecutor = account;
    }

    /**
     * @notice Caller accepts the role as new Executor.
     * Can only be called by the proposed executor
     */
    function acceptExecutor() external override {
        if (msg.sender != _proposedNewExecutor) revert CommonEventsAndErrors.InvalidAccess();
        if (msg.sender == rescuer) revert CommonEventsAndErrors.InvalidAddress();

        emit NewExecutorAccepted(executor, msg.sender);
        executor = msg.sender;
        delete _proposedNewExecutor;
    }

    /**
     * @notice Grant `allowedCaller` the rights to call the function selectors in the access list.
     * @dev fnSelector == bytes4(keccak256("fn(argType1,argType2,...)"))
     */
    function setExplicitAccess(address allowedCaller, ExplicitAccess[] calldata access) external override onlyElevatedAccess {
        if (allowedCaller == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        uint256 _length = access.length;
        ExplicitAccess memory _access;
        for (uint256 i; i < _length; ++i) {
            _access = access[i];
            emit ExplicitAccessSet(allowedCaller, _access.fnSelector, _access.allowed);
            explicitFunctionAccess[allowedCaller][_access.fnSelector] = _access.allowed;
        }
    }

    function isElevatedAccess(address caller, bytes4 fnSelector) internal view returns (bool) {
        if (inRescueMode) {
            // If we're in rescue mode, then only the rescuers can call
            return caller == rescuer;
        } else if (caller == executor || explicitFunctionAccess[caller][fnSelector]) {
            // If we're not in rescue mode, the executor can call all functions
            // or the caller has been given explicit access on this function
            return true;
        }
        return false;
    }

    /**
     * @notice Under normal operations, only the executors are allowed to call.
     * If 'rescue mode' has been enabled, then only the rescuers are allowed to call.
     * @dev Important: Only for use when called from an *external* contract. 
     * If a function with this modifier is called internally then the `msg.sig` 
     * will still refer to the top level externally called function.
     */
    modifier onlyElevatedAccess() {
        if (!isElevatedAccess(msg.sender, msg.sig)) revert CommonEventsAndErrors.InvalidAccess();
        _;
    }

    /**
     * @notice Only the executors or rescuers can call.
     */
    modifier onlyInRescueMode() {
        if (!(inRescueMode && msg.sender == rescuer)) revert CommonEventsAndErrors.InvalidAccess();
        _;
    }

    modifier notInRescueMode() {
        if (inRescueMode) revert CommonEventsAndErrors.InvalidAccess();
        _;
    }
}
"
    },
    "contracts/v2/safeGuards/SafeForked.sol": {
      "content": "pragma solidity 0.8.19;
// SPDX-License-Identifier: AGPL-3.0-or-later
// Temple (v2/SafeGuards/SafeForked.sol)

import { GnosisSafe } from "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import { ISignatureValidator } from "@gnosis.pm/safe-contracts/contracts/interfaces/ISignatureValidator.sol";

// Ideally this isn't required. 
// See: https://github.com/safe-global/safe-contracts/pull/557
//      https://forum.safe.global/t/safe-contract-v1-5-0/3383
library SafeForked {

    // bytes4(keccak256("isValidSignature(bytes,bytes)")
    // Forked from https://github.com/safe-global/safe-contracts/blob/v1.3.0/contracts/interfaces/ISignatureValidator.sol#L6
    bytes4 internal constant EIP1271_MAGIC_VALUE = 0x20c13b0b;

    /// @notice divides bytes signature into `uint8 v, bytes32 r, bytes32 s`.
    /// @dev Make sure to peform a bounds check for @param pos, to avoid out of bounds access on @param signatures
    /// Forked from Safe https://github.com/safe-global/safe-contracts/blob/v1.3.0/contracts/common/SignatureDecoder.sol#L11
    /// @param pos which signature to read. A prior bounds check of this parameter should be performed, to avoid out of bounds access
    /// @param signatures concatenated rsv signatures
    function signatureSplit(bytes memory signatures, uint256 pos)
        internal
        pure
        returns (
            uint8 v,
            bytes32 r,
            bytes32 s
        )
    {
        // The signature format is a compact form of:
        //   {bytes32 r}{bytes32 s}{uint8 v}
        // Compact means, uint8 is not padded to 32 bytes.
        // solhint-disable-next-line no-inline-assembly
        assembly {
            let signaturePos := mul(0x41, pos)
            r := mload(add(signatures, add(signaturePos, 0x20)))
            s := mload(add(signatures, add(signaturePos, 0x40)))
            // Here we are loading the last 32 bytes, including 31 bytes
            // of 's'. There is no 'mload8' to do this.
            //
            // 'byte' is not working due to the Solidity parser, so lets
            // use the second best option, 'and'
            v := and(mload(add(signatures, add(signaturePos, 0x41))), 0xff)
        }
    }

    /**
     * @notice Checks whether the signature provided is valid for the provided data, hash. Will revert otherwise.
     * @dev Forked from Safe https://github.com/safe-global/safe-contracts/blob/v1.3.0/contracts/GnosisSafe.sol#L240
     * The only functional addition is to pass through the executor, and to throw a more descriptive error if the number of signatories doesn't match.
     * @param executor The address of who is executing the Safe transaction
     * @param safe The Gnosis Safe this is checking signatures for
     * @param dataHash Hash of the data (could be either a message hash or transaction hash)
     * @param data That should be signed (this is passed to an external validator contract)
     * @param signatures Signature data that should be verified. Can be ECDSA signature, contract signature (EIP-1271) or approved hash.
     * @param requiredSignatures Amount of required valid signatures.
     */
    function checkNSignatures(
        address executor, 
        GnosisSafe safe, 
        bytes32 dataHash,
        bytes memory data,
        bytes memory signatures,
        uint256 requiredSignatures
    ) internal view {
        // Check that the provided signature data is not too short
        // Left as a revert string so it shows in the Safe UI, and made more descriptive.
        require(signatures.length >= requiredSignatures * 65, "!Dynamic Signature Threshold");

        // There cannot be an owner with address 0.
        address lastOwner = address(0);
        address currentOwner;
        uint8 v;
        bytes32 r;
        bytes32 s;
        uint256 i;
        for (i = 0; i < requiredSignatures; i++) {
            (v, r, s) = signatureSplit(signatures, i);
            if (v == 0) {
                // If v is 0 then it is a contract signature
                // When handling contract signatures the address of the contract is encoded into r
                currentOwner = address(uint160(uint256(r)));

                // Check that signature data pointer (s) is not pointing inside the static part of the signatures bytes
                // This check is not completely accurate, since it is possible that more signatures than the threshold are send.
                // Here we only check that the pointer is not pointing inside the part that is being processed
                require(uint256(s) >= (requiredSignatures * 65), "GS021");

                // Check that signature data pointer (s) is in bounds (points to the length of data -> 32 bytes)
                require((uint256(s) + 32) <= signatures.length, "GS022");

                // Check if the contract signature is in bounds: start of data is s + 32 and end is start + signature length
                uint256 contractSignatureLen;
                // slither-disable-start assembly
                // solhint-disable-next-line no-inline-assembly
                assembly {
                    contractSignatureLen := mload(add(add(signatures, s), 0x20))
                }
                require((uint256(s) + 32 + contractSignatureLen) <= signatures.length, "GS023");

                // Check signature
                bytes memory contractSignature;
                // solhint-disable-next-line no-inline-assembly
                assembly {
                    // The signature data for contract signatures is appended to the concatenated signatures and the offset is stored in s
                    contractSignature := add(add(signatures, s), 0x20)
                }
                // slither-disable-end assembly
                require(ISignatureValidator(currentOwner).isValidSignature(data, contractSignature) == EIP1271_MAGIC_VALUE, "GS024");
            } else if (v == 1) {
                // If v is 1 then it is an approved hash
                // When handling approved hashes the address of the approver is encoded into r
                currentOwner = address(uint160(uint256(r)));
                // Hashes are automatically approved by the sender of the message or when they have been pre-approved via a separate transaction
                require(executor == currentOwner || safe.approvedHashes(currentOwner, dataHash) != 0, "GS025");
            } else if (v > 30) {
                // If v > 30 then default va (27,28) has been adjusted for eth_sign flow
                // To support eth_sign and similar we adjust v and hash the messageHash with the Ethereum message prefix before applying ecrecover
                currentOwner = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", dataHash)), v - 4, r, s);
            } else {
                // Default is the ecrecover flow with the provided data hash
                // Use ecrecover with the messageHash for EOA signatures
                currentOwner = ecrecover(dataHash, v, r, s);
            }
            require(currentOwner > lastOwner && safe.isOwner(currentOwner), "GS026");
            lastOwner = currentOwner;
        }
    }
}"
    },
    "contracts/v2/safeGuards/ThresholdSafeGuard.sol": {
      "content": "pragma solidity 0.8.19;
// SPDX-License-Identifier: AGPL-3.0-or-later
// Temple (v2/SafeGuards/ThresholdSafeGuard.sol)

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { EnumerableSet } from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { GnosisSafe } from "@gnosis.pm/safe-contracts/contracts/GnosisSafe.sol";
import { Enum } from "@gnosis.pm/safe-contracts/contracts/common/Enum.sol";

import { IThresholdSafeGuard } from "contracts/interfaces/v2/safeGuards/IThresholdSafeGuard.sol";
import { CommonEventsAndErrors } from "contracts/common/CommonEventsAndErrors.sol";
import { SafeForked } from "contracts/v2/safeGuards/SafeForked.sol";
import { TempleElevatedAccess } from "contracts/v2/access/TempleElevatedAccess.sol";

/**
  * @title Threshold Safe Guard
  * @notice This Safe Guard performs two pre-execute checks:
  *    1/ The safeTxExecutor is a safe owner or a pre-approved executor
  *    2/ The number of signers is >= a per contract/function defined threshold
  *       (or a default threshold if not explicitly set per contract/function)
 */
contract ThresholdSafeGuard is IThresholdSafeGuard, TempleElevatedAccess {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    string public constant VERSION = "1.0.0";

    /**
      * @notice Break-glass to disable this guard such that the default Safe behaviour is resumed.
      * Only the Owner is allowed to disable. Never set the owner to the same safe which this is guarding
      * as it could be bricked.
     */
    bool public override disableGuardChecks;

    /**
     * @notice The required number of signatures required by default for all transactions.
     */
    uint256 public override defaultSignaturesThreshold;

    /**
      * @notice Per contract and function override of required signature thresholds on the Safe.
      * If not explicitly set for a contract/function, then the `defaultSignaturesThreshold` is used.
      * If the threshold is less than the builtin Safe threshold, no signature checks are performed 
      * in this guard -- the Safe has already verified the signers.
      */
    mapping(address => mapping(bytes4 => uint256)) public override functionThresholds;

    /**
     * @notice The required signature thresholds for ETH transfers.
     */
    mapping(address => uint256) public override ethTransferThresholds;

    /**
      * @notice Approved addresses which are allowed to execute approved transactions
      * Safe owners are also allowed to execute.
     */
    EnumerableSet.AddressSet internal _safeTxExecutors;

    constructor(address _initialRescuer, address _initialExecutor, uint256 _defaultSignaturesThreshold) 
        TempleElevatedAccess(_initialRescuer, _initialExecutor)
    {
        defaultSignaturesThreshold = _defaultSignaturesThreshold;
    }

    /**
     * @notice Disable the Safe Guard, so all transactions pass if the Gnosis threshold of signers is met.
     *   1. Rescuers setRescueMode(true)
     *   2. Rescuers setDisableGuardChecks(true)
     * Note: Rescuers won't have a Guard on their multisig, so this can't be bricked.
     */
    function setDisableGuardChecks(bool value) external onlyInRescueMode {
        disableGuardChecks = value;
        emit DisableGuardChecksSet(value);
    }

    /**
      * @notice Add an address to the allowed list who can execute the transaction
      * once the minimum threshold of signers have approved
     */
    function addSafeTxExecutor(address account) external onlyElevatedAccess {
        if (account == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        emit SafeTxExecutorAdded(account);
        if (!_safeTxExecutors.add(account)) revert InvalidExecutor();
    }

    /**
      * @notice Remove an address from the allowed list who can execute the transaction
      * once the minimum threshold of signers have approved
     */
    function removeSafeTxExecutor(address account) external onlyElevatedAccess {
        if (account == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        emit SafeTxExecutorRemoved(account);
        if (!_safeTxExecutors.remove(account)) revert InvalidExecutor();
    }

    /**
      * @notice Set the default number of signatories required, when a contract/function signature
      * not explicitly defined.
     */
    function setDefaultSignaturesThreshold(uint256 threshold) external onlyElevatedAccess {
        emit DefaultSignaturesThresholdSet(threshold);
        defaultSignaturesThreshold = threshold;
    }

    /**
      * @notice Set the number of signatories required for a contract/function signature pair.
      * @dev functionSignature=bytes(0) is ok as this represents an ETH transfer which may also have
      * an explicit threshold.
     */
    function setFunctionThreshold(address contractAddr, bytes4 functionSignature, uint256 threshold) external onlyElevatedAccess {
        if (contractAddr == address(0)) revert CommonEventsAndErrors.InvalidAddress();

        emit FunctionThresholdSet(contractAddr, functionSignature, threshold);
        functionThresholds[contractAddr][functionSignature] = threshold;
    }

    /**
      * @notice Set the number of signatories required for a number of function signatures at a time.
     */
    function setFunctionThresholdBatch(address contractAddr, bytes4[] memory functionSignatures, uint256 threshold) external onlyElevatedAccess {
        if (contractAddr == address(0)) revert CommonEventsAndErrors.InvalidAddress();

        uint256 length = functionSignatures.length;
        bytes4 sig;
        for (uint256 i; i < length; ++i) {
            sig = functionSignatures[i];
            emit FunctionThresholdSet(contractAddr, sig, threshold);
            functionThresholds[contractAddr][sig] = threshold;
        }
    }

    /**
      * @notice Set the number of signatories required for a contract/function signature pair.
      * @dev functionSignature=bytes(0) is ok as this represents an ETH transfer which may also have
      * an explicit threshold.
     */
    function setEthTransferThreshold(address contractAddr, uint256 threshold) external onlyElevatedAccess {
        if (contractAddr == address(0)) revert CommonEventsAndErrors.InvalidAddress();
        emit EthTransferThresholdSet(contractAddr, threshold);
        ethTransferThresholds[contractAddr] = threshold;
    }

    /**
      * @notice The set of extra addresses (along with the Safe owners) allowed to execute
      * the transaction once the minimum threshold of signers have approved
     */
    function safeTxExecutors() external view returns (address[] memory executors) {
        return _safeTxExecutors.values();
    }

    /**
      * @notice The required signatory threshold for a given contract and functionSignature
     */
    function getThreshold(address contractAddr, bytes4 fnSignature) public view returns (uint256) {
        uint256 threshold = functionThresholds[contractAddr][fnSignature];
        return threshold > 0 ? threshold : defaultSignaturesThreshold;
    }

    /**
      * @notice The required signatory threshold for a given contract and functionSignature
     */
    function getEthTransferThreshold(address contractAddr) public view returns (uint256) {
        uint256 threshold = ethTransferThresholds[contractAddr];
        return threshold > 0 ? threshold : defaultSignaturesThreshold;
    }

    /**
      * @notice The Safe will call this method once the signatories has been checked vs Safe's builtin threshold.
      * This will revert:
      *    1/ If the safeTxExecutor is not either a safe owner or pre-approved executor
      *    2/ The number of signers does not meet a per contract/function defined threshold
      *       (or a default threshold if not explicitly set per contract/function)
      * 
      * Exceptions:
      *    a/ If the builtin Safe's threshold == 1, the number of signatories here isn't checked. 
      *       So the proposer can execute immediately. 
      *       This is to handle Tenderly transaction simulations via the dapp.
      *    b/ If number of signers for the contract/function is less than the safe threshold - then no extra
      *       signatory checks are done here, as it's already been done in the Safe.
     */
    function checkTransaction(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        // solhint-disable-next-line no-unused-vars
        address payable refundReceiver,
        bytes memory signatures,
        address safeTxExecutor
    ) external view {
        // Circuit breaker
        if (disableGuardChecks) return;

        GnosisSafe safe = GnosisSafe(payable(msg.sender));

        if (!(_safeTxExecutors.contains(safeTxExecutor) || safe.isOwner(safeTxExecutor))) {
            revert InvalidExecutor();
        }

        uint256 threshold;
        {
            uint256 safeThreshold = safe.getThreshold();

            // When Tenderly simulations run, the Safe's threshold is overriden to 1.
            // So only check the signatures when the threshold is > 1
            // WARNING: This means that we aren't doing any extra checks if the Safe's threshold == 1. It should always be > 1
            // Note - if multiple signers have already signed, then the Safe dapp doesn't do this override. So it may still fail if
            //        number of signers == 1 < x < dynamicThresholdRequirement
            if (safeThreshold == 1) return;

            // If the data length is 0, this means it's a native ETH transfer. In this case use the `ETH_TRANSFER_SELECTOR` selector
            // Perhaps in future there could be some custom decoding/approvals based on the arguments too
            threshold = data.length == 0 
                ? getEthTransferThreshold(to)
                : getThreshold(to, bytes4(data));

            // No need for extra threshold checks - it already has the required signers, because we know Safe
            // has checked these already prior to calling the guard.
            if (safeThreshold >= threshold) return;
        }

        // Check the signatures for the function being called.
        {
            bytes memory txHashData = safe.encodeTransactionData(
                to, 
                value, 
                data, 
                operation, 
                safeTxGas, 
                baseGas, 
                gasPrice, 
                gasToken, 
                refundReceiver, 
                safe.nonce() - 1 // Remove one from the nonce, as the Safe.execTransaction increased it prior to calling the guard.
            );
            SafeForked.checkNSignatures(safeTxExecutor, safe, keccak256(txHashData), txHashData, signatures, threshold);
        }
    }

    /// @notice unused
    // solhint-disable-next-line no-empty-blocks
    function checkAfterExecution(bytes32, bool) external view {}

    // solhint-disable-next-line payable-fallback
    fallback() external {
        // We don't revert on fallback to avoid issues in case of a Safe upgrade
        // E.g. The expected check method might change and then the Safe would be locked.
    }

    /**
      * @notice Governance can recover any token from the strategy.
     */
    function recoverToken(address token, address to, uint256 amount) external onlyElevatedAccess {
        emit CommonEventsAndErrors.TokenRecovered(to, token, amount);
        IERC20(token).safeTransfer(to, amount);
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 999999
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
