// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.0;

contract MATRIX {
    string public name = "MATRIX";
    string public symbol = "MTRX";
    uint256 public totalSupply = 500000000000000000000000000000;
    uint8 public decimals = 18;
    address public owner;
    uint256 public transactionFee = 490; // 4.9% transaction fee

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor() {
        balanceOf[msg.sender] = totalSupply;
        owner = msg.sender;
    }

    function transfer(address _to, uint256 _value) internal returns (bool success) {
        require(balanceOf[msg.sender] >= _value, "Not enough balance");

        uint256 feeAmount = msg.value * transactionFee / 10000; // Calculate transaction fee
        require(msg.value >= feeAmount, "Insufficient transaction cost");

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += (_value - feeAmount);

        payable(owner).transfer(feeAmount); // Pay transaction fee to contract owner

        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) internal returns (bool success) {
        require(balanceOf[_from] >= _value, "Not enough balance");
        require(allowance[_from][msg.sender] >= _value, "Not enough allowance");

        uint256 feeAmount = msg.value * transactionFee / 10000; // Calculate transaction fee
        require(msg.value >= feeAmount, "Insufficient transaction cost");

        balanceOf[_from] -= _value;
        balanceOf[_to] += (_value - feeAmount);

        payable(owner).transfer(feeAmount); // Pay transaction fee to contract owner

        allowance[_from][msg.sender] -= _value;
        emit Transfer(_from, _to, _value);
        return true;
    }
}
