    /**
    * @notice Returns the bias for a given voter for a given gauge, at a given period
    * @param gauge address of the gauge
    * @param voter address of the voter
    * @param period timestamp of the period
    * @return userBias (uint256) : voter bias
    */
    function _getVoterBias(address gauge, address voter, uint256 period) internal view returns(uint256 userBias) {
        IGaugeController gaugeController = IGaugeController(GAUGE_CONTROLLER);
        uint256 lastUserVote = gaugeController.last_user_vote(voter, gauge);
        IGaugeController.VotedSlope memory voteUserSlope = gaugeController.vote_user_slopes(voter, gauge);

        if(lastUserVote >= period) return 0;
        if(voteUserSlope.end <= period) return 0;
        if(voteUserSlope.slope == 0) return 0;

        userBias = voteUserSlope.slope * (voteUserSlope.end - period);
    }

    function calculateBiasPeriod(uint256 period, uint256 questID) external nonReentrant onlyAllowed {
        if(!validQuests[questID]) return;
        if(period > getCurrentPeriod()) revert Errors.IncorrectPeriod();

        QuestParams memory questParam = questParams[questID];

        uint256 adjustedBias = getReducedBias(period, questID, questParam.rootGauge, questParam.voteType);

        questAdjustedBiases[questID][period] = adjustedBias;

        bytes memory lzPayload = abi.encode(
            questID,
            period,
            adjustedBias
        );

        (uint256 msgFees,) = lzEndpoint.estimateFees(targetChain, address(this), lzPayload, useZro, adapterParams);
        _lzSend(targetChain, lzPayload, payable(address(this)), zroPaymentAddress, adapterParams, msgFees);

        emit BiasPeriodCalculated(questID, period, adjustedBias);
    }

    // Receive new Quest
    function _nonblockingLzReceive(
        uint16 _srcChainId,
        bytes memory, /*_srcAddress*/
        uint64, /*_nonce*/
        bytes memory _payload
    ) internal virtual override nonReentrant {
        (
            uint256 questID,
            address rootGauge,
            QuestDataTypes.QuestVoteType voteType,
            address[] memory voterList
        ) = abi.decode(_payload, (uint256, address, QuestDataTypes.QuestVoteType, address[]));

        questParams[questID] = QuestParams({rootGauge: rootGauge, voteType: voteType});
        questVoterList[questID] = voterList;
        validQuests[questID] = true;

        emit NewQuest(questID, _srcChainId, rootGauge);
    }
   
    /**
    * @notice Approves a new address as manager 
    * @dev Approves a new address as manager
    * @param newManager Address to add
    */
    function approveManager(address newManager) external onlyOwner {
        if(newManager == address(0)) revert Errors.AddressZero();
        approvedManagers[newManager] = true;

        emit ApprovedManager(newManager);
    }
   
    /**
    * @notice Removes an address from the managers
    * @dev Removes an address from the managers
    * @param manager Address to remove
    */
    function removeManager(address manager) external onlyOwner {
        if(manager == address(0)) revert Errors.AddressZero();
        approvedManagers[manager] = false;

        emit RemovedManager(manager);
    }

    function depositFees() external payable nonReentrant onlyOwner {
        uint256 amount = msg.value;

        emit DepositedFees(amount);
    }

    function updateTargetChain(uint16 _targetChain) external onlyOwner {
        targetChain = _targetChain;
    }

    function setUseZero(bool _useZro) external onlyOwner {
        useZro = _useZro;
        emit UseZeroUpdated(_useZro);
    }

    function setAdapterParams(bytes memory _adapterParams) external onlyOwner {
        adapterParams = _adapterParams;
        emit AdapterParamsUpdated(_adapterParams);
    }

    function setZeroPaymentAddress(address _paymentAddress) external onlyOwner {
        zroPaymentAddress = _paymentAddress;
        emit ZeroPaymentAddressUpdated(_paymentAddress);
    }

