      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

import "erc404/interfaces/IERC404.sol";


contract Minter is Ownable {
	using MerkleProof for bytes32[];

	IERC404 public token;

	bytes32 immutable root;

	bool private mintActive = false;

	uint256 private price_adj = 100;

	// Token mint details
	uint256 constant public PRICE_PER = 8 ether / 100;
	uint256 constant public MAX_MINT_PER = 10;
	mapping (address => uint256) public minted;

	// 404-state
	uint256 public constant supply = 10000;
	uint256 public constant decimals = 18;
	uint256 public constant units = 10 ** decimals;


	function mint(bytes32[] calldata proof, uint256 num) public payable {
		if (!mintActive)
			revert("Minting has not started yet.");
		if (msg.value != num * PRICE_PER)
			revert("Value != total mint price");
		if (num + minted[msg.sender] > MAX_MINT_PER)
			revert("Max minted for this address exceeded");

		minted[msg.sender] += num;
		_mintWhitelist(proof, num);
	}

	function _mintWhitelist(bytes32[] memory proof, uint256 num) internal {
		// Verify sender and their number of free mints
		require(
			proof.verify(
				root,
				keccak256(bytes.concat(keccak256(abi.encode(msg.sender))))
		), "Invalid whitelist proof!");

		/// ERC-404 transfer sends tokens and mints NFTs to sender
		token.transfer(msg.sender, num * units);
	}

	function mintStart() external onlyOwner {
		if (address(token) == address(0))
			revert ("Must set token before minting is enabled!");

		mintActive = true;
	}

	function mintStop() external onlyOwner {
		mintActive = false;
	}

	function ethWithdraw() external onlyOwner {
		payable(msg.sender).transfer(address(this).balance);
	}

	function setToken(address token_) external onlyOwner() {
		if (address(token) != address(0))
			revert("Token already set!");

        if (!IERC404(token_).supportsInterface(type(IERC404).interfaceId))
            revert("IERC404 interface support sanity check fail!");

		token = IERC404(token_);
	}

	function ownerWithdrawTokens(uint256 amount) external onlyOwner {
		token.transfer(msg.sender, amount);
	}

	constructor(bytes32 root_, address initialOwner_) Ownable(initialOwner_) {
		root = root_;
	}
}"
