// File: turkeys/turkeysMiddleMan.sol


pragma solidity ^0.8.0;


interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
}

interface IExternalContract {function addRevenue(uint256 amount) external;}

contract TurkeysMiddleman is Ownable {
    IERC20 public paymentToken = IERC20(0xA8b28269376a854Ce52B7238733cb257Dd3934e8);
    IExternalContract public externalContract = IExternalContract(0x49C59D51a3e0fA9df6c80F38Dda32b66E51b21c8);
    address public _externalContractAddress = 0x49C59D51a3e0fA9df6c80F38Dda32b66E51b21c8;
    address private _middleMan;

    function moveRevenue() external {
        uint256 myBalance = paymentToken.balanceOf(address(this));
        require(myBalance > 0, "No tokens to transfer");
        require(paymentToken.approve(_externalContractAddress, myBalance), "Approval failed");
        externalContract.addRevenue(myBalance);
    }

    function withdrawStuckEther() external onlyOwner {payable(owner()).transfer(address(this).balance);}
    function setMiddleman(address newMiddleMan) external onlyOwner {_middleMan = newMiddleMan;}
    function setReceiver(address _newReceiver) external onlyOwner {_externalContractAddress = _newReceiver;}
    function setExternalContractAddress(address _newAddress) public onlyOwner {externalContract = IExternalContract(_newAddress);}
    function setPaymentToken(address _newPaymentToken) public onlyOwner {paymentToken = IERC20(_newPaymentToken);}

    function withdrawStuckTokens(uint256 amount) external onlyOwner {
        require(paymentToken.balanceOf(address(this)) >= amount, "Insufficient token balance");
        paymentToken.transfer(owner(), amount);
    }
}
