    "contracts/MegacyNFTCollection.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/PullPayment.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";

import "./MegacyNFTFactory.sol";
import "./ERC721Tradable.sol";

// import "./interfaces/ICollectionContractInitializer.sol";
import "./interfaces/ICollectionFactory.sol";
// import "./interfaces/IGetRoyalties.sol";
// import "./interfaces/ITokenCreator.sol";
// import "./interfaces/IGetFees.sol";
// import "./interfaces/IRoyaltyInfo.sol";

import "./libraries/AccountMigrationLibrary.sol";
import "./libraries/ProxyCall.sol";
import "./libraries/BytesLibrary.sol";

contract MegacyNFTCollection is
    ERC721Tradable,
    PullPayment
{
    using AccountMigrationLibrary for address;
    using AddressUpgradeable for address;
    using BytesLibrary for bytes;
    using ProxyCall for IProxyCall;
    using Counters for Counters.Counter;

    uint256 private constant ROYALTY_IN_BASIS_POINTS = 1000;

    /**
     * We rely on the OZ Counter util to keep track of the next available ID.
     * We track the nextTokenId instead of the currentTokenId to save users on gas costs. 
     * Read more about it here: https://shiny.mirror.xyz/OUampBbIz9ebEicfGnQf5At_ReMHlZy0tB4glb9xQ0E
     */ 

    Counters.Counter private _nextTokenId;
    mapping(uint256 => string) _tokenURIs; /// @dev TokenURI for NFT metadata_updatable = false

    /**
     * @notice The factory which was used to create this collection.
     * @dev This is used to read common config.
     */
    ICollectionFactory public immutable collectionFactory;
    /**
     * @dev Stores an optional alternate address to receive creator revenue and royalty payments.
     * The target address may be a contract which could split or escrow payments.
     */
    mapping(uint256 => address payable) private tokenIdToCreatorPaymentAddress;

    address payable factoryAddress;

    event CollectionMinted(address _to, string _uri, uint256 _tokenId);
    event CollectionInitiated(address _contractOwner, string namecollection);
    event LogDepositReceived(address _from, uint _amount);

    // /**
    //  * @notice Called by the factory on creation.
    //  * @param _creator The creator of this collection contract.
    //  * @param _name The name of this collection.
    //  * @param _symbol The symbol for this collection.
    //  */
    // function initialize(
    //     address payable _creator,
    //     string calldata _name,
    //     string calldata _symbol
    // ) external initializer {
    //     require(msg.sender == address(collectionFactory), "CollectionContract: Collection must be created via the factory");
    //     // __ERC721_init_unchained(_name, _symbol);
    //     _transferOwnership(_creator);
    // }

    constructor(
        address _collectionFactory,
        string memory _name,
        string memory _symbol,
        string memory _contractURI,
        address _creator
    )
        ERC721Tradable(
            _name,
            _symbol,
            "https://vietrro.megacy.io/nft/metadata/"
        )
    {
        require(
            _collectionFactory.isContract(),
            "CollectionContract: collectionFactory is not a contract"
        );
        collectionFactory = ICollectionFactory(_collectionFactory);
        setBaseContractURI(_contractURI);
        factoryAddress = payable(_collectionFactory);
        _transferOwnership(_creator);
        // nextTokenId is initialized to 1, since starting at 0 leads to higher gas cost for the first minter
        _nextTokenId.increment();
        emit CollectionInitiated(owner(), _name);
    }

    /**
    @notice Function used to receive ether
    @dev  Emits "LogDepositReceived" event | Ether send to this contract for
    no reason will be credited to the contract owner, and the deposit logged,
    */
    receive() external payable {
        payable(owner()).transfer(msg.value);
        emit LogDepositReceived(msg.sender, msg.value);
    }

    function _setTokenURI(uint256 tokenId, string memory _tokenURI) internal {
        _tokenURIs[tokenId] = _tokenURI;
    }

    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
        require(_exists(_tokenId));

        string memory _tokenURI = _tokenURIs[_tokenId];
        if (bytes(_tokenURI).length > 0) {
            return _tokenURI;
        }
        return string(abi.encodePacked(_baseURI(), Strings.toHexString(address(this)), "/", Strings.toString(_tokenId)));
    }

    function mintTo(address _to) public virtual onlyOwner returns (uint256) {
        uint256 currentTokenId = _nextTokenId.current();
        string memory _uri = string(abi.encodePacked(_baseURI(), Strings.toHexString(address(this)), "/", Strings.toString(currentTokenId)));
        return mintToWithURI(_to, _uri);
    }

    function mintToWithURI(
        address _to,
        string memory _uri
    ) public virtual onlyOwner returns (uint256) {
        uint256 currentTokenId = _nextTokenId.current();
        _safeMint(_to, currentTokenId);
        _setTokenURI(currentTokenId, _uri);
        _nextTokenId.increment();
        MegacyNFTFactory(factoryAddress).recordMint(
            address(this),
            _to,
            currentTokenId
        );
        emit CollectionMinted(_to, _uri, currentTokenId);
        return currentTokenId;
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override(ERC721) {
        MegacyNFTFactory(factoryAddress).recordTransfer(
            address(this),
            ownerOf(tokenId),
            to,
            tokenId
        );
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public virtual override(ERC721) {
        MegacyNFTFactory(factoryAddress).recordTransfer(
            address(this),
            ownerOf(tokenId),
            to,
            tokenId
        );
        super.safeTransferFrom(from, to, tokenId);
    }

    /**
    @notice Function used to withdraw contract funds
    */
    function withdraw() external payable onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    /**
     * Override isApprovedForAll to auto-approve OS's proxy contract
     */
    function isApprovedForAll(
        address _owner,
        address _operator
    ) public view override(ERC721Tradable) returns (bool isOperator) {
        // if OpenSea's ERC721 Proxy Address is detected, auto-return true
        // for Polygon's Mumbai testnet, use 0xff7Ca10aF37178BdD056628eF42fD7F799fAc77c 0x58807baD0B376efc12F5AD86aAc70E78ed67deaE
        if (_operator == address(0x58807baD0B376efc12F5AD86aAc70E78ed67deaE)) {
            return true;
        }
        // if OpenSea's ERC1155 Proxy Address is detected, auto-return true
        // for Polygon's Mumbai testnet, use 0x53d791f18155C211FF8b58671d0f7E9b50E596ad 0x207Fa8Df3a17D96Ca7EA4f2893fcdCb78a304101
        if (_operator == address(0x207Fa8Df3a17D96Ca7EA4f2893fcdCb78a304101)) {
            return true;
        }
        // otherwise, use the default ERC721.isApprovedForAll()
        return super.isApprovedForAll(_owner, _operator); //ERC721Tradable
    }

    /**
     * @notice Returns an array of recipient addresses to which royalties for secondary sales should be sent.
     * The expected royalty amount is communicated with `getFeeBps`.
     * param tokenId The tokenId of the NFT to get the royalty recipients for.
     * @return recipients An array of addresses to which royalties should be sent.
     */
    function getFeeRecipients(
        uint256
    ) external view returns (address payable[] memory recipients) {
        recipients = new address payable[](1);
        recipients[0] = payable(owner());
    }

    /**
     * @notice Returns an array of royalties to be sent for secondary sales in basis points.
     * The expected recipients is communicated with `getFeeRecipients`.
     * @dev The tokenId param is ignored since all NFTs return the same value.
     * @return feesInBasisPoints The array of fees to be sent to each recipient, in basis points.
     */
    function getFeeBps(
        uint256 /* tokenId */
    ) external pure returns (uint256[] memory feesInBasisPoints) {
        feesInBasisPoints = new uint256[](1);
        feesInBasisPoints[0] = ROYALTY_IN_BASIS_POINTS;
    }

    /**
        @dev Returns the total tokens minted so far.
        1 is always subtracted from the Counter since it tracks the next available tokenId.
     */
    function totalSupply() public view returns (uint256) {
        return _nextTokenId.current() - 1;
    }
}"
    },
    "contracts/MegacyNFTFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
// import "@openzeppelin/contracts/utils/Create2.sol";
// import "@openzeppelin/contracts/access/Ownable.sol";

import "./MegacyNFTCollection.sol";
import "./TokenOwnershipRegister.sol";

import "./interfaces/ICollectionContractInitializer.sol";
import "./interfaces/ICollectionFactory.sol";
import "./interfaces/IProxyCall.sol";
import "./interfaces/IRoles.sol";

contract MegacyNFTFactory is ICollectionFactory, Ownable {
  using AddressUpgradeable for address;
  using AddressUpgradeable for address payable;
  using Clones for address;
  using Strings for uint256;

    address tokenOwnershipRegisterAddress;

    event CollectionDeployed(address _creatorAddress, address _contractAddress);

    /**
     * @notice The contract address which manages common roles.
     * @dev Used by the collections for a shared operator definition.
     */
    IRoles public rolesContract;

    /**
     * @notice The address of the template all new collections will leverage.
     */
    address public implementation;

    /**
     * @notice The address of the proxy call contract implementation.
     * @dev Used by the collections to safely call another contract with arbitrary call data.
     */
    IProxyCall public proxyCallContract;

    /**
     * @notice The implementation version new collections will use.
     * @dev This is auto-incremented each time the implementation is changed.
     */
    uint256 public version;

    modifier onlyAdmin() {
        require(rolesContract.isAdmin(msg.sender), "Caller does not have the Admin role");
        _;
    }

    modifier onlyContract() {
        require(rolesContract.isAdmin(msg.sender), "Caller does not have the Admin role");
        _;
    }

    constructor(address _ownershipRegisterAddress) {
        tokenOwnershipRegisterAddress = _ownershipRegisterAddress;
    }

    /**
     * @notice Defines requirements for the collection factory at deployment time.
     * @param _proxyCallContract The address of the proxy call contract implementation.
     * @param _rolesContract The address of the contract defining roles for collections to use.
     */
    // constructor(address _proxyCallContract, address _rolesContract) {
    //     _updateRolesContract(_rolesContract);
    //     _updateProxyCallContract(_proxyCallContract);
    // }

    /**
     * @notice Allows Musee to change the collection implementation used for future collections.
     * This call will auto-increment the version.
     * Existing collections are not impacted.
     * @param _implementation The new collection implementation address.
     */
    function adminUpdateImplementation(address _implementation) external onlyAdmin {
        _updateImplementation(_implementation);
    }

    /**
     * @notice Allows Musee to change the proxy call contract address.
     * @param _proxyCallContract The new proxy call contract address.
     */
    function adminUpdateProxyCallContract(address _proxyCallContract) external onlyAdmin {
        _updateProxyCallContract(_proxyCallContract);
    }

    /**
     * @notice Allows Musee to change the admin role contract address.
     * @param _rolesContract The new admin role contract address.
     */
    function adminUpdateRolesContract(address _rolesContract) external onlyAdmin {
        _updateRolesContract(_rolesContract);
    }

    /**
    @notice Function used to receive ether
    @dev  Emits "LogDepositReceived" event | Ether send to this contract for
    no reason will be credited to the contract owner, and the deposit logged,
    */
    receive() external payable{
        payable (owner()).transfer(msg.value);
    }

    /**
    @notice Factory function used to create a NFT collection
    @dev Emits "CollectionDeployed" event
    */
    function createBlankNftCollection(string calldata _collectionName, string calldata _collectionSymbol, string calldata _collectionUri) external returns (address collectionAddress) {
        MegacyNFTCollection newCollect = new MegacyNFTCollection(address(this), _collectionName, _collectionSymbol, _collectionUri, msg.sender);
        collectionAddress = address(newCollect);
        TokenOwnershipRegister(tokenOwnershipRegisterAddress).registerCollection(collectionAddress);
        emit CollectionDeployed(msg.sender, collectionAddress);
    }

    function recordMint(address _collection, address _owner, uint _tokenId) external {
        TokenOwnershipRegister(tokenOwnershipRegisterAddress).recordMint(_collection, _owner, _tokenId);
    }

    function recordTransfer(address _collection, address _from, address _to, uint _tokenId) external {
        TokenOwnershipRegister(tokenOwnershipRegisterAddress).recordTransfer(_collection, _from, _to, _tokenId);
    }

    function _updateRolesContract(address _rolesContract) private {
        require(_rolesContract.isContract(), "not a contract");
        rolesContract = IRoles(_rolesContract);
        // emit RolesContractUpdated(_rolesContract);
    }

    /**
     * @notice Returns the address of a collection given the current implementation version, creator, and nonce.
     * This will return the same address whether the collection has already been created or not.
     * @param creator The creator of the collection.
     * @param nonce An arbitrary value used to allow a creator to mint multiple collections.
     * @return collectionAddress The address of the collection contract that would be created by this nonce.
     */
    function predictCollectionAddress(address creator, uint256 nonce) external view returns (address collectionAddress) {
        collectionAddress = implementation.predictDeterministicAddress(_getSalt(creator, nonce));
    }

    function _getSalt(address creator, uint256 nonce) private pure returns (bytes32) {
        return keccak256(abi.encodePacked(creator, nonce));
    }

    /**
     * @dev Updates the implementation address, increments the version, and initializes the template.
     * Since the template is initialized when set, implementations cannot be re-used.
     * To downgrade the implementation, deploy the same bytecode again and then update to that.
     */
    function _updateImplementation(address _implementation) private {
        require(_implementation.isContract(), "not a contract");
        implementation = _implementation;
        unchecked {
        // Version cannot overflow 256 bits.
        version++;
        }
        // The implementation is initialized when assigned so that others may not claim it as their own.
        ICollectionContractInitializer(_implementation).initialize(
            payable(address(rolesContract)),
            string(abi.encodePacked("MCY Collection Template v", version.toString())),
            string(abi.encodePacked("FCTv", version.toString()))
            );
        // emit ImplementationUpdated(_implementation, version);
    }

    function _updateProxyCallContract(address _proxyCallContract) private {
        require(_proxyCallContract.isContract(), "not a contract");
        proxyCallContract = IProxyCall(_proxyCallContract);
    }
}"
    },
    "contracts/TokenOwnershipRegister.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;


import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenOwnershipRegister is Ownable {
    mapping(address => bool) deployedCollections;
    mapping(address => mapping(address => uint256)) public userToCollectionToBalance;
    mapping(address => mapping(address => uint256[])) public userToCollectionToTokens;
    address factoryAddress;

    modifier onlyFactory() {
        require(msg.sender == factoryAddress, "Factory only");
        _;
    }

    modifier onlyRegisteredCollections(address sender) {
        require(deployedCollections[sender], "Registered collections only");
        _;
    }

    function setFactoryAddress(address _factoryAddress) external onlyOwner {
        factoryAddress = _factoryAddress;
    }


    function registerCollection(address _newCollection) external onlyFactory {
        deployedCollections[_newCollection] = true;
    }

    function recordMint(address _collection, address _owner, uint _tokenId) external onlyRegisteredCollections(_collection) {
        userToCollectionToBalance[_owner][_collection]+= 1;
        userToCollectionToTokens[_owner][_collection].push(_tokenId);
    }

    function recordTransfer(address _collection, address _from, address _to, uint _tokenId) external onlyRegisteredCollections(_collection) {
        uint[] storage tokens = userToCollectionToTokens[_from][_collection];
        uint balance = userToCollectionToBalance[_from][_collection];
        for(uint i = 0; i < balance; i++) {
            if(tokens[i] == _tokenId) {
                uint lastIndex = balance - 1;
                uint lastIndexValue = tokens[lastIndex];
                tokens[i] = lastIndexValue;
                // tokens.pop();
                delete tokens[lastIndex];
            }
        }
        userToCollectionToTokens[_to][_collection].push(_tokenId);
        userToCollectionToBalance[_from][_collection]-= 1;
        userToCollectionToBalance[_to][_collection]+= 1;

    }
}"
    }
  },
  "settings": {
    "viaIR": true,
    "optimizer": {
      "enabled": true,
      "runs": 200,
      "details": {
        "yul": true
      }
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
