contract PreSeedManager is Ownable {
    IERC20 public token;
    address public vestingContract;
    bool public distributed = false;

    address[] public recipients = [
        0x93B924bE106Ad70f68a1B1798C8c4bB9DEeF5117,
        0xeAA2151b6e13430D39408600fce01627538d3966,
        0x452b4259C704B853eF50596FA5862CAbC7Dba1e6,
        0x4F2513B9dfE86f09A1A907DE50A4A6bbB365E9A3,
        0xa3a463B30D84184E8F6d8E19739FDED9d4bC8500,
        0xF1D8F67b4cfcE0B0B4bE66f81E27dd30BAdc023d,
        0x0c08c2568eB054b853959DCBD217e46Ff5ECC773,
        0x985200C74a897f5293E127aa97a4444775E77C8A
    ];
    
    uint256[] public vestingAmounts = [
        950000 * 10 ** 18,
        665000 * 10 ** 18,
        940500 * 10 ** 18, 
        509437.5 * 10 ** 18,
        509437.5 * 10 ** 18,
        509437.5 * 10 ** 18,
        509437.5 * 10 ** 18,
        156750 * 10 ** 18
    ];

    constructor(address _tokenAddress, address _vestingContract) Ownable(msg.sender) {
        token = IERC20(_tokenAddress);
        vestingContract = _vestingContract;
    }

    function distributeVestedTokens() public onlyOwner {
        for (uint256 i = 0; i < recipients.length; i++) {
            require(token.transfer(recipients[i], vestingAmounts[i]), "Token transfer failed");
        }
    }

    function setVestingContract(address _vestingContract) external onlyOwner {
        vestingContract = _vestingContract;
    }
}
