    "sto/token/STOTokenUpgradeable.sol": {
      "content": "/// SPDX-License-Identifier: MIT
pragma solidity <0.9.0;

import { STOTokenConfiscateUpgradeable, Errors } from "./STOTokenConfiscateUpgradeable.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable@4.9.3/proxy/utils/Initializable.sol";
import { AddressUpgradeable } from "@openzeppelin/contracts-upgradeable@4.9.3/utils/AddressUpgradeable.sol";
import { STOTokenCheckpointsUpgradeable, ERC20BurnableUpgradeable, ERC20Upgradeable } from "./STOTokenCheckpointsUpgradeable.sol";

/// @title STOTokenUpgradeable ERC20 Template for STO/Equity
/// @custom:security-contact tech@brickken.com
contract STOTokenUpgradeable is Initializable, STOTokenConfiscateUpgradeable {
    string public url;
    uint224 public supplyCap; // This is unrelated to the _maxSupply of the checkpoints feature. This is optional, while the other is mandatory to be respected at any point.
    address public issuer;
    address internal minter_OLD_SLOT; // keep for back compatibility. Ignore.
    uint256 public totalHolders;

    /// @dev Mapping to store whether an address is already a holder or not
    mapping(address user => bool isHolder) public holders;

    /// @dev Mapping storing whether addresses are whitelisted or not
    mapping(address user => bool whitelistStatus) public whitelist_OLD_SLOT; // keep for back compatibility. Ignore.

    /// @dev Event to signal that the issuer changed
    /// @param issuer New issuer address
    event ChangeIssuer(address indexed issuer);

    /// @dev Event to signal that the minter changed
    /// @param newMinter New minter address
    event ChangeMinter(address indexed newMinter);

    /// @dev Event to signal that the url changed
    /// @param newURL New url
    event ChangeURL(string newURL);

    /// @dev Event to signal that the max supply changed
    /// @param newSupplyCap New max supply
    event ChangeSupplyCap(uint224 newSupplyCap);

    /// @dev Event emitted when any group of wallet is added or remove to the whitelist
    /// @param addresses array of addresses of the wallets changed in the whitelist
    /// @param statuses array of boolean status to define if add or remove the wallet to the whitelist
    /// @param account address of the account with the TOKEN_WHITELIST_ADMIN_ROLE role
    event ChangeWhitelist(address[] addresses, bool[] statuses, address account);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // INTERNAL / PRIVATE FUNCTIONS

    function __STOTokenUpgradeable_init(
        string calldata newName,
        string calldata newSymbol,
        address newPaymentToken
    ) internal {

        __STOTokenConfiscate_init(
            newPaymentToken,
            newName,
            newSymbol
        );
    }

    /// @dev Method to setup or update the max supply of STOToken
    /// @param newSupplyCap the new max supply to set
    function _changeSupplyCap(uint224 newSupplyCap) internal {
        if (totalSupply() > newSupplyCap && newSupplyCap != 0) revert Errors.SupplyCapExceeded();
        supplyCap = newSupplyCap;
    }

    /// @dev Method to setup or update the IPFS URI where the all documents of the tokenization are stored
    /// @param newURL the new URI to be set
    function _changeUrl(string memory newURL) internal {
        url = newURL;
    }

    /**
     * @dev Returns the highest version that has been initialized. See {reinitializer}.
    */
    function version() external view returns(uint8) {
        return _getInitializedVersion();
    }

    /// Method to mint tokens directly
    /// @param _to the address to who the tokens should be minted
    /// @param _amount the amount of tokens to be minted
    function _mintTokens(address _to, uint256 _amount) internal {
        if (supplyCap > 0 && totalSupply() + _amount > supplyCap)
            revert Errors.SupplyCapExceeded();
        _mint(_to, _amount); // Whether the amount exceeds type(uint224).max is checked internally
    }

    /// @dev Hook that is called after any transfer of tokens. This includes minting and burning.
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(STOTokenCheckpointsUpgradeable) {
        uint256 balanceFrom = balanceOf(from);

        // If it's minting, the from is address(0) so no need to account for that
        // If it's burning, the balanceFrom will account if now the original user has no tokens
        if(from != address(0) && balanceFrom == 0 && amount > 0) {
            totalHolders = totalHolders - 1;
            holders[from] = false;
        }

        // If it's minting, the `to` is checked to already exist as holder or not
        // If it's burning, the `to` is the zero address so no need to account for it
        if (amount > 0 && !holders[to] && to != address(0)) {
            totalHolders = totalHolders + 1;
            holders[to] = true;
        }

        super._afterTokenTransfer(from, to, amount);
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[43] private __gap;
}
"
    },
    "sto/helpers/Errors.sol": {
      "content": "/// SPDX-License-Identifier: MIT
pragma solidity <0.9.0;

/// @title Errors
/// @custom:security-contact tech@brickken.com
library Errors {
    /// If a contract has been alredy initialized
    error AlreadyInitialized();

    /// Issuer `issuer` can't start a new Issuance Process if the Previous one has not been Finalized and Withdrawn
    error IssuanceNotFinalized(address issuer);

    /// Issuance start date has not been reached 
    error IssuanceNotStarted(address issuer);

    /// Issuance soft cap has not been reached
    error IssuanceNotSuccess(address issuer);

    /// The Initialization of the Issuance Process sent by the Issuer `issuer` is not valid
    error InitialValueWrong(address issuer);

    /// This transaction exceed the Max Supply of STO Token
    error MaxSupplyExceeded();

    /// This transaction exceed the Supply Cap of STO Token
    error SupplyCapExceeded();

    /// The issuance collected funds are not withdrawn yet
    error IssuanceNotWithdrawn(address issuer);

    /// The issuance process is not in rollback state
    error IssuanceNotInRollback(uint256 index);

    /// Fired when fees are over 100%
    error FeeOverLimits(uint256 newFee);

    /// The Issuer `issuer` tried to Finalize the Issuance Process before to End Date `endDate`
    error IssuanceNotEnded(address issuer, uint256 endDate);

    /// The Issuer `issuer` tried to Withdraw the Issuance Process was Withdrawn
    error IssuanceWasWithdrawn(address issuer);

    /// The Issuer `issuer` tried to Rollback the Issuance Process was Rollbacked
    error IssuanceWasRollbacked(address issuer);

    /// The User `user` tried to buy STO Token in the Issuance Process was ended in `endDate`
    error IssuanceEnded(address user, uint256 endDate);

    /// The User `user` tried to buy with ERC20 `token` is not WhiteListed in the Issuance Process
    error TokenIsNotWhitelisted(address token, address user);

    /// The User `user` tried to buy STO Token, and the Amount `amount` exceed the Maximal Ticket `maxTicket`
    error AmountExceeded(address user, uint256 amount, uint256 maxTicket);

	/// the User `user` tried to buy STO Token, and the Amount `amount` is under the Minimal Ticket `minTicket`
	error InsufficientAmount(address user, uint256 amount, uint256 minTicket);

    /// The value is negative
    error NotNegativeValue(int256 value);

    /// When something is wrong with Uniswap config
    error WrongUniswapConfig();

    /// Slippage control
    error LessThanExpectedAmount(uint256 minExpected, uint256 amount);

    /// The User already redeemed the tokens bought in previous investments
    error TokensAlreadyReedemed(address user);

    /// The User `user` has not enough balance `amount` in the ERC20 Token `token`
    error InsufficientBalance(address user, address token, uint256 amount);

    /// The User `user` tried to redeem the ERC20 Token Again! in the Issuance Process with Index `index`
    error RedeemedAlready(address user, uint256 index);

    /// The User `user` tried to be refunded with payment tokend Again! in the Issuance Process with Index `index`
    error RefundedAlready(address user, uint256 index);

    /// The User `user` is not Investor in the Issuance Process with Index `index`
    error NotInvestor(address user, uint256 index);

    /// The Max Amount of STO Token in the Issuance Process will be Raised
    error HardCapRaised();

    /// User `user`,don't have permission to reinitialize the contract
    error UserIsNotAdmin(address user);

    /// User is not Whitelisted, User `user`,don't have permission to transfer or call some functions
    error UserIsNotWhitelisted(address user);

	/// At least pair of arrays have a different length
    error LengthsMismatch();

	/// The premint Amount of STO Tokens in the Issuance Process exceeds the Max Amount of STO Tokens
    error PremintGreaterThanMaxSupply();

	/// The Address can't be zero address
	error NotZeroAddress();

	/// The Address is not a Contract
	error NotContractAddress();

	/// The Dividend Amount can't be zero
	error DividendAmountIsZero();

	/// The Wallet `claimer` is not Available to Claim Dividend
	error NotAvailableToClaim(address claimer);

	/// The User `claimer` can't claim
	error NotAmountToClaim(address claimer);

	///The User `user` try to claim an amount `amountToClaim` more than the amount available `amountAvailable`
	error ExceedAmountAvailable(address claimer, uint256 amountAvailable, uint256 amountToClaim);

    /// Confiscation Feature is Disabled
    error ConfiscationDisabled();

    // The token is not the payment token
	error InvalidPaymentToken(address token);

    // The deadline for the offchain price has expired
    error ExpiredSignature(uint256 deadline, uint256 currentTimestamp);

    // The retrieved address from ECDSA signature doesn't match allowed signed
    error InvalidSigner(address retrievedAddress, address validAddress);

    // Bad twap interval
    error BadTwapIntervalValue(uint256 value);

    // Null value not accepted
    error NotZeroValue();
}
"
    },
    "sto/helpers/Roles.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity <0.9.0;

library Roles {

    // DEFAULT_ADMIN_ROLE = grant/revoke roles (brickken)
    //
    // This role operated by brickken can grant/revoke any role in the system. Specifically it
    // can also perform some special actions. Brickken operates through Gnosis Safe multisig for every
    // operation.
    //
    // On the escrow:
    //
    // - change issuer, if issuer wallet gets compromised
    // - change payment token, if issuer desires to change the asset used in the offering
    // - change router, if unexpected circumstances forces us to change Uniswap v3 router
    // - change price and swap manager, if unexpected circumstances forces us to change the contract logic
    // - change treasury address, if brickken wants to change where success fees are sent
    // 
    // On the token:
    //
    // - change issuer, if issuer wallet gets compromised
    // - change payment token, if issuer desires to change the asset used to distribute dividends 
    // - change max supply, if there are extensions in the STO equities amount, this will conflict if max supply has been already minted
    // 
    // On the factory:
    //
    // Brickken is the owner and sole operator of the factory so it has full permissions on it

    bytes32 public constant FACTORY_PAUSER_ROLE = keccak256(abi.encode("FACTORY_PAUSER_ROLE")); // pause / unpause factory (brickken);
    bytes32 public constant FACTORY_ISSUER_ROLE = keccak256(abi.encode("FACTORY_ISSUER_ROLE")); // whitelisted issuers (brickken by default);
    bytes32 public constant FACTORY_WHITELISTER_ROLE = keccak256(abi.encode("FACTORY_WHITELISTER_ROLE")); // can whitelist new issuers (brickken);

    bytes32 public constant ESCROW_WITHDRAW_ROLE = keccak256(abi.encode("ESCROW_WITHDRAW_ROLE")); // who can withdraw / partially withdraw to issuer (issuer);
    bytes32 public constant ESCROW_NEW_OFFERING_ROLE = keccak256(abi.encode("ESCROW_NEW_OFFERING_ROLE")); // starts a new offering (issuer);
    bytes32 public constant ESCROW_ERC20WHITELIST_ROLE = keccak256(abi.encode("ESCROW_ERC20WHITELIST_ROLE")); // add/remove ERC20 from whitelist (brickken, issuer);
    bytes32 public constant ESCROW_OFFCHAIN_REPORTER_ROLE = keccak256(abi.encode("ESCROW_OFFCHAIN_REPORTER_ROLE")); // report offchain tickets (issuer);
    bytes32 public constant ESCROW_OFFERING_FINALIZER_ROLE = keccak256(abi.encode("ESCROW_OFFERING_FINALIZER_ROLE")); // finalize an offering (brickken, issuer);

    bytes32 public constant TOKEN_MINTER_ADMIN_ROLE = keccak256(abi.encode("TOKEN_MINTER_ADMIN_ROLE")); // add/remove minters (issuer);
    bytes32 public constant TOKEN_WHITELIST_ADMIN_ROLE = keccak256(abi.encode("TOKEN_WHITELIST_ADMIN_ROLE")); // change investors whitelist (issuer);
    bytes32 public constant TOKEN_CONFISCATE_ADMIN_ROLE = keccak256(abi.encode("TOKEN_CONFISCATE_ADMIN_ROLE")); // pause / unpause or disable confiscation (brickken);

    bytes32 public constant TOKEN_URL_ROLE = keccak256(abi.encode("TOKEN_URL_ROLE")); // change url (brickken,issuer);
    bytes32 public constant TOKEN_MINTER_ROLE = keccak256(abi.encode("TOKEN_MINTER_ROLE")); // mint new tokens (issuer, escrow contract);
    bytes32 public constant TOKEN_WHITELIST_ROLE = keccak256(abi.encode("TOKEN_WHITELIST_ROLE")); // whether the user is whitelisted or not (issuer);
    bytes32 public constant TOKEN_CONFISCATE_EXECUTOR_ROLE = keccak256(abi.encode("TOKEN_CONFISCATE_EXECUTOR_ROLE")); // execute confiscation (brickken);
    bytes32 public constant TOKEN_DIVIDEND_DISTRIBUTOR_ROLE = keccak256(abi.encode("TOKEN_DIVIDEND_DISTRIBUTOR_ROLE")); // distribute dividends (issuer);
}"
