    "contracts/bulkers/ApexLayerBulker.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.15;

import {BaseBulker} from "./BaseBulker.sol";
import {IUniswapV3SwapCallback, IUniswapV3SwapPool, IUniswapV3FlashCallback} from "../pools/UniswapV3.sol";
import {WETH9} from "../vendor/canonical-weth/contracts/WETH9.sol";
import {IPufferVault} from "../vendor/@puffer/IPufferVault.sol";
import {CometInterface} from "../CometInterface.sol";
import {ERC20} from "../ERC20.sol";
import "hardhat/console.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title Compound's Bulker contract for Ethereum mainnet
 * @notice Executes multiple Comet-related actions in a single transaction
 * @author Compound
 */
contract ApexLayerBulker is BaseBulker, IUniswapV3SwapCallback, ReentrancyGuard {
  uint160 internal constant MIN_SQRT_RATIO = 4295128739;
  uint160 internal constant MAX_SQRT_RATIO = 1461446703485210103287273052203988822378723970342;

  bytes32 public constant ACTION_OPEN_POSITION = "ACTION_OPEN_POSITION";
  bytes32 public constant ACTION_CLOSE_POSITION = "ACTION_CLOSE_POSITION";

  struct FlashSwapParams {
    bool isOpenPosition;
    CometInterface comet;
    address user;
    address asset;
    uint256 userDepositAmount;
    uint256 flashloanAmount;
    uint256 maxAmountRepay;
  }

  mapping(address => IUniswapV3SwapPool) uniswapV3;

  /**
   * @notice Construct a new MainnetBulker instance
   * @param admin_ The admin of the Bulker contract
   * @param weth_ The address of wrapped ETH
   **/
  constructor(address admin_, address payable weth_) BaseBulker(admin_, weth_) {
    uniswapV3[0xD9A442856C234a39a81a089C06451EBAa4306a72] = IUniswapV3SwapPool(
      0xBDB04e915B94FbFD6e8552ff7860E59Db7d4499a
    ); // pufETH -> pufETH/WETH

    uniswapV3[0xbf5495Efe5DB9ce00f80364C8B423567e58d2110] = IUniswapV3SwapPool(
      0xBE80225f09645f172B079394312220637C440A63
    ); // ezETH -> ezETH/WETH

    uniswapV3[0xCd5fE23C85820F7B72D0926FC9b05b43E359b7ee] = IUniswapV3SwapPool(
      0x7A415B19932c0105c82FDB6b720bb01B0CC2CAe3
    ); // weETH -> weETH/WETH

    uniswapV3[0xA1290d69c65A6Fe4DF752f95823fae25cB99e5A7] = IUniswapV3SwapPool(
      0x059615EBf32C946aaab3D44491f78e4F8e97e1D3
    ); // rsETH -> rsETH/WETH
  }

  function handleAction(bytes32 action, bytes calldata data) internal override {
    if (action == ACTION_OPEN_POSITION) {
      (
        CometInterface comet,
        address asset,
        uint256 userDepositAmount,
        uint256 flashloanAmount,
        uint256 maxAmountRepay
      ) = abi.decode(data, (CometInterface, address, uint256, uint256, uint256));
      createPosition(comet, asset, userDepositAmount, flashloanAmount, maxAmountRepay);
    } else if (action == ACTION_CLOSE_POSITION) {
      (CometInterface comet, address asset, uint256 maxAmountRepay) = abi.decode(
        data,
        (CometInterface, address, uint256)
      );
      closePosition(comet, asset, maxAmountRepay);
    } else {
      revert UnhandledAction();
    }
  }

  function createPosition(
    CometInterface comet,
    address asset,
    uint256 userDepositAmount,
    uint256 flashloanAmount,
    uint256 maxAmountRepay
  ) internal nonReentrant {
    if (address(uniswapV3[asset]) != address(0)) {
      bool baseTokenZero = wrappedNativeToken == uniswapV3[asset].token0();
      FlashSwapParams memory callbackParams = FlashSwapParams({
        isOpenPosition: true,
        comet: comet,
        user: msg.sender,
        asset: asset,
        userDepositAmount: userDepositAmount,
        flashloanAmount: flashloanAmount,
        maxAmountRepay: maxAmountRepay
      });
      bytes memory data = abi.encode(callbackParams);
      uniswapV3[asset].swap(
        address(this),
        baseTokenZero,
        -int(callbackParams.flashloanAmount),
        baseTokenZero ? MIN_SQRT_RATIO + 1 : MAX_SQRT_RATIO - 1,
        data
      );
    }
  }

  function closePosition(CometInterface comet, address asset, uint256 maxAmountRepay) internal nonReentrant {
    if (address(uniswapV3[asset]) != address(0)) {
      uint256 borrowBalance = comet.borrowBalanceOf(msg.sender);
      if (borrowBalance == 0) {
        uint256 collateralBalance = comet.collateralBalanceOf(msg.sender, asset);
        comet.withdrawFrom(msg.sender, msg.sender, asset, collateralBalance);
      } else {
        bool zeroForOne = wrappedNativeToken == uniswapV3[asset].token1();
        FlashSwapParams memory callbackParams = FlashSwapParams({
          isOpenPosition: false,
          comet: comet,
          user: msg.sender,
          asset: asset,
          userDepositAmount: 0,
          flashloanAmount: borrowBalance,
          maxAmountRepay: maxAmountRepay
        });
        bytes memory data = abi.encode(callbackParams);
        uniswapV3[asset].swap(
          address(this),
          zeroForOne,
          -int(borrowBalance),
          zeroForOne ? MIN_SQRT_RATIO + 1 : MAX_SQRT_RATIO - 1,
          data
        );
      }
    }
  }

  function uniswapV3SwapCallback(int256 amount0Delta, int256 amount1Delta, bytes calldata data) external {
    FlashSwapParams memory decoded = abi.decode(data, (FlashSwapParams));
    require(msg.sender == address(uniswapV3[decoded.asset]), "flashloan from pool only.");
    uint256 repayAmount = amount0Delta > 0 ? uint(amount0Delta) : uint(amount1Delta);
    if (decoded.isOpenPosition) {
      doTransferIn(decoded.asset, decoded.user, decoded.userDepositAmount);
      uint256 depositAmount = decoded.flashloanAmount + decoded.userDepositAmount;
      ERC20(decoded.asset).approve(address(decoded.comet), depositAmount);
      decoded.comet.supplyTo(decoded.user, decoded.asset, depositAmount);
      require(repayAmount <= decoded.maxAmountRepay, "amount repay too high.");
      decoded.comet.withdrawFrom(decoded.user, msg.sender, decoded.comet.baseToken(), repayAmount);
    } else {
      ERC20(decoded.comet.baseToken()).approve(address(decoded.comet), decoded.flashloanAmount);
      decoded.comet.supplyTo(decoded.user, decoded.comet.baseToken(), decoded.flashloanAmount);
      uint256 collateralBalance = decoded.comet.collateralBalanceOf(decoded.user, decoded.asset);
      decoded.comet.withdrawFrom(decoded.user, address(this), decoded.asset, collateralBalance);
      require(repayAmount <= decoded.maxAmountRepay, "amount repay too high.");
      doTransferOut(decoded.asset, msg.sender, repayAmount);
      doTransferOut(decoded.asset, decoded.user, collateralBalance - repayAmount);
    }
  }
}
"
