pragma solidity ^0.8.0;


import "hardhat/console.sol";
import {IDelegateRegistry} from "./IDelegateRegistry.sol";


import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";


/**
* @dev LeCryptoPunks is a partial Interface of the official CryptoPunks
       contract, which will be needed to verify punk-ownership. 
*/
interface LeCryptoPunks {
   //  function balanceOf(address account) external view returns(uint256);
   function punkIndexToAddress(uint256 punkIndex) external view returns(address);
}


interface TheCIGsContract {
   // The set of standard ERC20 functions
   function totalSupply() external view returns (uint256);
   function balanceOf(address account) external view returns (uint256);
   function transfer(address recipient, uint256 amount) external returns (bool);
   function allowance(address owner, address spender) external view returns (uint256);
   function approve(address spender, uint256 amount) external returns (bool);
   function transferFrom(address sender, address recipient,uint256 amount) external returns (bool);

   // Custom function to get the address of the CEO:
   function The_CEO() external view returns (address);
}



contract PunkInvaders is ERC721, Pausable, Ownable, ReentrancyGuard {
   using Strings for uint256;
   using SafeERC20 for IERC20;


   struct InvaderStructObject {
      address ownerAddress;
      uint256 cryptopunkIDNumber;
      string  punkInvaderInGameName;
      string  ownerSocMediaHandle;
      string  ownerBioLink;
      string  tippingCurrency;
      uint    amountTipped;
   }


   // ***** MAPPING *****
   //
   // Collection/Dictionary object that uses standard KEY-VALUE scheme to store all minted INVADER-NFTs.
   //    KEY:   tokenID
   //    VALUE: InvaderStructObject
   mapping(uint256 => InvaderStructObject) public InvaderStructObjectsDictionary;

   // Additional ARRAY Collection-Object to store all INVADER Struct-Objects:
   InvaderStructObject[] public InvaderStructObjectsArray;

   // High-Scores related Arrays to hold Names and UINT-Scores:
   uint256[] public highScoresArray = [101, 201, 301, 401, 501, 601, 701, 801, 901, 1001];
   string[] public highScoresHolderNamesArray = [ "WARHOL", "DUCHAMP", "CHAGALL", "KANDINSKY", "PICASSO", "VAN GOGH", "RINGO", "GEORGE", "PAUL", "JOHN" ];


   ///////////////////////////////////////////////////////
   //
   //    *****  OTHER CONTRACTS  *****
   //
   // I. CRYPTOPUNKS CONTRACT --> create a reference to it:
   LeCryptoPunks public punksContract;
   address punksContractAddress;

   // II. DELEGATE.XYZ CONTRACT --> create a reference to the official Delegation Registry Contract:
   IDelegateRegistry public immutable delegateRegistryContract;

   // III. CIG-TOKEN CONTRACT --> create a reference to it:
   TheCIGsContract public immutable CIGsContractInstance;
   address public constant CIGsContractAddress = 0xCB56b52316041A62B6b5D0583DcE4A8AE7a3C629;


   uint256 public MaxInvaders = 360;
   uint256 public InvadersNFTCounter;
   uint8 public maxNumNFTsAllowedToMint = 2;

   uint256 public Mother_Ship_Punk = 5230;
   uint256 public The_Rogue_Punk = 10001;

   bool public CEO_MODE = false;
   address public CIGS_CEO_Address;

   string public baseMetadataURI;


   // EVENTS:
   event BaseURIUpdated(string newlyUpdatedURI);
   event InvaderDataCustomized(uint256 invaderNFTIdNumber, uint256 newPunkID, string newInGameName, string newSocMedHandle, string newBioLink);
   event HighScoresUpdated(uint256[] highScoresArray, string[] highScoreNamesArray);
   event MaxInvadersSet(uint256 newMaxInvaders);
   event MaxNumNFTsAllowedToMintSet(uint8 newMaxNumAllowed);
   event CashedOut(uint8 currencyID, uint256 amountCashedOut);
   event RoguePunkSet(uint256 newRoguePunkID);
   event MotherShipPunkSet(uint256 newMotherShipPunkID);
   event CEOModeSet(bool OnOrOff);


   // ERRORS:
   error notOwner();
   error invalidPunkID();




   constructor(address[] memory mintToAddressesArray, uint256[] memory punkIDsArray, string[] memory inGameNamesArray, string[] memory socMediaHandlesArray, string[] memory bioURLLinksArray) ERC721("PunkInvaders", "PNKVDRS") {
      // I. The PUNKS CONTRACT
      punksContractAddress = 0xb47e3cd837dDF8e4c57F05d70Ab865de6e193BBB;
      punksContract = LeCryptoPunks(punksContractAddress);

      // II. The DELEGATE.XYZ Registry Contract:
      delegateRegistryContract = IDelegateRegistry(0x00000000000000447e69651d841bD8D104Bed493);

      // III. The CIGs Contract:
      CIGsContractInstance = TheCIGsContract(0xCB56b52316041A62B6b5D0583DcE4A8AE7a3C629);
      // Calling ".The_CEO()":
      CIGS_CEO_Address = CIGsContractInstance.The_CEO();


      baseMetadataURI = "https://www.punkinvaders.com/JSONs/PunkInvader-JSON-";


      // Auto-mint 3 Invader-NFTs, one to myself, and one each to Matt & John:
      for(uint8 invaderIDCounter = 0; invaderIDCounter < punkIDsArray.length; invaderIDCounter++) {
         address mintToAddress  = mintToAddressesArray[invaderIDCounter];
         _safeMint(mintToAddress, InvadersNFTCounter, "");

         // Create a PunkInvader Struct-Object:
         InvaderStructObject memory newInvaderStructObject = InvaderStructObject(mintToAddress, punkIDsArray[invaderIDCounter], inGameNamesArray[invaderIDCounter], socMediaHandlesArray[invaderIDCounter], bioURLLinksArray[invaderIDCounter], "Complimentary", 0);

         // Insert it into both Collection-Object:
         InvaderStructObjectsDictionary[InvadersNFTCounter] = newInvaderStructObject;
         InvaderStructObjectsArray.push(newInvaderStructObject);

         InvadersNFTCounter++;
      }


      // Immediately PAUSE the Contract upon deployment's completion:
      pause();

   }





   function getInvaderStructObjectData(uint256 tokenIDNumber) external view 
      returns(address ownerAddress, uint256 thePunkID, string memory punkInGameName, string memory ownerSocMedHandle, string memory ownerBioLink, string memory tippingCurrencyName, uint tipAmount) {

      InvaderStructObject memory theInvaderStructObject = InvaderStructObjectsDictionary[tokenIDNumber];

      return(theInvaderStructObject.ownerAddress, theInvaderStructObject.cryptopunkIDNumber, theInvaderStructObject.punkInvaderInGameName, theInvaderStructObject.ownerSocMediaHandle, theInvaderStructObject.ownerBioLink, theInvaderStructObject.tippingCurrency, theInvaderStructObject.amountTipped);
   }




   function getHighScores() external view returns(uint256[] memory theScores, string[] memory hiScoreNames) {
      return(highScoresArray, highScoresHolderNamesArray);
   }




   function updateHighScores(uint256[] memory scoresArray, string[] memory scorerNamesArray) external {
      // Make sure Caller actually OWNS an INVADER-NFT:
      require(balanceOf(msg.sender) > 0, "ERROR! UNAUTHORIZED CALLER!");

      for(uint8 scoreCounter = 0; scoreCounter < 10; scoreCounter++) {
         highScoresArray[scoreCounter] = scoresArray[scoreCounter];
         highScoresHolderNamesArray[scoreCounter] = scorerNamesArray[scoreCounter];
      }

      emit HighScoresUpdated(highScoresArray, highScoresHolderNamesArray);
   }





   // Checks if CALLER is truly a DELEGATE for the Wallet that owns the PUNK with the passed-in ID number
   // 1. Get the actual OWNER of the Punk in Question
   // 2. Verify that OWNER really made the passed-in DELEGATE-WALLET ADDRESS the official DELEGATE for the PUNK
   function isValidDelegatedMinter(uint256 delegatedTokenID, address punkManagingContractAddress) public view returns(bool walletIsValidDelegate) {
      require(delegatedTokenID >= 0 && delegatedTokenID <= 9999, "ERROR! Punk Index # must be between 0 and 9,999!");

      // Will store the OWNER of the passed-in PUNK in this here variable:
      address punkOwningWalletAddress;

      /////////////////////////////
      //
      //  SOME "GLOBALS" HERE:
      //
      // 1. Address of the O.G. LARVALABS CRYPTOPUNKS CONTRACT:
      // address PUNKS_MAINNET_MARKETPLACE = 0xb47e3cd837dDF8e4c57F05d70Ab865de6e193BBB;
      //
      // 2. Address of the O.G. WRAPPED PUNKS CONTRACT:
      // address WRAPPED_PUNKS = 0xb7F7F6C52F2e2fdb1963Eab30438024864c313F6;
      //
      // 3. Address of YUGA'S new WRAPPER CONTRACT on MAINNET:
      // address YUGA_PUNKS = 0x000000000000003607fce1aC9e043a86675C5C2F;
      //
      // 4. Address of the DELEGATE.XYZ REGISTRY CONTRACT:
      // address DELEGATE_XYZ_REGISTRY = 0x00000000000000447e69651d841bD8D104Bed493;


      /////////////////////////////////////////////////////////////////////////////////////////////////////
      //
      //  I. Get the ADDRESS of the WALLET that actually owns the Punk in question.
      //  -Note that this Punk may have been WRAPPED by either the OG "WRAPPED PUNKS" contract or the newer
      //   YUGA-PUNKS Wrapped Contract. If NEITHER is true, than the Punk is still managed by LarvaLabs' OG CryptoPunks contract.
      if(punkManagingContractAddress == punksContractAddress) {
         punkOwningWalletAddress = LeCryptoPunks(punkManagingContractAddress).punkIndexToAddress(delegatedTokenID);
      }
      else if(punkManagingContractAddress == 0xb7F7F6C52F2e2fdb1963Eab30438024864c313F6) {
         // O.G. WRAPPED PUNKS CONTRACT:
         punkOwningWalletAddress = ERC721(punkManagingContractAddress).ownerOf(delegatedTokenID);
      }
      else if(punkManagingContractAddress == 0x000000000000003607fce1aC9e043a86675C5C2F) {
         // YUGA MAINNET-WRAPPER:
         punkOwningWalletAddress = ERC721(punkManagingContractAddress).ownerOf(delegatedTokenID);
      }
      else {
         return false;
      }


      // II. USING DELEGATE.XYZ's REGISTRY Contract:
      //     -DELEGATE.XYZ's "checkDelegateForERC721()" method - which returns TRUE or FALSE - can tell us whether or 
      //      not a particular WALLET has truly been made an OFFICIAL DELEGATE for and BY a specific Punk-owning Wallet
      if( ! delegateRegistryContract.checkDelegateForERC721(msg.sender, punkOwningWalletAddress, punkManagingContractAddress, delegatedTokenID, bytes32(""))) {
         // The passed-in Wallet address is *NOT* a Delegate for the passed-in Punk-ID!
         return false;
      }
      else {
         // The passed-in Wallet address *IS* a VALID Delegate for the passed-in Punk-ID!
         return true;
      }

   }






   function mintInvader(uint256 delegatedTokenID, address punkManagingContract, bool payingWithETH, uint256 numCIGsReceived) external payable whenNotPaused() {
      // 1. Make sure we haven't sold out:
      if(InvadersNFTCounter >= MaxInvaders) {
         _pause();
      }

      // 2. Make sure the passed-in Punk-ID is between 0 and 9,999:
      require(delegatedTokenID >= 0 && delegatedTokenID <= 9999, "ERROR! Punk ID must be between 0 and 9,999!");

      // 3. Prevent Callers from minting more NFTs than permitted - unless it's the contract's Owner that's doing the minting:
      if(msg.sender != owner()) {
         require(balanceOf(msg.sender) < maxNumNFTsAllowedToMint, "ERROR! You've minted the maximum number allowed of INVADER-NFTS!");
      }

      string memory tippingCurrency = "ETH";
      uint256 tipAmount = msg.value;

      // 4. Check what CURRENCY Caller is trying to mint with:
      if(payingWithETH == false) {
         // MAKE SURE NO *ETH* was sent to the contract! We're only accepting CIGs here:
         require(msg.value == 0, "'mintInvader()' ERROR!!! 'msg.value' MUST == 0 ('ZERO')");

         tippingCurrency = "CIGs";
         tipAmount = numCIGsReceived;

         IERC20(CIGsContractAddress).transferFrom(msg.sender, address(this), numCIGsReceived); 
      }


      // 5. Make sure the Caller either OWNS the Punk or is a VALID DELEGATE of the Wallet that owns the Punk.
      require(LeCryptoPunks(0xb47e3cd837dDF8e4c57F05d70Ab865de6e193BBB).punkIndexToAddress(delegatedTokenID) == msg.sender || isValidDelegatedMinter(delegatedTokenID, punkManagingContract) == true, "E3");

      // 6. MINT an INVADER-NFT:
      _safeMint(msg.sender, InvadersNFTCounter, "");

      // 7. Create a Struct-Object for this new INVADER-NFT:
      InvaderStructObject memory newInvaderStructObject = InvaderStructObject(msg.sender, 105230, "ANON", "ANON", "ANON-BIO-LINK", tippingCurrency, tipAmount);

      // 8. Insert the new INVADER-NFT Struct-Object into the DICTIONARY/MAPPING:
      InvaderStructObjectsDictionary[InvadersNFTCounter] = newInvaderStructObject;

      // 9. PUSH the new Invader-NFT-STRUCT-OBJECT into the ARRAY:
      InvaderStructObjectsArray.push(newInvaderStructObject);

      // 10. INCREMENT our NFT-Counter:
      InvadersNFTCounter++;

      // No need to EMIT an EVENT here because the ERC721 Contract will already be emitting it's own "TRANSFER" Event!
   }





   // "_beforeTokenTransfer" must be overridden to ensure the ".ownerAddress" property of any given INVADER
   // Struct-Object gets changed/updated to the "to" address, otherwise the Struct-Object will still
   // hold the value of the ORIGINAL Minter of this NFT even *AFTER* the Transfer occurs.
   function _beforeTokenTransfer(address from, address to, uint256 firstTokenId, uint256 batchSize) internal virtual override {

      super._beforeTokenTransfer(from, to, firstTokenId, batchSize);

      // If 'from' is the 0x0000 address than this is actually a MINT of a NEW INVADER NFT, as 
      // opposed to a regualt peer-to-peer Transfer, which means we don't need to do anything:
      if(from == address(0))   return;

      // Otherwise, we must handle the TRANSFER.
      // 1. Grab the given INVADER Struct-Object from the mapping:
      InvaderStructObject storage theInvaderNFTGettingTransferred = InvaderStructObjectsDictionary[firstTokenId];

      // 2. Create a BRAND NEW Struct-NFT-Object that would be an almost exact duplicate of the current Object
      //    that's getting Transferred - except set it's "ownerAddress" property to the NEW OWNER'S ETH-Address:
      theInvaderNFTGettingTransferred.ownerAddress = to;

      // 3. Update the ARRAY of InvaderStructObjects as well:
      InvaderStructObjectsArray[firstTokenId] = theInvaderNFTGettingTransferred;
   }





   function customizeInvaderNFT(uint256 invaderNFTIdNum, uint256 punkIDNum, string memory punkInGameName, string memory socMedHandle, string memory ownerBioURL) public {
      if(msg.sender != ownerOf(invaderNFTIdNum))
         revert notOwner();

      if(punkIDNum < 0 || punkIDNum > 9999)
         revert invalidPunkID();

      // 1. Grab the INVADER Struct-Object from the MAPPING:
      InvaderStructObject storage theInvader = InvaderStructObjectsDictionary[invaderNFTIdNum];

      // 2. Assign the new passed-in values to each of its Properties:
      theInvader.cryptopunkIDNumber = punkIDNum;
      theInvader.punkInvaderInGameName = punkInGameName;
      theInvader.ownerSocMediaHandle = socMedHandle;
      theInvader.ownerBioLink = ownerBioURL;

      // 3. Update the MAPPING/DICTIONARY:
      InvaderStructObjectsDictionary[invaderNFTIdNum] = theInvader;

      // 4. Update the ARRAY of Structs:
      InvaderStructObjectsArray[invaderNFTIdNum] = theInvader;

      // 5. EMIT the "InvaderDataCustomized" EVENT:
      emit InvaderDataCustomized(invaderNFTIdNum, theInvader.cryptopunkIDNumber, theInvader.punkInvaderInGameName, theInvader.ownerSocMediaHandle, theInvader.ownerBioLink);
   }





   // ADMIN function that lets me set the MAX number of INVADER-NFTs this CONTRACT is allowed to Mint:
   function setMaxInvaders(uint256 maxInvees) external onlyOwner() {
      MaxInvaders = maxInvees;
      emit MaxInvadersSet(MaxInvaders);
   }



   // ADMIN function that lets me set the number of INVADER-NFTs a Caller is allowed to Mint:
   function setMaxNumNFTsAllowedToMint(uint8 maxNumberAllowed) external onlyOwner() {
      maxNumNFTsAllowedToMint = maxNumberAllowed;
      emit MaxNumNFTsAllowedToMintSet(maxNumNFTsAllowedToMint);
   }




   // ADMIN function that lets me turn/toggle the "CEO-MODE" ON and OFF:
   function toggleCEOMode() external onlyOwner() {
      if(CEO_MODE == true) {
         CEO_MODE = false;
      } 
      else {
         CEO_MODE = true;
      } 

      emit CEOModeSet(CEO_MODE);
   }





   // ADMIN function that lets me set the Rogue-Punk's ID:
   // *May also allows the CIGs_CEO do this  >! IF !<  I've enabled that feature via the "CEO_MODE" Bool variable...
   function setRoguePunk(uint256 newRoguePunkID) external {
      require(msg.sender == owner() || (CEO_MODE && msg.sender == CIGS_CEO_Address), "ERROR! Only Contract-Owner or CIGs-CEO can do this!");
      require(newRoguePunkID >= 0 && newRoguePunkID <= 9999, "ERROR! Punk ID must be between 0 and 9999!");

      The_Rogue_Punk = newRoguePunkID;
      emit RoguePunkSet(The_Rogue_Punk);
   }




   // ADMIN function that lets me set the Mother-Ship-Punk's ID: 
   // *May also allow the CIGs_CEO do this  >! IF !<  I've enabled that feature via the "CEO_MODE" Bool variable...
   function setMotherShipPunk(uint256 newMotherShipPunkID) external {
      require(msg.sender == owner() || (CEO_MODE && msg.sender == CIGS_CEO_Address), "ERROR! Only Contract-Owner or CIGs CEO can do this!");
      require(newMotherShipPunkID >= 0 && newMotherShipPunkID <= 9999, "ERROR! Punk ID must be between 0 and 9999!");

      Mother_Ship_Punk = newMotherShipPunkID;
      emit MotherShipPunkSet(Mother_Ship_Punk);
   }





   ///////////////////////////////////////////////////////////////////////////////
   //
   //   *** TOKEN URI FUNCTIONS ***
   // 
   // 
   // I. SETTER function:
   function setBaseURI(string memory newBaseURI) external onlyOwner() {
      baseMetadataURI = newBaseURI;
      emit BaseURIUpdated(baseMetadataURI);
   }



   // II. GETTER function (OVERRIDING the standard ERC721 "_baseURI()" function):
   function _baseURI() internal view virtual override returns (string memory) {
      return baseMetadataURI;
   }


   // III. GETTER Function:
   function tokenURI(uint256 tokenId) public view override(ERC721) returns (string memory) {
      require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

      // If "baseURI" is EMPTY, return "", otherwise, concatenate ("") the "baseURI" with the "tokenId":
      return bytes(baseMetadataURI).length > 0 ? string(abi.encodePacked(baseMetadataURI, tokenId.toString(), ".json")) : "";
   }





   // Withdraw the ENTIRE BALANCE:
   function cashOut(uint8 currencyID) public onlyOwner() {
      uint256 amountToWithdraw;

      if(currencyID == 1) {
         // 1. Withdraw the ETH:
         amountToWithdraw = address(this).balance;
         (bool sentSuccessfully, ) = payable(msg.sender).call{ value: amountToWithdraw } ("");
         require(sentSuccessfully, "\n\n !!! ETH Withdrawal FAILED!!!");
      }
      else {
         // 2. Withdraw the DummyCoins/CIGs:
         amountToWithdraw = IERC20(CIGsContractAddress).balanceOf(address(this));
         if(amountToWithdraw > 0) {
            IERC20(CIGsContractAddress).safeTransfer(msg.sender, amountToWithdraw);
         }
      }

      emit CashedOut(currencyID, amountToWithdraw);
   }





   // PAUSING and UN-PAUSING:
   function pause() public onlyOwner() {
      _pause();
   }


   function unpause() public onlyOwner() {
      _unpause();
   }



   function supportsInterface(bytes4 interfaceId) public view override(ERC721) returns(bool) {
      return super.supportsInterface(interfaceId);
   }



}





