import { LibFundsAdminStorage as FundsStorage } from "./../libraries/LibFundsAdminStorage.sol";
import { LibStructStorageV2 as StructStorage } from "../libraries/LibStructStorageV2.sol";

contract FundsAdminFacetV2 is IFundsAdmin, FacetInitializable {
    using EnumerableSet for EnumerableSet.AddressSet;
    using SafeERC20 for IERC20;

    event WithdrawPlatformFees(address indexed withdrawer, address indexed token, uint256 day, uint256 amount);

    function initializeFundsAdmin(
        uint256 _referenceBlockNumber,
        uint256 _blocksPerDay
    ) external initializer(FundsStorage.getStorage().inited) {
        FundsStorage.FundsAdminStorage storage data = FundsStorage.getStorage();
        data.referenceBlockNumber = _referenceBlockNumber;
        data.blocksPerDay = _blocksPerDay;
    }

    function getCurrentDay() internal view returns (uint256) {
        FundsStorage.FundsAdminStorage storage fundsData = FundsStorage.getStorage();
        return (block.number - fundsData.referenceBlockNumber) / fundsData.blocksPerDay;
    }

    function withdrawPlatformFees(
        address withdrawer,
        address token,
        uint256 amount
    ) external override only(StructStorage.FUNDS_ADMIN_ROLE) {
        if (token == address(0)) {
            (bool success, ) = payable(withdrawer).call{ value: amount }("");
            if (!success) revert StructStorage.FA_NATIVE_TRANSFER();
        } else {
            IERC20(token).safeTransfer(withdrawer, amount);
        }

        emit WithdrawPlatformFees(withdrawer, token, getCurrentDay(), amount);
    }

    function referenceBlockNumber() external view returns (uint) {
        FundsStorage.FundsAdminStorage storage fundsData = FundsStorage.getStorage();
        return fundsData.referenceBlockNumber;
    }

    function blocksPerDay() external view override returns (uint) {
        FundsStorage.FundsAdminStorage storage fundsData = FundsStorage.getStorage();
        return fundsData.blocksPerDay;
    }

    function setReferenceBlockNumber(uint _referenceBlockNumber) external override only(StructStorage.OPERATOR_ROLE) {
        FundsStorage.FundsAdminStorage storage fundsData = FundsStorage.getStorage();
        fundsData.referenceBlockNumber = _referenceBlockNumber;
    }

    function setBlocksPerDay(uint _blocksPerDay) external override only(StructStorage.OPERATOR_ROLE) {
        FundsStorage.FundsAdminStorage storage fundsData = FundsStorage.getStorage();
        fundsData.blocksPerDay = _blocksPerDay;
    }
}
"
