import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { LibStructStorageV2 as StructStorage } from "../libraries/LibStructStorageV2.sol";

library LibMarketplaceStorage {
    using EnumerableSet for EnumerableSet.AddressSet;

    bytes32 internal constant MARKETPLACE_STORAGE_SLOT = keccak256("KTHD.MARKETPLACE.STORAGE");
    /**
     * @dev Storage slot with the address of the current dummy-implementation.
     * This is the keccak-256 hash of "eip1967.proxy.implementation" subtracted by 1
     * bytes32(uint(keccak256("eip1967.proxy.implementation")) - 1)
     */
    bytes32 internal constant IMPLEMENTATION_SLOT = 0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc;

    struct MarketplaceStorage {
        StructStorage.InitFlag inited;
        // Mapping used to ensure that a payments payload is used only once
        mapping(uint256 => bool) splitsNonces;
        // Mapping used to ensure that a sell payload is used only once for a seller
        mapping(address => mapping(uint256 => bool)) sellNonces;
        // Backend number used for processing logs efficiently
        mapping(uint256 => bool) externalIds;
        // Mapping used for limiting the edition number for a drop (dropId => number of editions minted
        mapping(uint256 => uint) editionsMinted;
        // Set used for accepted payment tokens
        EnumerableSet.AddressSet acceptedTokens;
        // String used for computing utility URI for the NFTs with utility associated
        string baseUtilityUri;
        // Mapping from a CollectionOffer hash to the number of NFT sold (set to max to cancel an offer)
        mapping(bytes32 => uint256) collectionOffers;
    }

    struct ImplementationStorage {
        // Address used for visibility of the Diamond in Etherscan
        address dummyImplementation;
    }

    function getStorage() internal pure returns (MarketplaceStorage storage storageStruct) {
        bytes32 position = MARKETPLACE_STORAGE_SLOT;
        assembly {
            storageStruct.slot := position
        }
    }

    function getImplementationStorage() internal pure returns (ImplementationStorage storage storageStruct) {
        bytes32 position = IMPLEMENTATION_SLOT;
        assembly {
            storageStruct.slot := position
        }
    }
}
