pragma solidity 0.8.21;

import "./../utils/ContextMixin.sol";
import "./../interfaces/IAccessControl.sol";
import { LibStructStorageV2 as StructStorage } from "../libraries/LibStructStorageV2.sol";

abstract contract FacetInitializable is ContextMixin {
    modifier initializer(StructStorage.InitFlag storage flag) {
        require(!flag.inited, "already inited");
        _;
        flag.inited = true;
    }

    modifier only(bytes32 role) {
        if (!IAccessControl(address(this)).hasRole(role, msg.sender)) {
            revert StructStorage.MISSING_ROLE(role);
        }
        _;
    }
}
