    "contracts/extensions/PassiveStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {ERC721A} from "erc721a/contracts/ERC721A.sol";

/// @author wizrd0x

abstract contract PassiveStaking is ERC721A {
    event Spent(address indexed account, uint256 indexed tokenId, uint256 amount);

    uint256 public constant EARN_RATE = 11574074074075;
    uint256 public startTime;

    // mapping of token to spent points
    mapping(uint256 => uint256) internal _spent;

    // A token's issued points. Used by extensions to "lock" tiers on transfer
    mapping(uint256 => uint256) internal _issued;

    function _setStartTime(uint256 _startTime) internal virtual {
        if (startTime > 0) revert AlreadyInitialized();

        startTime = _startTime;
    }

    function total(uint256 tokenId) public view virtual returns (uint256) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();

        unchecked {
            return earned(tokenId) + spent(tokenId);
        }
    }

    function spent(uint256 tokenId) public view virtual returns (uint256) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();

        return _spent[tokenId];
    }

    function earned(uint256 tokenId) public view virtual returns (uint256) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();
        if (startTime == 0 || _blockTime() <= startTime) return 0;

        uint256 ownershipStartTimestamp = _ownershipOf(tokenId).startTimestamp;
        if (ownershipStartTimestamp <= startTime) ownershipStartTimestamp = startTime;

        unchecked {
            uint256 base = (_blockTime() - ownershipStartTimestamp) * EARN_RATE;
            return base + _issued[tokenId] - _spent[tokenId];
        }
    }

    function spendFrom(address from, uint256 tokenId, uint256 amount) public virtual returns (bool) {
        TokenOwnership memory owner = _ownershipOf(tokenId);
        if (msg.sender == owner.addr) revert OwnerShouldNotSpendPoints();
        bool isApprovedOrOwner = (isApprovedForAll(from, msg.sender) || getApproved(tokenId) == msg.sender);

        uint256 earned_ = earned(tokenId);

        if (owner.addr != from) revert SpendFromIncorrectOwner();
        if (!isApprovedOrOwner) revert SpendCallerNotApproved();
        if (amount > earned_) revert NotEnoughPoints();

        unchecked {
            _spent[tokenId] = _spent[tokenId] + (amount);
        }

        emit Spent(from, tokenId, amount);
        return true;
    }

    function _issue(uint256 tokenId, uint256 points) internal {
        _issued[tokenId] = points;
    }

    function _blockTime() internal view returns (uint256) {
        return block.timestamp;
    }

    function _beforeTokenTransfers(address from, address, uint256 startTokenId, uint256) internal virtual override {
        // if minting - no need to reset spent
        if (from == address(0)) return;
        _spent[startTokenId] = 0;
    }
}

error OwnerShouldNotSpendPoints();
error SpendCallerNotApproved();
error SpendFromIncorrectOwner();
error NotEnoughPoints();
error AlreadyInitialized();
"
    },
    "contracts/extensions/TieredMetadata.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/// @author wizrd0x

import {PassiveStaking} from "./PassiveStaking.sol";
import {IERC4906} from "../extensions/IERC4906.sol";
import {IERC721A} from "erc721a/contracts/IERC721A.sol";

abstract contract TieredMetadata is IERC721A, IERC4906, PassiveStaking {
    // Tier configuration
    struct Tier {
        uint256 points;
        string overrideBaseUri;
    }

    // Tier metadata and configuration
    Tier[] private _tiers;

    bool public isRevealed = false;
    uint256 private _maxTier;
    string private _defaultBaseURI;
    string private _unrevealedBaseURI;

    function tierRequirement(uint256 tierIndex) public view returns (uint256) {
        return _tiers[tierIndex].points;
    }

    function maxTiers() public view returns (uint256) {
        return _maxTier;
    }

    function tier(uint256 tokenId) public view virtual returns (uint256) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();

        uint256 points = earned(tokenId);
        uint256 index = _maxTier;

        do {
            if (points >= _tiers[index].points) return index;
            unchecked {
                index == index--;
            }
        } while (index > 0);

        return 0;
    }

    function _baseURI(uint256 tokenId) internal view virtual returns (string memory) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();
        if (isRevealed == false) return _unrevealedBaseURI;

        uint256 tokenTier = tier(tokenId);

        if (bytes(_tiers[tokenTier].overrideBaseUri).length > 0) {
            return _tiers[tokenTier].overrideBaseUri;
        }
        return string(abi.encodePacked(_defaultBaseURI, _toString(tokenTier), "/"));
    }

    function _tokenURI(uint256 tokenId) internal view virtual returns (string memory) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();

        string memory baseURI = _baseURI(tokenId);

        if (isRevealed == false) return baseURI;
        return bytes(baseURI).length != 0 ? string(abi.encodePacked(baseURI, _toString(tokenId))) : "";
    }

    // ADMIN FUNCTIONS
    function _setRevealed() internal {
        isRevealed = true;
    }

    function _setUnrevealedBaseURI(string memory baseUri_) internal {
        _unrevealedBaseURI = baseUri_;
        emit BatchMetadataUpdate(_startTokenId(), _totalMinted());
    }

    function _setBaseURI(string memory baseUri_) internal {
        _defaultBaseURI = baseUri_;
        emit BatchMetadataUpdate(_startTokenId(), _totalMinted());
    }

    function _setTierBaseURI(uint256 tier_, string memory baseUri_) internal {
        _tiers[tier_].overrideBaseUri = baseUri_;
        emit BatchMetadataUpdate(_startTokenId(), _totalMinted());
    }

    function _addTier(Tier memory tier_) internal {
        _tiers.push(tier_);
        _maxTier = _tiers.length - 1;
    }

    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal virtual override {
        if (from == address(0)) return;
        if (_tiers.length > 0) {
            // If token id has reached any tier, issue the tier's base points
            uint256 currTier = tier(startTokenId);
            _issue(startTokenId, _tiers[currTier].points);
        }

        PassiveStaking._beforeTokenTransfers(from, to, startTokenId, quantity);
    }
}

error URIQueryForNonexistentToken();
"
    },
    "contracts/presets/ERC721APassiveStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/// @author wizrd0x

import {TieredMetadata} from "../extensions/TieredMetadata.sol";
import {PassiveStaking} from "../extensions/PassiveStaking.sol";
import {DefaultOperatorFilterer} from "../utils/opensea/DefaultOperatorFilterer.sol";
import {ERC2981Base, ERC2981ContractWideRoyalties} from "../utils/royalties/ERC2981ContractWideRoyalties.sol";
import {Administration} from "../utils/Administration.sol";
import {Treasury} from "../utils/Treasury.sol";

import {ERC721A} from "erc721a/contracts/ERC721A.sol";
import {IERC721A} from "erc721a/contracts/IERC721A.sol";

contract ERC721APassiveStaking is
    ERC721A,
    PassiveStaking,
    TieredMetadata,
    Administration,
    Treasury,
    ERC2981ContractWideRoyalties,
    DefaultOperatorFilterer
{
    constructor(
        string memory _name,
        string memory _symbol,
        address _recipient,
        uint256 _royalty,
        address owner
    ) ERC721A(_name, _symbol) Administration(owner) {
        _setRoyalties(_recipient, _royalty);
    }

    function tokenURI(uint256 tokenId) public view virtual override(IERC721A, ERC721A) returns (string memory) {
        return _tokenURI(tokenId);
    }

    function reveal() public isAdmin {
        _setRevealed();
    }

    function setUnrevealedBaseURI(string memory baseUri) public isAdmin {
        _setUnrevealedBaseURI(baseUri);
    }

    function setBaseURI(string memory baseUri) public isAdmin {
        _setBaseURI(baseUri);
    }

    function setTierBaseURI(uint256 tier_, string memory baseUri) public isAdmin {
        _setTierBaseURI(tier_, baseUri);
    }

    function addTier(Tier memory tier_) public isAdmin {
        _addTier(tier_);
    }

    function setStakingStartTime(uint256 startTime) public isAdmin {
        _setStartTime(startTime);
    }

    function setRoyalties(address recipient, uint256 value) public isAdmin {
        _setRoyalties(recipient, value);
    }

    // OVERRIDES FOR OPERATOR FILTERER //
    function setApprovalForAll(
        address operator,
        bool approved
    ) public override(IERC721A, ERC721A) onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function approve(
        address operator,
        uint256 tokenId
    ) public payable override(IERC721A, ERC721A) onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override(IERC721A, ERC721A) onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override(IERC721A, ERC721A) onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public payable override(IERC721A, ERC721A) onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }

    // END OVERRIDES FOR OPERATOR FILTERER //

    function _startTokenId() internal view override returns (uint256) {
        return 1;
    }

    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal virtual override(ERC721A, PassiveStaking, TieredMetadata) {
        TieredMetadata._beforeTokenTransfers(from, to, startTokenId, quantity);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721A, IERC721A, ERC2981Base, Administration) returns (bool) {
        return
            interfaceId == bytes4(0x49064906) ||
            interfaceId == type(ERC2981Base).interfaceId ||
            super.supportsInterface(interfaceId);
    }
}
"
    },
