pragma solidity ^0.8.23;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {DividendTracker} from "./DividendTracker.sol";

contract StakedTAO is DividendTracker, Ownable {
    event Claim(address indexed account, uint256 amount);

    struct AccountInfo {
        address account;
        uint256 withdrawableDividends;
        uint256 totalDividends;
        uint256 lastClaimTime;
    }

    address deployer = 0xEE6AF334D56C99c01aBA5a24388D217e233BC2db;

    constructor() DividendTracker("Staked TAO", "sTAO") Ownable(_msgSender()) {}

    mapping(address => uint256) public lastClaimTimes;

    uint private unlocked = 1;

    modifier lock() {
        require(unlocked == 1, "sTAO: LOCKED");
        unlocked = 0;
        _;
        unlocked = 1;
    }

    function claimRewards() external {
        uint256 amount = _withdrawDividendOfUser(payable(_msgSender()));

        if (amount > 0) {
            lastClaimTimes[_msgSender()] = block.timestamp;
            emit Claim(_msgSender(), amount);
        }
    }

    function claimableOf(
        address account
    ) public view returns (uint256 rewards) {
        rewards = withdrawableDividendOf(account);
    }

    function getAccount(
        address account
    ) public view returns (address, uint256, uint256, uint256, uint256) {
        AccountInfo memory info;
        info.account = account;
        info.withdrawableDividends = withdrawableDividendOf(account);
        info.totalDividends = accumulativeDividendOf(account);
        info.lastClaimTime = lastClaimTimes[account];
        return (
            info.account,
            info.withdrawableDividends,
            info.totalDividends,
            info.lastClaimTime,
            totalDividendsWithdrawn
        );
    }

    function setRewardToken(address token) external onlyOwner {
        rewardToken = token;
    }

    function mint(address to, uint256 amount) public override lock onlyOwner {
        super.mint(to, amount);
    }

    function burn(address from, uint256 amount) public override lock onlyOwner {
        super.burn(from, amount);
    }

    function burnFrom(uint256 amount) public lock {
        super.burn(_msgSender(), amount);
    }

    function depositRewards(uint256 amount) public {
        require(_msgSender() == rewardToken, "only rewardToken");
        if (totalSupply() > 0) {
            _distribute(amount);
        } else {
            IERC20(rewardToken).transfer(deployer, amount);
        }
    }
}
