    "DiamondHands.sol": {
      "content": "pragma solidity ^0.8.2;

import "@openzeppelin/access/Ownable.sol";
import "@openzeppelin/interfaces/IERC20.sol";

contract DiamondHands is Ownable {

    event CapsuleCreated(uint256 id);
    event CapsuleOpened(uint256 id);

    struct Capsule {
        uint256 timestamp;
        uint256 duration;
        uint256 amount;
        address token;
        address owner;
        bool opened;
    }

    uint256 capsuleId = 0;
    mapping(uint256 => Capsule) public capsules;
    mapping(address => uint256[]) public ownerCapsules;

    constructor() Ownable(msg.sender) {

    }

    function createCapsule(uint256 _duration, uint256 _amount, address _token) public {
        require(_duration > 0, "Duration must be greater than 0");
        require(_amount > 0, "Amount must be greater than 0");
        require(_token != address(0) && _token != address(this), "Token address must be valid");

        Capsule memory capsule = Capsule(block.timestamp, _duration, _amount, _token, msg.sender, false);
        capsules[capsuleId] = capsule;
        ownerCapsules[msg.sender].push(capsuleId);
        capsuleId++;

        IERC20(_token).transferFrom(msg.sender, address(this), _amount);

        emit CapsuleCreated(capsuleId - 1);
    }

    function createCapsuleFromAllowance(uint256 _duration, address _token) public {
        require(_duration > 0, "Duration must be greater than 0");
        require(_token != address(0) && _token != address(this), "Token address must be valid");

        uint256 _amount = IERC20(_token).allowance(msg.sender, address(this));
        require(_amount > 0, "Amount must be greater than 0");

        Capsule memory capsule = Capsule(block.timestamp, _duration, _amount, _token, msg.sender, false);
        capsules[capsuleId] = capsule;
        ownerCapsules[msg.sender].push(capsuleId);
        capsuleId++;

        IERC20(_token).transferFrom(msg.sender, address(this), capsule.amount);

        emit CapsuleCreated(capsuleId - 1);
    }

    function openCapsule(uint256 _id) public {
        Capsule memory capsule = capsules[_id];
        require(capsule.owner == msg.sender, "Only owner can open capsule");
        require(!capsule.opened, "Capsule already opened");
        require(block.timestamp >= capsule.timestamp + capsule.duration, "Capsule is still locked");

        capsules[_id].opened = true;
        IERC20(capsule.token).transfer(msg.sender, capsule.amount);

        emit CapsuleOpened(_id);
    }

}"
