    mapping(address => mapping(address => uint256)) private _allowances;
    uint256 private _totalShare;
    mapping(address => bool) private blacklisted;
    //use exchangeRate to cache balance and share exchange rate to save gas.
    uint256 public exchangeRate;

    //pack to same slot to save gas.
    uint64 public lastDistributionTimestamp;
    uint64 public minDistributionInterval;
    uint64 public maxDistributionRatio;

    constructor() {
        exchangeRate = WAD;
    }

    function initialize() public initializer {
        __Ownable_init();
        __Pausable_init();

        exchangeRate = WAD;
    }

    /**
     * @dev Only issuer can call functions marked by this modifier.
     **/
    modifier onlyIssuer() {
        require(msg.sender == issuer, "!issuer");
        _;
    }

    /**
     * @dev Only operator can call functions marked by this modifier.
     **/
    modifier onlyOperator() {
        require(msg.sender == operator, "!operator");
        _;
    }

    /**
     * @dev Only emergency role can call functions marked by this modifier.
     **/
    modifier onlyEmergency() {
        require(msg.sender == emergency, "!emergency");
        _;
    }

    /**
     * @dev Only account is not blacklisted can call functions marked by this modifier.
     * @param account The address to check
     */
    modifier notBlacklisted(address account) {
        require(!blacklisted[account], "account is blacklisted");
        _;
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public pure returns (string memory) {
        return "Flat Coin";
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public pure returns (string memory) {
        return "FC";
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     */
    function decimals() public pure returns (uint8) {
        return 18;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return getAmountByShares(_totalShare);
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return getAmountByShares(_shares[account]);
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20};
     *
     * Requirements:
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
