    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual whenNotPaused {
        require(owner != address(0), "approve from the zero address");
        require(spender != address(0), "approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
     * @notice Moves `shareAmount` shares from `sender` to `recipient`.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must hold at least `shareAmount` shares.
     * - the contract must not be paused.
     */
    function _transferShares(
        address sender,
        address recipient,
        uint256 shareAmount
    ) internal {
        _shares[sender] = _shares[sender].sub(
            shareAmount,
            "transfer amount exceeds balance"
        );
        _shares[recipient] = _shares[recipient].add(shareAmount);
    }

    /** @dev Creates `shareAmount` share and assigns them to `account`, increasing
     * the total share.
     * Requirements
     *
     * - `account` cannot be the zero address.
     */
    function _mintShare(address account, uint256 shareAmount) internal virtual {
        require(account != address(0), "mint to the zero address");

        _totalShare = _totalShare.add(shareAmount);
        _shares[account] = _shares[account].add(shareAmount);
    }

    /**
     * @dev Destroys `shareAmount` shares from `account`, reducing the
     * total share.
     * Requirements
     *
     * - `account` cannot be the zero address.
     * - `account` must have at least `shareAmount` share.
     */
    function _burnShare(address account, uint256 shareAmount) internal virtual {
        require(account != address(0), "burn from the zero address");

        _shares[account] = _shares[account].sub(
            shareAmount,
            "burn amount exceeds balance"
        );
        _totalShare = _totalShare.sub(shareAmount);
    }

    /**
     * @dev Set issue role to `_issuer` address, only owner can call this function
     * Requirements
     *
     * - `_issuer` cannot be the zero address.
     */
    function setIssuer(address _issuer) external onlyOwner {
        require(_issuer != address(0), "new role is zero address");
        issuer = _issuer;
        emit IssuerUpdated(_issuer);
    }

    /**
     * @dev Set operator role to `_operator` address, only owner can call this function
     * Requirements
     *
     * - `_operator` cannot be the zero address.
     */
    function setOperator(address _operator) external onlyOwner {
        require(_operator != address(0), "new role is zero address");
        operator = _operator;
        emit OperatorUpdated(_operator);
    }

    /**
     * @dev Set emergency role to `_emergency` address, only owner can call this function
     * Requirements
     *
     * - `_emergency` cannot be the zero address.
     */
    function setEmergency(address _emergency) external onlyOwner {
        require(_emergency != address(0), "new role is zero address");
        emergency = _emergency;
        emit EmergencyUpdated(_emergency);
    }

    /**
     * @dev Set min distribution interval to 'interval', only owner can call this function
     */
    function setMinDistributionInterval(uint64 interval) external onlyOwner {
        minDistributionInterval = interval;
        emit MinDistributionIntervalUpdated(interval);
    }

    /**
     * @dev Set max distribution ratio to 'ratio', only owner can call this function
     */
    function setMaxDistributionRatio(uint64 ratio) external onlyOwner {
        maxDistributionRatio = ratio;
        emit MaxDistributionRatioUpdated(ratio);
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply. Only issuer role can call this function.
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     */
    function mint(
        address account,
        uint256 amount
    ) external onlyIssuer whenNotPaused {
        uint256 _sharesToMint = getSharesByAmount(amount);
        _mintShare(account, _sharesToMint);
        emit Transfer(address(0), account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply. Only issuer role can call this function.
     *
     * Emits a {Transfer} event with `to` set to the zero address.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     * - `account` must have at least `amount` tokens.
     */
    function burn(address account, uint256 amount) external onlyIssuer {
        uint256 _sharesToMint = getSharesByAmount(amount);
        _burnShare(account, _sharesToMint);
        emit Transfer(account, address(0), amount);
    }

    /**
     * @dev Add `account` address to black list.
     * only emergency role can call this function
     */
    function addBlackList(address account) external onlyEmergency {
        blacklisted[account] = true;
        emit BlackListAdded(account);
    }

    /**
     * @dev Remove `account` address to black list.
     * only emergency role can call this function
     */
    function removeBlackList(address account) external onlyEmergency {
        delete blacklisted[account];
        emit BlackListRemoved(account);
    }

    /**
     * @dev Pause the contract, all transfer and approve event will be forbidden.
     * only emergency role can call this function
     */
    function pause() external onlyEmergency {
        _pause();
    }

    /**
     * @dev Unpause the contract, transfer and approve event will be recovered.
     * only owner role can call this function
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Distribute interest for the token owner.
     * only operator role can call this function
     * Emits a {InterestsDistributed} event.
     *
     * Requirements:
     *
     * - `distributedInterest` amount cannot exceed maxDistributionRatio.
     * - Duration since last 'distributeInterests' cannot less than minDistributionInterval
     */
    function distributeInterests(
        int256 distributedInterest,
        uint256 interestFromTime,
        uint256 interestToTime
    ) external onlyOperator {
        require(
            lastDistributionTimestamp + minDistributionInterval <
                block.timestamp,
            "min distribution interval violated"
        );

        uint256 oldTotalSupply = totalSupply();
        uint256 interestDiff = distributedInterest >= 0
            ? distributedInterest.toUint256()
            : (-distributedInterest).toUint256();
        require(
            oldTotalSupply.mul(maxDistributionRatio) >= interestDiff.mul(WAD),
            "max distribution ratio exceed"
        );

        uint newTotalSupply = distributedInterest >= 0
            ? oldTotalSupply.add(interestDiff)
            : oldTotalSupply.sub(interestDiff);
        exchangeRate = newTotalSupply.mul(WAD).div(_totalShare);
        lastDistributionTimestamp = uint64(block.timestamp);

        emit InterestsDistributed(
            distributedInterest,
            newTotalSupply,
            interestFromTime,
            interestToTime
        );
    }
}
