      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import { ERC7616 } from "./ERC7616.sol";
import { console2 } from "forge-std/src/console2.sol";
import { IJelly7616 } from "./interfaces/IJelly7616.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

// owner should be in whitelist & should approve all token to this contract
// 21,000 blocks in total, which shuold be
// 21,000 * 13.5 / 60/ 60 = 78.75 hours
contract Jelly7616 is ERC7616, IJelly7616 {
    uint256 public constant MINT_PRICE = 0.01 ether;
    uint256 public constant EPOCH_BLOCKS = 200;
    uint256 public constant TOTAL_QUOTA_EACH_EPOCH = 25_000 ether;
    uint256 public constant MAX_EPOCH = 40;
    uint256 public currentEpoch = 0;
    uint256 public currentEpochEndBlock;
    address payable teamAddress;
    string public dataURI;
    string public baseTokenURI;
    uint256 public startBlock;

    // epoch => address => minted amt
    mapping(uint256 => mapping(address => uint256)) public minters;
    // epoch => total minted amt
    mapping(uint256 => uint256) public totalMinted;
    // epoch => address => isRewardClaimed
    mapping(uint256 => mapping(address => bool)) public isClaimed;
    // epoch => sharePerMint
    mapping(uint256 => uint256) public EpochSharePerMint;
    // tokenId => attribute
    mapping(uint256 => uint256) public tokenAttributes;

    error NoEnoughEth();
    error MaxEpochsReached();
    error StartBlockAlreadySet();
    error MintIsNotStarted();
    error NotEligible();
    error EpochNotEnded();
    error AlreadyClaimed();

    event JellyMinted(address indexed minter, uint256 indexed epoch);
    event JellyBatchMinted(address indexed minter, uint256 indexed epoch, uint256 amount);
    event JellyClaimed(address indexed minter, uint256 indexed epoch, uint256 amount);
    event EpochEnded(uint256 indexed epoch, uint256 totalMinted, uint256 sharePerMint);

    constructor(address teamAddress_, address _owner) ERC7616("Jelly", "JELLY", 18, 10_000, _owner) {
        teamAddress = payable(teamAddress_);

        whitelist[teamAddress_] = true;
        whitelist[address(0)] = true;

        if (teamAddress_ != _owner) {
            whitelist[_owner] = true;
        }
    }

    function setStartBlock(uint256 blockNumber_) external onlyOwner {
        startBlock = blockNumber_;
        currentEpochEndBlock = blockNumber_ + EPOCH_BLOCKS;
    }

    function setDataURI(string memory _dataURI) public onlyOwner {
        dataURI = _dataURI;
    }

    function setTokenURI(string memory _tokenURI) public onlyOwner {
        baseTokenURI = _tokenURI;
    }

    function setNameSymbol(string memory _name, string memory _symbol) public onlyOwner {
        _setNameSymbol(_name, _symbol);
    }

    function mint() public payable {
        // Perform all minting checks
        performMintingChecks();

        // Perform the mint operation for 1 quantity
        performMint(1);
    }

    function batchMint(uint256 quantity) public payable {
        require(quantity > 0, "Quantity must be greater than 0");

        // Perform all minting checks for the requested quantity
        performMintingChecks();

        // Perform the mint operation for the requested quantity
        performMint(quantity);

        // Emit event for minting
        emit JellyBatchMinted(msg.sender, currentEpoch, quantity);
    }

    function performMintingChecks() internal {
        if (block.number < startBlock || startBlock == 0) {
            revert MintIsNotStarted();
        }

        endEpoch();
        if (currentEpoch == MAX_EPOCH) {
            revert MaxEpochsReached();
        }
    }

    function performMint(uint256 quantity) internal {
        uint256 totalPrice = MINT_PRICE * quantity;
        if (msg.value < totalPrice) {
            revert NoEnoughEth();
        }
        uint256 excessAmount = msg.value - totalPrice;

        unchecked {
            minters[currentEpoch][msg.sender] += quantity;
            totalMinted[currentEpoch] += quantity;
        }

        // Return the excess ETH to the sender
        if (excessAmount > 0) {
            payable(msg.sender).transfer(excessAmount);
        }

        teamAddress.transfer(totalPrice);
    }

    function getTotalClaimableAmount(address _owner) public view returns (uint256) {
        uint256 totalClaimable = 0;
        uint256 curEpoch = getActualCurrentEpoch();

        for (uint256 i = 0; i < curEpoch; i++) {
            if (!isClaimed[i][_owner] && minters[i][_owner] > 0) {
                // Calculate the claimable amount for each unclaimed epoch
                uint256 claimableAmt = minters[i][_owner] * EpochSharePerMint[i];
                totalClaimable += claimableAmt;
            }
        }

        return totalClaimable;
    }

    function getClaimableEpoches(address _owner) public view returns (uint256[] memory) {
        uint256 claimableCount = 0;

        for (uint256 i = 0; i < currentEpoch; i++) {
            if (!isClaimed[i][_owner] && minters[i][_owner] > 0) {
                claimableCount++;
            }
        }

        uint256[] memory claimableEpochs = new uint256[](claimableCount);
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < currentEpoch; i++) {
            if (!isClaimed[i][_owner] && minters[i][_owner] > 0) {
                claimableEpochs[currentIndex] = i;
                currentIndex++;
            }
        }

        return claimableEpochs;
    }

    // todo: we should not allow users to claim more than 256 tokens in 1 block
    // because that would lose the randomness
    function claimToken(uint256[] calldata epoches) public {
        endEpoch();
        uint256 totalClaimable = 0;
        uint256 curEpoch = getActualCurrentEpoch();

        for (uint256 i = 0; i < epoches.length; i++) {
            uint256 epoch = epoches[i];

            if (epoch >= curEpoch) {
                revert EpochNotEnded();
            }

            // check if already claimed
            if (isClaimed[epoch][msg.sender]) {
                revert AlreadyClaimed();
            }

            if (minters[epoch][msg.sender] > 0) {
                // Calculate the claimable amount for each unclaimed epoch
                uint256 claimableAmt = minters[epoch][msg.sender] * EpochSharePerMint[epoch];
                totalClaimable += claimableAmt;
                isClaimed[epoch][msg.sender] = true;

                emit JellyClaimed(msg.sender, epoch, claimableAmt);
            } else {
                revert NotEligible();
            }
        }

        _transfer(address(0), msg.sender, totalClaimable);
    }

    function calculateSharesPerMint(uint256 epoch) internal view returns (uint256) {
        if (totalMinted[epoch] > 0) {
            uint256 sharePerMintAtEpoch = TOTAL_QUOTA_EACH_EPOCH / totalMinted[epoch];
            return sharePerMintAtEpoch;
        } else {
            return 0;
        }
    }

    function endEpoch() internal {
        if (block.number < currentEpochEndBlock) {
            return;
        }

        uint256 sharePerMint = calculateSharesPerMint(currentEpoch);
        EpochSharePerMint[currentEpoch] = sharePerMint;

        emit EpochEnded(currentEpoch, totalMinted[currentEpoch], sharePerMint);

        currentEpoch = getActualCurrentEpoch();

        if (currentEpoch == MAX_EPOCH) {
            return;
        }

        // Update epoch information for the new epoch
        currentEpochEndBlock = startBlock + (getActualCurrentEpoch() + 1) * EPOCH_BLOCKS;
    }

    function getEpochesCollapsed() public view returns (uint256 epochesCollapsed) {
        if (block.number >= currentEpochEndBlock) {
            epochesCollapsed = (block.number - startBlock) / EPOCH_BLOCKS - currentEpoch;
        } else {
            epochesCollapsed = 0;
        }
    }

    function getActualCurrentEpoch() public view returns (uint256) {
        uint256 actualEpoch = currentEpoch + getEpochesCollapsed();

        return actualEpoch < MAX_EPOCH ? actualEpoch : MAX_EPOCH;
    }

    function getActualCurrentEpochStartBlock() public view returns (uint256 actualCurrentEpochStartBlock) {
        actualCurrentEpochStartBlock = startBlock + EPOCH_BLOCKS * getActualCurrentEpoch();
    }

    function getCurrentEpochEndBlock() public view returns (uint256) {
        return getActualCurrentEpochStartBlock() + EPOCH_BLOCKS;
    }

    function _mint(address to) internal virtual override {
        if (to == address(0)) {
            revert("InvalidRecipient");
        }

        unchecked {
            minted++;
        }

        uint256 id = minted;

        if (_ownerOf[id] != address(0)) {
            revert("AlreadyExists");
        }

        _ownerOf[id] = to;
        _owned[to].push(id);
        _ownedIndex[id] = _owned[to].length - 1;

        emit Transfer(address(0), to, id);

        // added the following according
        uint256 attribute = getRandomNumber(id);
        tokenAttributes[id] = attribute;
    }

    function getRandomNumber(uint256 tokenId) internal view returns (uint256) {
        return uint256(blockhash(block.number - 1)) + block.timestamp + tokenId;
    }

    function getAttribute(uint256 seed) private pure returns (string memory) {
        bytes memory seedBytes = abi.encodePacked(seed);

        bytes32 hashResult = keccak256(seedBytes);

        uint256 hashedNumber = uint256(hashResult);

        uint256 random = hashedNumber % 256;

        uint256[] memory weights = new uint256[](9);
        string[] memory colors = new string[](9);

        // total weight is 256;
        weights[0] = 50; // Green
        colors[0] = "Green";

        weights[1] = 40; // Orange
        colors[1] = "Orange";

        weights[2] = 35; // Red
        colors[2] = "Red";

        weights[3] = 31; // Pink
        colors[3] = "Pink";

        weights[4] = 30; // Blue
        colors[4] = "Blue";

        weights[5] = 25; // Teal
        colors[5] = "Teal";

        weights[6] = 20; // Purple
        colors[6] = "Purple";

        weights[7] = 15; // Lavender
        colors[7] = "Lavender";

        weights[8] = 10; // Gold
        colors[8] = "Gold";

        uint256 sum = 0;

        for (uint256 i = 0; i < weights.length; i++) {
            sum += weights[i];
            if (random < sum) {
                return colors[i];
            }
        }

        revert("Invalid random number provided.");
    }

    function tokenURI(uint256 id) public view override returns (string memory) {
        if (bytes(baseTokenURI).length > 0) {
            return string.concat(baseTokenURI, Strings.toString(id));
        } else {
            uint256 seed = tokenAttributes[id];
            string memory color = getAttribute(seed);
            string memory jsonPreImage = string.concat(
                string.concat(
                    string.concat('{"name": "Jelly #', Strings.toString(id)),
                    '","description":"A collection of 10,000 Replicants enabled by ERC7616, an experimental token standard.","external_url":"https://jelly.top","image":"'
                ),
                string.concat(dataURI, color, ".jpg")
            );
            string memory jsonPostImage = string.concat('","attributes":[{"trait_type":"Color","value":"', color);
            string memory jsonPostTraits = '"}]}';

            return string.concat(
                "data:application/json;utf8,", string.concat(string.concat(jsonPreImage, jsonPostImage), jsonPostTraits)
            );
        }
    }

    fallback() external payable { }

    receive() external payable { }
}
