// File: referral1.sol


pragma solidity ^0.8.0;
pragma abicoder v2;


struct UserInfo {
    uint256 amount;         // How many LP tokens the user has provided.
    uint256 rewardDebt;     
    uint256 USDCrewardDebt; // Reward debt.
    uint256 lastDepositTime;
}

interface staking {
    function userInfo(uint256 _pid, address _user) external view returns(UserInfo memory);
}

contract referral is Ownable(msg.sender) {
    staking public stake = staking(0x1FDe0d2F44539789256D94D1784a86bF77D66DD0);

    mapping(address => address) public userReferrer;        // User => Referrer
    mapping(address => address[]) public userReferees;      // User => List of Referees

    uint256 public weight = 3;

    function setWeight(uint256 newweight) public onlyOwner {
        weight = newweight;
    }

    function getStakingAmount(address _user) public view returns(uint256) {
        uint256 amount1 = stake.userInfo(0, _user).amount;
        return (amount1);
    }

    function updateStake(staking _stake) external onlyOwner {
        stake = _stake;
    }

    function setReferrer(address _referrer) external {
        require(userReferrer[msg.sender] == address(0), "Referrer already set");
        require(_referrer != msg.sender, "Cannot refer self");
        require(userReferees[_referrer].length < 20, "Referrer has reached maximum referees");

        userReferrer[msg.sender] = _referrer;
        userReferees[_referrer].push(msg.sender);
    }

    function calculatePoints(address _user) external view returns(uint256) {
        uint256 totalAmount = getStakingAmount(_user)*weight; // Amount for the user themselves

        // Add amounts for all referees
        for (uint i = 0; i < userReferees[_user].length; i++) {
            totalAmount += getStakingAmount(userReferees[_user][i]);
        }

        // Add amount for the referrer, if any
        if (userReferrer[_user] != address(0)) {
            totalAmount += getStakingAmount(userReferrer[_user]);
        }

        return totalAmount;
    }

    function getReferees(address user) external view returns (address[] memory) {
        return userReferees[user];
    }

    function getRefereesStakedBalances(address user) external view returns (uint256[] memory) {
        address[] memory referees = userReferees[user];
        uint256[] memory stakedBalances = new uint256[](referees.length);

        for (uint256 i = 0; i < referees.length; i++) {
            stakedBalances[i] = getStakingAmount(referees[i]);
        }

        return stakedBalances;
    }



    // Additional functions and modifications can be added here.
}
