abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

contract Ownable is Context {
    address private _owner;
 
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
 
    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }
 
    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
 
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }
 
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
    }

contract Rewards is Ownable {
    using SafeMath for uint256;

    IERC20 private _token;
    mapping(address => uint256) private _claimedFees; 
    mapping(address => uint256) private _lastClaimedTimestamp; // new mapping to record the last claimed timestamp
    uint256 private _totalFees;
    uint256 private _feeCooldownTime = 30 minutes; // new variable to set the time between fee claims
    
    constructor(address tokenAddress) {
        require(tokenAddress != address(0), "FeeRecipient: token address is invalid");
        _token = IERC20(tokenAddress);
    }
    
    function claimFees() external {
        uint256 availableFees = getAvailableFees(msg.sender);
        require(availableFees > 0, "FeeRecipient: no available fees to claim");
        require(_token.balanceOf(msg.sender) > 0, "FeeRecipient: only token holders can claim fees");
        require(block.timestamp >= _lastClaimedTimestamp[msg.sender].add(_feeCooldownTime), "FeeRecipient: must wait before claiming fees again");
        require(_token.transfer(msg.sender, availableFees), "FeeRecipient: fee transfer failed");
        _claimedFees[msg.sender] = _claimedFees[msg.sender].add(availableFees);
        _lastClaimedTimestamp[msg.sender] = block.timestamp; // record the timestamp of the last claim
    }
    
    function getTotalFees() external view returns (uint256) {
        return _totalFees;
    }
    
    function getAvailableFees(address account) public view returns (uint256) {
        uint256 totalFees = _token.balanceOf(address(this));
        uint256 claimedFees = _claimedFees[account];
        uint256 accountBalance = _token.balanceOf(account);
        uint256 accountShare = totalFees.mul(accountBalance).div(_token.totalSupply());
        return accountShare.sub(claimedFees);
    }
}
