// File: CyberClownToken.sol


pragma solidity ^0.8.10;



contract CyberClownToken is ERC20, ERC20Burnable {
    uint256 private constant _totalSupply = 180200000000000 * 10**18; // 180.2兆 CCT
    uint256 public constant maxBurnAmount = _totalSupply / 2; // 最多可以燃烧一半的供应量

    constructor() ERC20("CyberClownToken", "CCT") {
        _mint(msg.sender, _totalSupply);
    }

    function burn(uint256 amount) public override {
        require(totalSupply() - amount >= maxBurnAmount, "Burn amount exceeds limit");
        super.burn(amount);
    }
}

