pragma solidity ^0.8.10;





contract ETHtoCCTExchange is CustomOwnable, Pausable {
    using Counters for Counters.Counter;
    Counters.Counter private _transactionCounter;

    uint256 public cctPrice = 100000; // 1 ETH = 100,000 CCT
    uint256 public minPurchaseAmount = 0.01 ether; // Minimum purchase amount is 0.01 ETH
    CyberClownToken public cctToken;

    event CCTPurchased(address indexed buyer, uint256 ethSpent, uint256 cctAmount);
    event PriceUpdated(uint256 newPrice);

    constructor(address _cctTokenAddress) {
        cctToken = CyberClownToken(_cctTokenAddress);
    }

    function buyCCTWithETH() public payable whenNotPaused {
        require(msg.value >= minPurchaseAmount, "Must send at least 0.01 ETH to buy CCT");

        uint256 cctAmount = (msg.value * cctPrice) / 1 ether;
        _transactionCounter.increment();

        require(cctToken.balanceOf(address(this)) >= cctAmount, "Not enough CCT in contract");
        require(cctToken.transfer(msg.sender, cctAmount), "CCT transfer failed");

        emit CCTPurchased(msg.sender, msg.value, cctAmount);
    }

    function updateCCTPrice(uint256 newPrice) external onlyOwner {
        cctPrice = newPrice;
        emit PriceUpdated(newPrice);
    }

    function withdrawETH() external onlyOwner {
        uint256 balance = address(this).balance;
        payable(owner()).transfer(balance);
    }

    function withdrawCCT(uint256 amount) external onlyOwner {
        require(cctToken.transfer(owner(), amount), "CCT transfer failed");
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function getETHBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function getCCTBalance() public view returns (uint256) {
        return cctToken.balanceOf(address(this));
    }

    function getTotalTransactions() public view returns (uint256) {
        return _transactionCounter.current();
    }
}
