      "content": "// SPDX-License-Identifier: MIT LICENSE

pragma solidity ^0.8.5;

import "./IERC20.sol";
import "../lib/openzeppelin-contracts/contracts/token/ERC721/IERC721Receiver.sol";
import "./IERC721Burnable.sol";
import "../lib/openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";


contract TokenStaking is IERC721Receiver,Initializable {

  struct Stake {
    uint96 since;
    address owner;
  }


  address public nft;
  address public reward;
  address public owner;

  uint256[] private rewardRate; //  Rate OF EMMISION (levels)
  mapping(uint256=> Stake) public vault; // TOKEN ID => Staking Details



  event NFTStaked(address owner, uint256 tokenId, uint256 value);
  event NFTUnstaked(address owner, uint256 tokenId, uint256 value);
  event Claimed(address owner, uint256 amount);   
  event UpdateOwner(address indexed oldOwner, address newOwner); 


  modifier onlyOwner() {
        require(owner == msg.sender, "LL:Invalid Owner");
        _;
    }


  function setOwner(address _owner) external onlyOwner {
      owner = _owner;
      emit UpdateOwner(msg.sender, owner);
  }


  function initialize(address _owner,address stakeToken,address rewardAddress,uint256[] calldata _rewardRate) external initializer {
       owner = _owner;
       nft=stakeToken;
       reward=rewardAddress;
       rewardRate=_rewardRate;   
  }

  function stake( uint256[] calldata _tokenIds) external {
    uint256 tokenId;
    for (uint256 i; i < _tokenIds.length; ) {
      tokenId = _tokenIds[i];
      require(IERC721Burnable(nft).ownerOf(tokenId) == msg.sender, "not your token");
      require(vault[tokenId].owner == address(0), "already stakedUser");

      IERC721Burnable(nft).safeTransferFrom(msg.sender, address(this), tokenId);
      vault[tokenId] = Stake(uint96(block.timestamp),msg.sender);

      unchecked{
        ++i;
      }
      emit NFTStaked(msg.sender, tokenId, block.timestamp);
    }
  }

  function unstake(uint256[] calldata tokenIds) external {
      (uint256 totalReward)=_claim(msg.sender, tokenIds, true);
      if (totalReward > 0) {
      IERC20(reward).transfer(msg.sender,totalReward);
    }
      _unstakeMany(msg.sender, tokenIds);
    emit Claimed(msg.sender, totalReward);

  }

  function claim(uint256[] calldata tokenIds) external {

    (uint256 totalReward)=_claim(msg.sender, tokenIds, false);
      if (totalReward > 0) {
      IERC20(reward).transfer(msg.sender,totalReward);
    }
    emit Claimed(msg.sender, totalReward);

  }


  function _unstakeMany(address account, uint256[] calldata tokenIds) internal {
    uint256 tokenId;
    for (uint256 i ; i < tokenIds.length; ) {
      tokenId = tokenIds[i];
      IERC721Burnable(nft).safeTransferFrom(address(this), account, tokenId);

      unchecked{
        ++i;
      }
      emit NFTUnstaked(account, tokenId, block.timestamp);
    }
  }

  function _claim(address account, uint256[] calldata tokenIds, bool _unstake) internal returns (uint256) {
    uint256 tokenId;
    uint256 rewardAmount;
    uint256 totalTime;
    for (uint256 i; i < tokenIds.length; ) {
      tokenId = tokenIds[i];
      Stake storage stakedUser = vault[tokenId];
      require(stakedUser.owner == account, "not an owner");

      totalTime=uint96(block.timestamp)-stakedUser.since;
      rewardAmount+=totalTime*rewardRate[IERC721Burnable(nft).checkLevel(tokenId)-1];

      if(_unstake==true){
        delete vault[tokenId];
      }
      else{
      stakedUser.since=uint96(block.timestamp);
      }

      unchecked {
        ++i;
      }
    }
    return (rewardAmount);
  }

  // should never be used inside of transaction because of gas fee
  function balanceOf(address account) public view returns (uint256) {
    uint256 balance = 0;
    uint256 supply = IERC721Burnable(nft).totalSupply();
    for(uint i = 0; i < supply; i++) {
      if (vault[i].owner == account) {
        balance += 1;
      }
    }
    return balance;
  }

  // should never be used inside of transaction because of gas fee
  function totalRewards(address account) public view returns (uint256) {
    uint256[] memory tokenIds = tokensOfOwner(account);
    uint256 tokenId;
    uint256 totalTime;
    uint256 rewardAmount;

    for (uint256 i; i < tokenIds.length; ) {
      tokenId = tokenIds[i];
      Stake storage stakedUser = vault[tokenId];
      totalTime=uint96(block.timestamp)-stakedUser.since;
      rewardAmount+=totalTime*rewardRate[IERC721Burnable(nft).checkLevel(tokenId)-1];
      unchecked {
        ++i;
      }
    }
    return rewardAmount;
  }

  // should never be used inside of transaction because of gas fee
  function idRewards(uint256[] calldata tokenIds) public view returns (uint256[] memory) {
    uint256[] memory rewardAmount = new uint256[](tokenIds.length);
    uint256 tokenId;
    uint256 totalTime;

    for (uint256 i; i < tokenIds.length; ) {
      tokenId = tokenIds[i];
      Stake memory stakedUser = vault[tokenId];
      totalTime=uint96(block.timestamp)-stakedUser.since;
      rewardAmount[i]+=totalTime*rewardRate[IERC721Burnable(nft).checkLevel(tokenId)-1];
      unchecked {
        ++i;
      }
    }
    return rewardAmount;
  }

  // should never be used inside of transaction because of gas fee
  function tokensOfOwner(address account) public view returns (uint256[] memory ownerTokens) {
    uint256 supply = IERC721Burnable(nft).totalSupply();
    uint256[] memory tmp = new uint256[](supply);

    uint256 index = 0;
    for(uint tokenId = 0; tokenId < supply; tokenId++) {
      if (vault[tokenId].owner == account) {
        tmp[index] = tokenId;
        index +=1;
      }
    }

    uint256[] memory tokens = new uint256[](index);
    for(uint i = 0; i < index; i++) {
      tokens[i] = tmp[i];
    }

    return tokens;
  }

  function onERC721Received(
        address,
        address from,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
      require(from != address(0x0), "Cannot send nfts to Vault directly");
      return IERC721Receiver.onERC721Received.selector;
    }

}"
