    "contracts/message-lib/MessageLib.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

library MessageLib {
    /**
     * @notice Messages are transferred between chains as 38=byte long bytes
     *         The first 8 bits are the value type.
     *         The next 88 bits are the value amount.
     *         The next 160 bits are the address related to the value.
     *         The remaining 48 bits are the value timestamp.
     */
    struct Message {
        uint8 valueType;
        uint256 value;
        address owner;
        uint256 timestamp;
    }

    uint256 internal constant _MAX_VALUE = type(uint88).max;
    uint256 internal constant _MAX_TIMESTAMP = type(uint48).max;

    uint8 public constant TOTAL_DEPOSITS_TYPE = 1;
    uint8 public constant TOTAL_POOL_UNLOCKS_TYPE = 2;
    uint8 public constant TOTAL_CLAIMS_TYPE = 3;
    uint8 public constant TOTAL_REQUESTS_TYPE = 4;

    error MessageLib_ValueOverflow();
    error MessageLib_TimestampOverflow();

    /**
     * @notice Extracts a Message from bytes.
     */
    function unpack(bytes memory b) internal pure returns (Message memory m) {
        uint8 valueType;
        uint88 value;
        address owner;
        uint48 _timestamp;

        /* solhint-disable no-inline-assembly */
        assembly {
            valueType := mload(add(b, 1))
            value := mload(add(b, 12))
            owner := mload(add(b, 32))
            _timestamp := mload(add(b, 38))
        }
        /* solhint-enable no-inline-assembly */

        return Message(valueType, value, owner, _timestamp);
    }

    /**
     * @notice Packs a Message into bytes.
     */
    function pack(Message memory m) internal pure returns (bytes memory) {
        if (m.value > _MAX_VALUE) revert MessageLib_ValueOverflow();
        if (m.timestamp > _MAX_TIMESTAMP) revert MessageLib_TimestampOverflow();

        return abi.encodePacked(m.valueType, uint88(m.value), m.owner, uint48(m.timestamp));
    }
}
"
    },
    "contracts/vaultNav/IVaultNav.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

struct Nav {
    uint192 nav;
    uint48 updateTime; // timestamp of the block when NAV was updated
}

// Events

interface IVaultNav {
    event NavUpdated(address indexed vaultType, uint256 nav, uint256 timestamp, uint256 roundId);
    event SetNavUpdater(address indexed vaultType, address updater);

    function updateNav(address vaultType, uint256 nav) external;
    function updateNavByTimestamp(address vaultType, uint256 timestamp, uint256 nav) external;
    function setNavUpdater(address vaultType, address updater) external;
    function getCurrentNav(address vaultType) external view returns (uint256 nav, uint256 updateTime);
    function getNavByTimestamp(
        address vaultType,
        uint256 timestamp
    ) external view returns (uint256 nav, uint256 updateTime);
    function getLatestRoundId() external view returns (uint256);
    function getRoundId(uint256 timestamp) external view returns (uint256);
}
