    "contracts/MiyaAuction.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import {Ownable} from "../lib/solady/src/auth/Ownable.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import {MiyaAuctionNFT} from "./MiyaAuctionNFT.sol";
import {SafeCastLib} from "./libs/SafeCastLib.sol";
import {SafeTransferLib} from "./libs/SafeTransferLib.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract MiyaAuction is Ownable, ERC721Holder {
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;

    error EndedAuction();
    error ActiveAuction();
    error LockedAuction();
    error InvalidAuction();
    error SettledAuction();
    error InactiveAuction();
    error InsufficientBid();
    error InvalidTimestamp();

    /*//////////////////////////////////////////////////////////////
                            AUCTION PARAMS
    //////////////////////////////////////////////////////////////*/

    // Packed for max storage + gas efficiency (2 slots)
    struct AuctionData {
        address bidder;
        uint96 amount;
        uint96 minPrice;
        uint40 startTime;
        uint40 endTime;
        bool settled;
    }

    EnumerableSet.AddressSet internal _nfts;
    mapping(address nft => EnumerableSet.UintSet) internal _activeNftIds;
    mapping(address nft => EnumerableSet.UintSet) internal _settledNftIds;
    mapping(address nft => mapping(uint256 tokenId => AuctionData)) internal _auctionData;
    MiyaAuctionNFT public auctioncore;
    uint40 public auctionDuration = 120 hours;
    uint96 public bidIncrement = 0.01 ether;
    uint96 public auctionFunds;
    bool auctionLocked;

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/

    event LockAuction();
    event BidPlaced(address indexed nft, uint256 indexed tokenId, uint256 indexed amount, address sender);
    event AuctionCreated(address indexed nft, uint256 indexed tokenId, uint40 indexed startTime, uint40 endTime);
    event AuctionSettled(address indexed nft, uint256 indexed tokenId, uint256 indexed amount, address winner);
    event AuctionDeleted(address indexed nft, uint256 indexed tokenId);
    event AuctionDurationUpdated(uint256 indexed amount);
    event AuctionBidIncrementUpdated(uint256 indexed amount);

    constructor(address _owner, string memory _name, string memory _symbol, string memory _baseUri) {
        _initializeOwner(_owner);
        auctioncore = new MiyaAuctionNFT(address(this), _name, _symbol, _baseUri);
    }

    /*//////////////////////////////////////////////////////////////
                    PUBLIC/EXTERNAL VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /*
     * @notice This returns the list of NFT collections that have been auctioned
     */
    function getNfts() external view returns (address[] memory) {
        return _nfts.values();
    }

    /*
     * @notice This returns the auction by address and tokenId
     */
    function getAuction(address _nft, uint256 _tokenId) external view returns (AuctionData memory) {
        return _auctionData[_nft][_tokenId];
    }

    /*
     * @notice This returns the list of actively auctioned tokenIds for a specific NFT
     */
    function getActiveAuctionsList(address _nft) external view returns (AuctionData[] memory) {
        uint256[] memory tokenIds = _activeNftIds[_nft].values();
        AuctionData[] memory data = new AuctionData[](tokenIds.length);
        for (uint256 i; i < tokenIds.length;) {
            data[i] = _auctionData[_nft][tokenIds[i]];
            unchecked {
                ++i;
            }
        }
        return data;
    }

    /*
     * @notice This returns the list of settled auctioned tokenIds for a specific NFT
     */
    function getSettledAuctionsList(address _nft) external view returns (AuctionData[] memory) {
        uint256[] memory tokenIds = _settledNftIds[_nft].values();
        AuctionData[] memory data = new AuctionData[](tokenIds.length);
        for (uint256 i; i < tokenIds.length;) {
            data[i] = _auctionData[_nft][tokenIds[i]];
            unchecked {
                ++i;
            }
        }
        return data;
    }

    /*
     * @notice This returns the list of active tokenIds auctioned for a specific NFT collection
     */
    function getActiveTokenIds(address _nft) external view returns (uint256[] memory) {
        return _activeNftIds[_nft].values();
    }

    /*
     * @notice This returns the list of settled tokenIds auctioned for a specific NFT collection
     */
    function getSettledTokenIds(address _nft) external view returns (uint256[] memory) {
        return _settledNftIds[_nft].values();
    }

    /*
     * @notice This returns the list of all auctions that are active or settled for a specific NFT collection
     * @dev WARNING: DO NOT USE THIS FUNCTION ONCHAIN, IT IS INTENDED TO BE VIEW ONLY
     */
    function getAllTokenIds(address _nft) external view returns (uint256[] memory) {
        uint256[] memory activeIds = _activeNftIds[_nft].values();
        uint256[] memory settledIds = _settledNftIds[_nft].values();
        uint256[] memory tokenIds = new uint256[](activeIds.length + settledIds.length);
        for (uint256 i; i < tokenIds.length;) {
            if (i < activeIds.length) tokenIds[i] = activeIds[i];
            else tokenIds[i] = settledIds[i - activeIds.length];
            unchecked {
                ++i;
            }
        }
        return tokenIds;
    }

    /*//////////////////////////////////////////////////////////////
                    PUBLIC/EXTERNAL WRITE FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /*
     * @notice This allow admin to auction an NFT
     */
    function createAuction(address _nft, uint256 _tokenId, uint96 _minPrice, uint40 _startTime)
        external
        onlyOwner
        returns (bool)
    {
        if (auctionLocked) revert LockedAuction();
        if (_activeNftIds[_nft].contains(_tokenId)) revert ActiveAuction();
        if (_startTime < block.timestamp) revert InvalidTimestamp();

        if (!_nfts.contains(_nft)) _nfts.add(_nft);
        if (_settledNftIds[_nft].contains(_tokenId)) _settledNftIds[_nft].remove(_tokenId);
        if (IERC721(_nft).ownerOf(_tokenId) != address(this)) {
            IERC721(_nft).transferFrom(owner(), address(this), _tokenId);
        }

        uint40 endTime = _startTime + auctionDuration;
        _activeNftIds[_nft].add(_tokenId);
        _auctionData[_nft][_tokenId] = AuctionData({
            bidder: address(0),
            amount: 0,
            minPrice: _minPrice,
            startTime: _startTime,
            endTime: endTime,
            settled: false
        });

        emit AuctionCreated(_nft, _tokenId, _startTime, endTime);
        return true;
    }

    /*
     * @notice This allows the admin to delete an auction that has no bids
     */
    function deleteAuction(address _nft, uint256 _tokenId) external onlyOwner {
        AuctionData storage auction = _auctionData[_nft][_tokenId];

        if (auctionLocked) revert LockedAuction();
        if (!_activeNftIds[_nft].contains(_tokenId)) revert InvalidAuction();
        if (auction.settled) revert SettledAuction();
        if (auction.bidder != address(0)) revert ActiveAuction();

        _activeNftIds[_nft].remove(_tokenId);
        if (_activeNftIds[_nft].length() == 0 && _settledNftIds[_nft].length() == 0) _nfts.remove(_nft);
        delete _auctionData[_nft][_tokenId];

        IERC721(_nft).transferFrom(address(this), owner(), _tokenId);

        emit AuctionDeleted(_nft, _tokenId);
    }

    /*
     * @notice This allows users to bid on auctions
     */
    function placeBid(address _nft, uint256 _tokenId) external payable {
        AuctionData storage auction = _auctionData[_nft][_tokenId];

        if (!_activeNftIds[_nft].contains(_tokenId)) revert InvalidAuction();
        if (block.timestamp < auction.startTime) revert InactiveAuction();
        if (block.timestamp > auction.endTime) revert EndedAuction();
        if (msg.value < auction.minPrice) revert InsufficientBid();
        if (msg.value < auction.amount + bidIncrement) revert InsufficientBid();

        // bidding logic
        address lastBidder = auction.bidder;
        uint96 lastBid = auction.amount;

        auction.bidder = msg.sender;
        auction.amount = SafeCastLib.toUint96(msg.value);
        unchecked {
            auctionFunds += uint96(msg.value) - lastBid;
        }

        if (lastBid > 0) {
            // refund last bidder
            SafeTransferLib.forceSafeTransferETH(lastBidder, lastBid);
        }

        auctioncore.mint(msg.sender);

        emit BidPlaced(_nft, _tokenId, msg.value, msg.sender);
    }

    /*
     * @notice This settles all concluded active auctions
     */
    function settleAllAuctions() external onlyOwner {
        address[] memory nfts = _nfts.values();
        for (uint256 i = _nfts.length(); i > 0;) {
            address nft = nfts[i - 1];
            for (uint256 j = _activeNftIds[nft].length(); j > 0;) {
                settleOneAuction(nft, _activeNftIds[nft].at(j - 1));
                unchecked {
                    --j;
                }
            }
            unchecked {
                --i;
            }
        }
        if (address(this).balance > 0) {
            SafeTransferLib.forceSafeTransferETH(owner(), address(this).balance - auctionFunds);
        }
    }

    /*
     * @notice This settles the auction, by sending out the nft associated with the auction
     * also setting its status to true
     */

    function settleOneAuction(address _nft, uint256 _tokenId) public onlyOwner {
        AuctionData storage data = _auctionData[_nft][_tokenId];
        address bidder = data.bidder;
        uint96 amount = data.amount;

        if (bidder == address(0)) return;
        else if (block.timestamp <= data.endTime) return;
        else {
            data.settled = true;
            _activeNftIds[_nft].remove(_tokenId);
            _settledNftIds[_nft].add(_tokenId);
            unchecked {
                auctionFunds -= amount;
            }
            IERC721(_nft).transferFrom(address(this), bidder, _tokenId);
            emit AuctionSettled(_nft, _tokenId, amount, bidder);
        }
    }

    /*//////////////////////////////////////////////////////////////
                        ADMIN WRITE FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /*
     * @notice This allows the admin to withdraw eth
     */
    function withdrawETH(address recipient) external onlyOwner {
        SafeTransferLib.forceSafeTransferETH(recipient, address(this).balance - auctionFunds);
    }

    /*
     * @notice This allows the admin to set the bid increment for the auction
     */
    function setBidIncrement(uint96 _bidIncrement) external onlyOwner {
        if (auctionLocked) revert LockedAuction();
        if (_bidIncrement == 0) revert();
        bidIncrement = _bidIncrement;
        emit AuctionBidIncrementUpdated(_bidIncrement);
    }

    /*
     * @notice This allows the admin to set the duration for the auction
     */
    function setDuration(uint32 _duration) external onlyOwner {
        if (auctionLocked) revert LockedAuction();
        auctionDuration = _duration;
        emit AuctionDurationUpdated(_duration);
    }

    /*
     * @notice This halts creation and deletion of auctions, but not bidding and settling
     */
    function lockAuction() external onlyOwner {
        if (auctionLocked) revert LockedAuction();
        auctionLocked = true;
        auctioncore.lockMint();
        emit LockAuction();
    }

    /*
     * @notice Update auctioncore NFT URI
     */
    function updateAuctioncoreURI(string memory baseUri_) external onlyOwner {
        auctioncore.updateBaseURI(baseUri_);
    }

    /*
     * @notice Lock auctioncore NFT URI
     */
    function lockAuctioncoreURI() external onlyOwner {
        auctioncore.lockURI();
    }

    /*
     * @notice Lock auctioncore NFT mint
     */
    function lockAuctioncoreMint() external onlyOwner {
        auctioncore.lockMint();
    }

    /*
     * @notice Renounce ownership of auctioncore NFT, which also locks URI but not mint
     */
    function renounceAuctioncore() external onlyOwner {
        auctioncore.renounceOwnership();
    }
}
"
    },
    "lib/solady/src/auth/Ownable.sol": {
