    "contracts/MiyaAuctionNFT.sol": {
      "content": "// SPDX-License-Identifier: CC0-1.0
pragma solidity ^0.8.23;

import {ERC721} from "../lib/solady/src/tokens/ERC721.sol";
import {Ownable} from "../lib/solady/src/auth/Ownable.sol";
import {LibString} from "../lib/solady/src/utils/LibString.sol";

contract MiyaAuctionNFT is ERC721, Ownable {
    using LibString for uint256;
    using LibString for string;

    error Locked();

    event URILocked();
    event MintLocked();
    event UpdateBaseURI(string indexed baseURI_);

    string internal _name;
    string internal _symbol;
    string internal _baseUri;
    uint128 totalSupply;
    bool mintLocked;
    bool uriLocked;

    constructor(address owner_, string memory name_, string memory symbol_, string memory baseUri_) {
        _initializeOwner(owner_);
        _name = name_;
        _symbol = symbol_;
        _baseUri = baseUri_;
        emit UpdateBaseURI(baseUri_);
    }

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function contractURI() external view returns (string memory) {
        return _baseUri.concat("contract.json");
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (!_exists(tokenId)) return "";
        else return _baseUri.concat(tokenId.toString());
    }

    function burn(uint256 tokenId) external {
        if (!_isApprovedOrOwner(msg.sender, tokenId)) revert Unauthorized();
        _burn(tokenId);
    }

    function mint(address to) external onlyOwner {
        if (mintLocked) return;
        uint256 tokenId = ++totalSupply;
        _mint(to, tokenId);
    }

    function mint(address to, uint128 num) external onlyOwner {
        if (mintLocked) return;
        uint128 tokenId = totalSupply;
        for (uint256 i; i < num;) {
            unchecked {
                _mint(to, ++tokenId);
                ++i;
            }
        }
        unchecked {
            totalSupply += num;
        }
    }

    function updateBaseURI(string memory baseUri_) external onlyOwner {
        if (uriLocked) revert Locked();
        _baseUri = baseUri_;
        emit UpdateBaseURI(baseUri_);
    }

    function lockURI() public onlyOwner {
        uriLocked = true;
        emit URILocked();
    }

    function lockMint() public onlyOwner {
        mintLocked = true;
        emit MintLocked();
    }

    function renounceOwnership() public override payable onlyOwner {
        lockURI();
        super.renounceOwnership();
    }
}
"
