    "contracts/AccessManagerUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import { Initializable } from "lib/openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

import { UUPSUpgradeable } from "lib/openzeppelin-contracts-upgradeable/contracts/proxy/utils/UUPSUpgradeable.sol";

import { EIP712Upgradeable } from "lib/openzeppelin-contracts-upgradeable/contracts/utils/cryptography/EIP712Upgradeable.sol";

import { AccessControlEnumerableUpgradeable } from "./internal-upgradeable/AccessControlEnumerableUpgradeable.sol";

import { IAccessManagerUpgradeable } from "./interfaces/IAccessManagerUpgradeable.sol";

import { MINTER_ROLE, OPERATOR_ROLE, ROUTER_ROLE, UPGRADER_ROLE } from "./libraries/Constants.sol";

contract AccessManagerUpgradeable is IAccessManagerUpgradeable, Initializable, UUPSUpgradeable, EIP712Upgradeable, AccessControlEnumerableUpgradeable {
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address admin_, address[] calldata operators_, address[] calldata routers_, string calldata name_, string calldata version_) external initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();
        __EIP712_init(name_, version_);
        _grantRoles(OPERATOR_ROLE, operators_);
        _grantRoles(ROUTER_ROLE, routers_);
        _grantRole(UPGRADER_ROLE, admin_);
        _grantRole(DEFAULT_ADMIN_ROLE, admin_);
    }

    function admin() public view override returns (address) {
        return getRoleMember(DEFAULT_ADMIN_ROLE, 0);
    }

    function grantRoles(bytes32 role_, address[] calldata addresses_) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRoles(role_, addresses_);
    }

    function changeAdmin(address newAdmin_) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(DEFAULT_ADMIN_ROLE, newAdmin_);
    }

    /* solhint-disable no-empty-blocks */
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(UPGRADER_ROLE) {}
}
"
    },
