abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor(address initialOwner) {
        require(initialOwner != address(0), "Invalid owner address");
        _transferOwnership(initialOwner);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }


    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contract MultisigWallet is Ownable {
    using SafeERC20 for IERC20;

    struct Transaction {
        address recepient;
        address token;
        uint amount;
        bool executed;
    }

    mapping(uint => Transaction) public transactions;

    mapping(uint => mapping(address => bool)) public confirmations;

    mapping(address => bool) public isManager;

    address[] public managers;

    uint public required;

    event Confirmation(address indexed sender, uint indexed transactionId);
    event Revocation(address indexed sender, uint indexed transactionId);
    event Submission(uint indexed transactionId);
    event Execution(uint indexed transactionId);

    constructor(address initialOwner) Ownable(initialOwner)  {
    }

    function setRequired(uint _required) external onlyOwner {
        require((_required > 0 && _required <= managers.length),
            "Invalid amount of required confirmations");
        required = _required;
    }

    function addToManagers(address account) external onlyOwner {
        require(!isManager[account] && account != address(0));
        isManager[account] = true;
        managers.push(account);
    }

    function removeFromManagers(address account) external onlyOwner {
        require(isManager[account] && account != address(0));
        require(managers.length - 1 >= required, "Please lower `required` value first");
        isManager[account] = false;
        for (uint i = 0; i < managers.length; i++) {
            if (managers[i] == account) {
                managers[i] = managers[managers.length - 1];
                managers.pop();
                break;
            }
        }
    }

    function getManagers() public view returns (address[] memory) {
        return (managers);
    }

    modifier confirmed(uint transactionId, address owner) {
        require(confirmations[transactionId][owner],
            "Transaction is not confirmed");
        _;
    }

    modifier notConfirmed(uint transactionId, address owner) {
        require(!confirmations[transactionId][owner],
            "Transaction is already confirmed");
        _;
    }

    modifier notExecuted(uint transactionId) {
        require(!transactions[transactionId].executed,
            "Transaction was already executed");
        _;
    }

    modifier managerExists(address manager) {
        require(isManager[manager],
            "Caller is not an authorized wallet");
        _;
    }

    modifier transactionExists(uint transactionId) {
        require(transactions[transactionId].recepient != address(0),
            "Transaction doesn't exist");
        _;
    }

    modifier transactionDoesntExist(uint transactionId) {
        require(transactions[transactionId].recepient == address(0),
            "Transaction already exists");
        _;
    }

    function sendETH(
        uint _txId,
        address _recepient,
        uint _amount) external managerExists(msg.sender)
    {
        require(required > 0, "`required` value is not set");
        addTransaction(_txId, address(0), _recepient, _amount);
        confirmTransaction(_txId);
    }

    function sendToken(uint _txId,
        address _token,
        address _recepient,
        uint _amount) external managerExists(msg.sender)
    {
        require(required > 0, "`required` value is not set");
        addTransaction(_txId, _token, _recepient, _amount);
        confirmTransaction(_txId);
    }

    function addTransaction(
        uint _txId,
        address _token,
        address _recepient,
        uint _amount) internal
    transactionDoesntExist(_txId)

    {
        require(_recepient != address(0),
            "Zero address recepient not allowed");
        require((_amount > 0), "Amount cannot be zero");
        Transaction memory txn = Transaction({
        recepient : _recepient,
        amount : _amount,
        token : _token,
        executed : false
        });
        transactions[_txId] = txn;
        emit Submission(_txId);
    }

    function confirmTransaction(uint transactionId) public
    managerExists(msg.sender)
    transactionExists(transactionId)
    notExecuted(transactionId)
    notConfirmed(transactionId, msg.sender)
    {
        confirmations[transactionId][msg.sender] = true;
        emit Confirmation(msg.sender, transactionId);
        if (isConfirmed(transactionId)) {
            executeTransaction(transactionId);
        }
    }

    function revokeConfirmation(uint transactionId)
    public
    managerExists(msg.sender)
    transactionExists(transactionId)
    confirmed(transactionId, msg.sender)
    notExecuted(transactionId)
    {
        confirmations[transactionId][msg.sender] = false;
        emit Revocation(msg.sender, transactionId);
    }

    function executeTransaction(uint transactionId) internal
    {
        uint balance;
        if (transactions[transactionId].token == address(0)) {
            balance = address(this).balance;
            require(transactions[transactionId].amount <= balance,
                "Insufficient ETH balance on contract");
            (bool success,) = transactions[transactionId].recepient.call{
            value : transactions[transactionId].amount
            }("");
            require(success, "Transaction execution reverted.");
        } else {
            balance = IERC20(transactions[transactionId].token).balanceOf(address(this));
            require(transactions[transactionId].amount <= balance,
                "Insufficient token balance on contract");
            IERC20(transactions[transactionId].token).safeTransfer(
                transactions[transactionId].recepient,
                transactions[transactionId].amount
            );
        }
        emit Execution(transactionId);
        transactions[transactionId].executed = true;
    }

    function isConfirmed(uint transactionId) public view returns (bool)
    {
        uint count = 0;
        for (uint i = 0; i < managers.length; i++) {
            if (confirmations[transactionId][managers[i]])
                count += 1;
        }
        if (count == required) {
            return true;
        } else {
            return false;
        }
    }

    function getConfirmationCount(uint transactionId) public view returns (uint)
    {
        uint count = 0;
        for (uint i = 0; i < managers.length; i++) {
            if (confirmations[transactionId][managers[i]]) {
                count += 1;
            }
        }
        return count;

    }

    function getConfirmations(uint transactionId) public view returns (address[] memory _confirmations)
    {
        address[] memory confirmationsTemp = new address[](managers.length);
        uint count = 0;
        uint i;
        for (i = 0; i < managers.length; i++)
            if (confirmations[transactionId][managers[i]]) {
                confirmationsTemp[count] = managers[i];
                count += 1;
            }
        _confirmations = new address[](count);
        for (i = 0; i < count; i++)
            _confirmations[i] = confirmationsTemp[i];
    }

    function isExecuted(uint transactionId) public view returns (bool) {
        return transactions[transactionId].executed;
    }

    receive() external payable {
    }
}
